import React, { useState } from 'react';

const EmailVerificationPending = ({ email, onResendEmail, onBackToLogin }) => {
  const [isResending, setIsResending] = useState(false);
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState(''); // 'success' or 'error'

  const handleResendEmail = async () => {
    setIsResending(true);
    setMessage('');
    setMessageType('');

    try {
      const response = await fetch('/api/auth/resend-verification', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email })
      });

      const data = await response.json();

      if (response.ok) {
        setMessage('Verification email sent successfully! Please check your inbox.');
        setMessageType('success');
        if (onResendEmail) {
          onResendEmail();
        }
      } else {
        setMessage(data.error || 'Failed to resend verification email.');
        setMessageType('error');
      }
    } catch (error) {
      console.error('Resend verification error:', error);
      setMessage('An error occurred while sending verification email.');
      setMessageType('error');
    } finally {
      setIsResending(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-gray-50 to-white py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-lg w-full space-y-8">
        <div className="bg-white py-12 px-8 shadow-2xl rounded-2xl border-2 border-black relative overflow-hidden">
          {/* Decorative elements */}
          <div className="absolute top-0 left-0 w-full h-2 bg-black"></div>
          <div className="absolute -top-4 -right-4 w-8 h-8 bg-black rounded-full opacity-10"></div>
          <div className="absolute -bottom-4 -left-4 w-12 h-12 bg-black rounded-full opacity-5"></div>

          {/* Logo/Brand */}
          <div className="text-center mb-8">
            <div className="inline-flex items-center justify-center w-16 h-16 bg-black rounded-full mb-4">
              <span className="text-white font-bold text-xl">J</span>
            </div>
            <h1 className="text-2xl font-bold text-black">JACS Cebu</h1>
            <p className="text-sm text-gray-600">Property Management</p>
          </div>

          <div className="text-center">
            {/* Email icon */}
            <div className="relative mb-8">
              <div className="mx-auto flex items-center justify-center h-20 w-20 rounded-full bg-black shadow-2xl mb-4">
                <svg className="h-10 w-10 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 8l7.89 4.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z"></path>
                </svg>
              </div>
              <div className="absolute -top-2 -right-2 w-8 h-8 bg-white border-4 border-black rounded-full flex items-center justify-center">
                <div className="w-3 h-3 bg-black rounded-full animate-pulse"></div>
              </div>
            </div>

            <h2 className="text-4xl font-bold text-black mb-4">Check Your Email</h2>
            <p className="text-gray-700 text-lg mb-6">
              We've sent a verification link to:
            </p>
            <div className="bg-gray-50 border-2 border-black rounded-lg py-3 px-4 mb-8">
              <p className="text-lg font-bold text-black break-all">{email}</p>
            </div>

            <div className="text-left bg-gray-50 border-2 border-black rounded-lg p-6 mb-8">
              <h3 className="text-lg font-bold text-black mb-4 flex items-center">
                <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z"></path>
                </svg>
                Next Steps:
              </h3>
              <ul className="text-gray-700 space-y-3">
                <li className="flex items-start">
                  <span className="inline-block w-6 h-6 bg-black text-white rounded-full text-xs font-bold flex items-center justify-center mr-3 mt-0.5">1</span>
                  Check your email inbox
                </li>
                <li className="flex items-start">
                  <span className="inline-block w-6 h-6 bg-black text-white rounded-full text-xs font-bold flex items-center justify-center mr-3 mt-0.5">2</span>
                  Look for an email from JACS Cebu Property Management
                </li>
                <li className="flex items-start">
                  <span className="inline-block w-6 h-6 bg-black text-white rounded-full text-xs font-bold flex items-center justify-center mr-3 mt-0.5">3</span>
                  Click the verification link in the email
                </li>
                <li className="flex items-start">
                  <span className="inline-block w-6 h-6 bg-black text-white rounded-full text-xs font-bold flex items-center justify-center mr-3 mt-0.5">4</span>
                  If you don't see it, check your spam folder
                </li>
              </ul>
            </div>

            {/* Message display */}
            {message && (
              <div className={`mb-6 p-4 rounded-lg border-2 ${
                messageType === 'success' 
                  ? 'bg-green-50 border-green-600 text-green-800' 
                  : 'bg-red-50 border-red-600 text-red-800'
              }`}>
                <p className="font-medium">{message}</p>
              </div>
            )}

            {/* Action buttons */}
            <div className="space-y-4">
              <button
                onClick={handleResendEmail}
                disabled={isResending}
                className="w-full flex justify-center py-4 px-6 border-2 border-black rounded-lg shadow-lg text-lg font-bold text-white bg-black hover:bg-gray-800 focus:outline-none focus:ring-4 focus:ring-gray-300 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-200 transform hover:scale-105"
              >
                {isResending ? (
                  <>
                    <div className="animate-spin rounded-full h-5 w-5 border-2 border-white border-t-transparent mr-3"></div>
                    Sending...
                  </>
                ) : (
                  <>
                    <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 8l7.89 4.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z"></path>
                    </svg>
                    Resend Verification Email
                  </>
                )}
              </button>

              {onBackToLogin && (
                <button
                  onClick={onBackToLogin}
                  className="w-full flex justify-center py-4 px-6 border-2 border-black rounded-lg shadow-lg text-lg font-bold text-black bg-white hover:bg-gray-50 focus:outline-none focus:ring-4 focus:ring-gray-300 transition-all duration-200 transform hover:scale-105"
                >
                  <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 19l-7-7m0 0l7-7m-7 7h18"></path>
                  </svg>
                  Back to Login
                </button>
              )}
            </div>

            <div className="mt-8 text-center">
              <div className="bg-gray-50 border-2 border-black rounded-lg p-4">
                <p className="text-sm text-gray-700 font-medium">üí° Didn't receive the email?</p>
                <p className="text-xs text-gray-600 mt-1">Check your spam folder or try resending.</p>
                <p className="text-xs text-gray-600 mt-1">‚è∞ The verification link will expire in 24 hours.</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EmailVerificationPending;
