import React, { useEffect, useMemo, useState } from 'react';
import api from '../../services/api';

const statusToBadgeClass = {
  pending_approval: 'bg-yellow-100 text-yellow-800 border-yellow-200',
  active: 'bg-green-100 text-green-800 border-green-200',
  inactive: 'bg-gray-100 text-gray-700 border-gray-200',
  rejected: 'bg-red-100 text-red-800 border-red-200'
};

const STORAGE_KEY = 'pm_buildings_v1';

const ManageProperty = ({ onOpenManageUnits = () => {} }) => {

  const [buildings, setBuildings] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('All');
  const [sortBy, setSortBy] = useState('Recent');
  const sortOrder = 'desc';
  const [selectedIds, setSelectedIds] = useState(new Set());
  const [page, setPage] = useState(1);
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [editingBuilding, setEditingBuilding] = useState(null);
  const [showDetailsModal, setShowDetailsModal] = useState(false);
  const [detailsBuilding, setDetailsBuilding] = useState(null);
  const [companies, setCompanies] = useState([]);
  const [newProperty, setNewProperty] = useState({
    buildingName: '',
    subdomain: '',
    address: '',
    totalUnits: '',
    propertyType: '',
    yearBuilt: '',
    floorCount: '',
    parkingSpaces: '',
    contactPerson: '',
    contactPosition: '',
    contactEmail: '',
    contactPhone: '',
    businessRegNumber: '',
    tin: '',
    businessPermitNumber: '',
    businessPermitExpiry: '',
    fireSafetyCert: null,
    buildingPermit: null,
    occupancyPermit: null,
    electricalCert: null,
    amenities: [],
    customAmenity: '',
    additionalNotes: '',
    averageRent: '',
    furnishing: '',
    legalDocs: []
  });
  const itemsPerPage = 6;

  // Fetch companies for dropdown (only manager's companies)
  const fetchCompanies = async () => {
    try {
      const currentUserIdRaw = localStorage.getItem('user_id') || localStorage.getItem('owner_id');
      const currentUserId = currentUserIdRaw ? parseInt(currentUserIdRaw, 10) : null;

      if (!currentUserId) {
        console.error('No user ID found');
        return;
      }

      const response = await fetch(`/api/manager/properties/companies/list?user_id=${currentUserId}`);
      const data = await response.json();
      if (data.companies) {
        setCompanies(data.companies);
      }
    } catch (error) {
      console.error('Error fetching companies:', error);
    }
  };

  // Load buildings from localStorage
  useEffect(() => {
    const bootstrap = async () => {
      // Fetch companies first
      await fetchCompanies();

      // Try API first
      try {
        // Ensure we read the same key that login writes
        const currentUserIdRaw = localStorage.getItem('user_id') || localStorage.getItem('owner_id');
        const currentUserId = currentUserIdRaw ? parseInt(currentUserIdRaw, 10) : null;
        const res = await api.listCompanies(currentUserId ? { owner_id: currentUserId } : {});
        if (res && Array.isArray(res.items)) {
          const mapped = res.items.map((it) => ({
            id: it.id,
            ownerId: it.owner_id,
            companyName: it.company_name || 'No Company',
            buildingName: it.building_name || it.title || 'Unnamed Property',
            subdomain: it.portal_subdomain || 'no-subdomain',
            address: it.address || 'No Address',
            totalUnits: it.total_units || 0,
            occupiedUnits: 0,
            vacantUnits: it.total_units || 0,
            status: it.status || 'pending_approval',
            adminApproval: it.admin_approval || 'Pending',
            registrationDate: it.created_at,
            approvalDate: null,
            lastUpdated: it.updated_at,
            monthlyRevenue: it.monthly_rent || 0,
            averageRent: it.monthly_rent || 0,
            contactPerson: it.contact_person || 'No Contact',
            contactEmail: it.contact_email || 'No Email',
            contactPhone: it.contact_phone || 'No Phone',
            furnishing: it.furnishing || 'Not Specified',
            propertyType: it.property_type || 'Not Specified',
            averageRent: it.monthly_rent || 0,
            image: 'data:image/svg+xml;base64,',
            images: [], // Initialize empty images array
            legalDocs: [] // Initialize empty legal docs array
          }));
          setBuildings(mapped);
          return;
        }
      } catch (_) {
        // fall back to localStorage
      }
      const persistedRaw = localStorage.getItem(STORAGE_KEY);
      if (persistedRaw) {
        try {
          const parsed = JSON.parse(persistedRaw);
          if (parsed && Array.isArray(parsed)) {
            const hasSampleData = parsed.some(building => 
              building.companyName === 'Metro Plaza Development Corp.' ||
              building.companyName === 'Mountain Ridge Properties Inc.' ||
              building.companyName === 'Tech Hub Real Estate LLC'
            );
            if (hasSampleData) {
              localStorage.removeItem(STORAGE_KEY);
              setBuildings([]);
              return;
            }
            setBuildings(parsed);
            return;
          }
        } catch (error) {}
      }
      setBuildings([]);
    };
    bootstrap();
  }, []);

  // Prefill contact info from manager profile (owner/user) if available
  useEffect(() => {
    (async () => {
      try {
        const res = await api.getManagerProfile();
        const info = res?.profile?.personalInfo || {};
        setNewProperty(prev => ({
          ...prev,
          contactPerson: prev.contactPerson || info.name || '',
          contactEmail: prev.contactEmail || info.email || '',
          contactPhone: prev.contactPhone || info.phone || ''
        }));
      } catch (_) {
        // ignore; leave fields empty if profile not available
      }
    })();
  }, []);

  // Save buildings to localStorage
  useEffect(() => {
    if (buildings.length > 0) {
      localStorage.setItem(STORAGE_KEY, JSON.stringify(buildings));
    }
  }, [buildings]);

  // Reset pagination when filters/search change
  useEffect(() => {
    setPage(1);
  }, [searchTerm, statusFilter, sortBy, sortOrder]);

  const filteredBuildings = useMemo(() => {
    const term = (searchTerm || '').trim().toLowerCase();
    return buildings.filter(building => {
      // Search across multiple fields
      const haystacks = [
        building.companyName,
        building.buildingName,
        building.subdomain,
        building.address,
        building.contactPerson,
        building.contactEmail,
        building.contactPhone
      ].filter(Boolean).map(v => String(v).toLowerCase());
      const matchesSearch = term.length === 0 || haystacks.some(v => v.includes(term));

      // Simplified status matching
      const matchesStatus = statusFilter === 'All' || building.status === statusFilter;

      return matchesSearch && matchesStatus;
    });
  }, [buildings, searchTerm, statusFilter]);

  const sortedBuildings = useMemo(() => {
    const toDate = (val) => {
      if (!val) return new Date(0);
      const d = typeof val === 'string' ? new Date(val) : val;
      return isNaN(d?.getTime?.()) ? new Date(0) : d;
    };
    const sorted = [...filteredBuildings].sort((a, b) => {
      let comparison = 0;
      switch (sortBy) {
        case 'Recent': {
          const dateA = toDate(a.lastUpdated || a.registrationDate);
          const dateB = toDate(b.lastUpdated || b.registrationDate);
          comparison = dateA - dateB;
          break;
        }
        case 'Revenue':
          comparison = (a.monthlyRevenue || 0) - (b.monthlyRevenue || 0);
          break;
        case 'Units':
          comparison = (a.totalUnits || 0) - (b.totalUnits || 0);
          break;
        case 'Name':
          comparison = (a.companyName || '').localeCompare(b.companyName || '');
          break;
        default:
          comparison = 0;
      }
      return sortOrder === 'desc' ? -comparison : comparison;
    });
    return sorted;
  }, [filteredBuildings, sortBy, sortOrder]);

  const currentPageItems = useMemo(() => {
    const startIndex = (page - 1) * itemsPerPage;
    return sortedBuildings.slice(startIndex, startIndex + itemsPerPage);
  }, [sortedBuildings, page]);

  const totalPages = Math.ceil(sortedBuildings.length / itemsPerPage);

  const formatDate = (date) => {
    if (!date) return 'N/A';
    // Convert string to Date object if needed
    const dateObj = typeof date === 'string' ? new Date(date) : date;
    if (isNaN(dateObj.getTime())) return 'N/A';
    return dateObj.toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric'
    });
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-PH', {
      style: 'currency',
      currency: 'PHP',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(amount);
  };

  const getSubdomainUrl = (subdomain) => {
    // Build a correct portal URL for both local dev and production
    const host = window.location.hostname || '';
    // For localhost we want to open the dedicated sub-domain app (default 8080)
    if (host.includes('localhost')) {
      const devPort = process.env.REACT_APP_SUBDOMAIN_PORT || '8080';
      return `http://${subdomain}.localhost:${devPort}`;
    }
    // Production: use configurable base domain and protocol
    const baseDomain = process.env.REACT_APP_PORTAL_BASE_DOMAIN || 'jacs.com';
    const protocol = process.env.REACT_APP_PORTAL_PROTOCOL || 'https';
    return `${protocol}://${subdomain}.${baseDomain}`;
  };

  const getSubdomainDisplay = (subdomain) => {
    if (!subdomain || subdomain === 'no-subdomain') {
      return 'No subdomain';
    }
    const host = window.location.hostname || '';
    if (host.includes('localhost')) {
      const devPort = process.env.REACT_APP_SUBDOMAIN_PORT || '8080';
      return `${subdomain}.localhost:${devPort}`;
    }
    const baseDomain = process.env.REACT_APP_PORTAL_BASE_DOMAIN || 'jacs.com';
    return `${subdomain}.${baseDomain}`;
  };

  const handleEditClick = (building) => {
    // Create a shallow copy to edit and ensure arrays are initialized
    setEditingBuilding({ 
      ...building,
      images: building.images || [],
      legalDocs: building.legalDocs || []
    });
    setShowEditModal(true);
  };

  const handleSaveEdit = () => {
    if (!editingBuilding) return;
    const original = buildings.find(b => b.id === editingBuilding.id);
    const updated = { 
      ...editingBuilding, 
      lastUpdated: new Date(),
      // Set main image from first image in images array
      image: editingBuilding.images && editingBuilding.images.length > 0 
        ? editingBuilding.images[0] 
        : editingBuilding.image || 'data:image/svg+xml;base64,'
    };

    // Handle status changes and approval dates
    if (original && original.status !== editingBuilding.status) {
      // If status changed from pending_approval to active, set approval date
      if (original.status === 'pending_approval' && editingBuilding.status === 'active') {
      updated.approvalDate = new Date();
      }
      // If status changed to rejected, clear approval date
      if (editingBuilding.status === 'rejected') {
        updated.approvalDate = null;
    }
    }

    setBuildings(prev => prev.map(b => (b.id === updated.id ? updated : b)));
    setShowEditModal(false);
    setEditingBuilding(null);
  };

  const handleViewDetails = (building) => {
    setDetailsBuilding(building);
    setShowDetailsModal(true);
  };

  const handleInputChange = (field, value) => {
    setNewProperty(prev => ({
      ...prev,
      [field]: value
    }));
  };


  const handleLegalUpload = (event) => {
    const files = Array.from(event.target.files);
    const urls = files.map(file => URL.createObjectURL(file));
    setNewProperty(prev => ({
      ...prev,
      legalDocs: [...prev.legalDocs, ...urls]
    }));
  };

  const removeLegalDoc = (index) => {
    setNewProperty(prev => ({
      ...prev,
      legalDocs: prev.legalDocs.filter((_, i) => i !== index)
    }));
  };

  const handlePropertyImageUpload = (event) => {
    const files = Array.from(event.target.files);
    const urls = files.map(file => URL.createObjectURL(file));
    setNewProperty(prev => ({
      ...prev,
      images: [...(prev.images || []), ...urls].slice(0, 10) // Limit to 10 images
    }));
  };

  const removePropertyImage = (index) => {
    setNewProperty(prev => ({
      ...prev,
      images: prev.images.filter((_, i) => i !== index)
    }));
  };

  // Edit modal handlers
  const handleEditPropertyImageUpload = (event) => {
    const files = Array.from(event.target.files);
    const urls = files.map(file => URL.createObjectURL(file));
    setEditingBuilding(prev => ({
      ...prev,
      images: [...(prev.images || []), ...urls].slice(0, 10) // Limit to 10 images
    }));
  };

  const removeEditPropertyImage = (index) => {
    setEditingBuilding(prev => ({
      ...prev,
      images: prev.images.filter((_, i) => i !== index)
    }));
  };

  const handleEditLegalUpload = (event) => {
    const files = Array.from(event.target.files);
    const urls = files.map(file => URL.createObjectURL(file));
    setEditingBuilding(prev => ({
      ...prev,
      legalDocs: [...(prev.legalDocs || []), ...urls]
    }));
  };

  const removeEditLegalDoc = (index) => {
    setEditingBuilding(prev => ({
      ...prev,
      legalDocs: prev.legalDocs.filter((_, i) => i !== index)
    }));
  };


  const handleSubmit = (e) => {
    e.preventDefault();

    // Check if company is registered
    if (companies.length === 0) {
      alert('Please register a company in your profile first before adding properties.');
      return;
    }

    // Create new building entry with defaults
    const now = new Date();
    const averageRent = parseInt(newProperty.averageRent || '0', 10) || 0;
    const totalUnitsNum = parseInt(newProperty.totalUnits || '0', 10) || 0;
    // Get company name from the first (and only) company
    const companyName = companies.length > 0 ? companies[0].name : '';
    const companyId = companies.length > 0 ? companies[0].id : null;

    const newBuilding = {
      id: Date.now(),
      companyName: companyName,
      buildingName: newProperty.buildingName.trim(),
      subdomain: newProperty.subdomain.trim().toLowerCase(),
      address: newProperty.address.trim(),
      totalUnits: totalUnitsNum,
      occupiedUnits: 0,
      vacantUnits: totalUnitsNum,
      status: 'pending_approval',
      adminApproval: 'Pending',
      registrationDate: now,
      approvalDate: null,
      lastUpdated: now,
      monthlyRevenue: 0,
      averageRent,
      propertyType: newProperty.propertyType || '',
      contactPerson: newProperty.contactPerson.trim(),
      contactEmail: newProperty.contactEmail.trim(),
      contactPhone: newProperty.contactPhone.trim(),
      furnishing: newProperty.furnishing || '',
      averageRent: newProperty.averageRent || '',
      image: newProperty.images && newProperty.images.length > 0 ? newProperty.images[0] : 'data:image/svg+xml;base64,',
      legalDocs: newProperty.legalDocs || []
    };
    setBuildings(prev => [newBuilding, ...prev]);
    // Fire-and-forget API persist; on success, replace temp ID with DB ID
    (async () => {
      try {
        const currentUserIdRawCreate = localStorage.getItem('user_id');
        const payload = {
          company_id: companyId,
          building_name: newBuilding.buildingName,
          subdomain: newBuilding.subdomain,
          address: newBuilding.address,
          total_units: newBuilding.totalUnits,
          monthly_rent: newProperty.averageRent || null,
          property_type: newProperty.propertyType || null,
          furnishing: newProperty.furnishing || null,
          contact_person: newBuilding.contactPerson,
          contact_email: newBuilding.contactEmail,
          contact_phone: newBuilding.contactPhone,
          amenities: newProperty.amenities || [],
          images: newProperty.images || [],
          additional_notes: newProperty.additionalNotes || '',
          legal_documents: newProperty.legalDocs || [],
          owner_id: currentUserIdRawCreate ? parseInt(currentUserIdRawCreate, 10) : undefined
        };
        const res = await api.createCompany(payload);
        if (res && res.item && res.item.id) {
          setBuildings(prev => prev.map(b => b.id === newBuilding.id ? { ...newBuilding, id: res.item.id } : b));
        }
      } catch (err) {
        console.warn('Failed to persist managed property, keeping local only', err);
      }
    })();
    setShowAddModal(false);
    // Reset form
    setNewProperty({
      buildingName: '',
      subdomain: '',
      address: '',
      totalUnits: '',
      propertyType: '',
      yearBuilt: '',
      floorCount: '',
      parkingSpaces: '',
      contactPerson: '',
      contactPosition: '',
      contactEmail: '',
      contactPhone: '',
      businessRegNumber: '',
      tin: '',
      businessPermitNumber: '',
