import React, { useState, useEffect } from 'react';
import ApiService from '../../services/api';

const Profile = ({ onClose }) => {
  const [profileData, setProfileData] = useState({
    firstName: 'JOHN',
    lastName: 'SMITH',
    email: 'johnsmith@gmail.com',
    phone: '+63 912 345 6789',
    dateOfBirth: '',
    addressLine1: '',
    addressLine2: '',
    city: '',
    province: '',
    postalCode: '',
    country: '',
    position: 'Student',
    company: 'University of Cebu',
    bio: 'Student looking for affordable accommodation near the university. Prefer quiet environment for studying.'
  });

  const [stats, setStats] = useState({
    totalInquiries: 0,
    activeInquiries: 0,
    memberSince: ''
  });

  const [isEditing, setIsEditing] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState('https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80');

  const normalizeDateForInput = (value) => {
    if (!value) return '';
    // Accept 'YYYY-MM-DD' or ISO strings
    const match = String(value).match(/^(\d{4}-\d{2}-\d{2})/);
    if (match) return match[1];
    // Accept 'MM/DD/YYYY'
    const us = String(value).match(/^(\d{2})\/(\d{2})\/(\d{4})$/);
    if (us) return `${us[3]}-${us[1]}-${us[2]}`;
    try {
      const d = new Date(value);
      if (!isNaN(d)) {
        const m = `${d.getMonth() + 1}`.padStart(2, '0');
        const day = `${d.getDate()}`.padStart(2, '0');
        return `${d.getFullYear()}-${m}-${day}`;
      }
    } catch (_) {}
    return '';
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfileData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedFile(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreviewUrl(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSave = async () => {
    try {
      // If a new image is selected, upload it first so subsequent profile fetch includes new URL
      if (selectedFile) {
        try {
          const up = await ApiService.uploadTenantProfileImage(selectedFile);
          if (up?.profile_image_url) {
            setPreviewUrl(up.profile_image_url);
          }
        } catch (e) {
          console.error('Profile image upload error:', e);
          alert(e?.message || 'Failed to upload profile image');
          return;
        }
      }

      const payload = {
        first_name: profileData.firstName,
        last_name: profileData.lastName,
        phone_number: profileData.phone || null,
        date_of_birth: normalizeDateForInput(profileData.dateOfBirth) || null,
        address_line1: profileData.addressLine1 || null,
        address_line2: profileData.addressLine2 || null,
        city: profileData.city || null,
        province: profileData.province || null,
        postal_code: profileData.postalCode || null,
        country: profileData.country || null,
      };
      const res = await ApiService.updateTenantProfile(payload);
      const updated = res?.profile || {};
      setProfileData(prev => ({
        ...prev,
        firstName: updated.first_name || prev.firstName,
        lastName: updated.last_name || prev.lastName,
        email: updated.email || prev.email,
        phone: updated.phone_number || prev.phone,
        dateOfBirth: normalizeDateForInput(updated.date_of_birth) || prev.dateOfBirth,
        addressLine1: updated.address_line1 || prev.addressLine1,
        addressLine2: updated.address_line2 || prev.addressLine2,
        city: updated.city || prev.city,
        province: updated.province || prev.province,
        postalCode: updated.postal_code || prev.postalCode,
        country: updated.country || prev.country,
      }));
      if (updated.statistics) {
        setStats({
          totalInquiries: updated.statistics.total_inquiries || 0,
          activeInquiries: updated.statistics.active_inquiries || 0,
          memberSince: updated.statistics.member_since || ''
        });
      }
      // Ensure we have latest server data when modal is reopened
      try {
        const fresh = await ApiService.getTenantProfile();
        const p = fresh?.profile || {};
        setProfileData(prev => ({
          ...prev,
          firstName: p.first_name || prev.firstName,
          lastName: p.last_name || prev.lastName,
          email: p.email || prev.email,
          phone: p.phone_number || prev.phone,
          dateOfBirth: normalizeDateForInput(p.date_of_birth) || prev.dateOfBirth,
          addressLine1: p.address_line1 || prev.addressLine1,
          addressLine2: p.address_line2 || prev.addressLine2,
          city: p.city || prev.city,
          province: p.province || prev.province,
          postalCode: p.postal_code || prev.postalCode,
          country: p.country || prev.country,
        }));
        if (p.statistics) {
          setStats({
            totalInquiries: p.statistics.total_inquiries || 0,
            activeInquiries: p.statistics.active_inquiries || 0,
            memberSince: p.statistics.member_since || ''
          });
        }
      } catch (_) {}
      setIsEditing(false);
      setSelectedFile(null);
      alert('Profile updated successfully!');
    } catch (error) {
      console.error('Tenant profile update error:', error);
      alert(error?.message || 'Failed to update profile');
    }
  };

  const handleCancel = () => {
    // Reset to original data
    setProfileData({
      firstName: 'JOHN',
      lastName: 'SMITH',
      email: 'johnsmith@gmail.com',
      phone: '+63 912 345 6789',
      dateOfBirth: '',
      addressLine1: '',
      addressLine2: '',
      city: '',
      province: '',
      postalCode: '',
      country: '',
      position: 'Student',
      company: 'University of Cebu',
      bio: 'Student looking for affordable accommodation near the university. Prefer quiet environment for studying.'
    });
    setIsEditing(false);
  };

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const data = await ApiService.getTenantProfile();
        const p = data?.profile || {};
        if (p && (p.first_name || p.email)) {
          setProfileData(prev => ({
            ...prev,
            firstName: p.first_name || prev.firstName,
            lastName: p.last_name || prev.lastName,
            email: p.email || prev.email,
            phone: p.phone_number || prev.phone,
            dateOfBirth: normalizeDateForInput(p.date_of_birth) || prev.dateOfBirth,
            addressLine1: p.address_line1 || prev.addressLine1,
            addressLine2: p.address_line2 || prev.addressLine2,
            city: p.city || prev.city,
            province: p.province || prev.province,
            postalCode: p.postal_code || prev.postalCode,
            country: p.country || prev.country,
          }));
          if (p.profile_image_url) setPreviewUrl(p.profile_image_url);
          if (p.statistics) {
            setStats({
              totalInquiries: p.statistics.total_inquiries || 0,
              activeInquiries: p.statistics.active_inquiries || 0,
              memberSince: p.statistics.member_since || ''
            });
          }
          return;
        }
      } catch (error) {
        console.error('Tenant profile fetch error:', error);
      }

      // Fallback: use /auth/me for basic user info if tenant route fails or user is not tenant
      try {
        const me = await ApiService.me();
        const u = me?.user || {};
        setProfileData(prev => ({
          ...prev,
          firstName: u.first_name || prev.firstName,
          lastName: u.last_name || prev.lastName,
          email: u.email || prev.email,
          phone: u.phone_number || prev.phone,
          dateOfBirth: normalizeDateForInput(u.date_of_birth) || prev.dateOfBirth,
          addressLine1: u.address_line1 || prev.addressLine1,
          addressLine2: u.address_line2 || prev.addressLine2,
          city: u.city || prev.city,
          province: u.province || prev.province,
          postalCode: u.postal_code || prev.postalCode,
          country: u.country || prev.country,
        }));
        if (u.profile_image_url) setPreviewUrl(u.profile_image_url);
      } catch (e) {
        console.error('Fallback /auth/me fetch error:', e);
      }
    };
    fetchProfile();
  }, []);

  return (
    <div className="fixed inset-0 bg-gray-900 bg-opacity-75 flex items-center justify-center p-3 z-50">
      <div className="bg-white rounded-xl w-full max-w-4xl max-h-[90vh] shadow-2xl overflow-hidden">
        {/* Header */}
        <div className="flex items-center justify-between p-4 border-b border-gray-200 bg-gradient-to-r from-gray-50 to-white">
          <div className="flex items-center space-x-3">
            <button
              onClick={onClose}
              className="w-9 h-9 bg-black rounded-full flex items-center justify-center hover:bg-gray-800 transition-all duration-300 shadow-lg"
            >
              <svg className="w-4 h-4 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
              </svg>
            </button>
            <div>
              <h1 className="text-lg font-black text-black">Tenant Profile</h1>
              <p className="text-xs text-gray-500">Manage your tenant account information</p>
            </div>
          </div>
          <div className="flex space-x-2">
            {isEditing ? (
              <>
                <button
                  onClick={handleCancel}
                  className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 rounded-lg hover:bg-gray-200 transition-colors"
                >
                  Cancel
                </button>
                <button
                  onClick={handleSave}
                  className="px-4 py-2 text-sm font-medium text-white bg-black rounded-lg hover:bg-gray-800 transition-colors"
                >
                  Save Changes
                </button>
              </>
            ) : (
              <button
                onClick={() => setIsEditing(true)}
                className="px-4 py-2 text-sm font-medium text-white bg-black rounded-lg hover:bg-gray-800 transition-colors flex items-center space-x-2"
              >
                <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" />
                </svg>
                <span>Edit Profile</span>
              </button>
            )}
          </div>
        </div>

        {/* Content */}
        <div className="p-6 overflow-y-auto max-h-[calc(90vh-72px)]">
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
            {/* Left Column - Profile Photo */}
            <div className="lg:col-span-1">
              <div className="bg-gray-50 rounded-xl p-6 border border-gray-200">
                <div className="text-center">
                  <div className="relative inline-block">
                    <div className="w-32 h-32 rounded-full overflow-hidden border-4 border-white shadow-lg mx-auto">
                      <img
                        src={previewUrl}
                        alt="Profile"
                        className="w-full h-full object-cover"
                      />
                    </div>
                    {isEditing && (
                      <label className="absolute bottom-0 right-0 w-10 h-10 bg-black rounded-full flex items-center justify-center cursor-pointer hover:bg-gray-800 transition-colors shadow-lg">
                        <svg className="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 9a2 2 0 012-2h.93a2 2 0 001.664-.89l.812-1.22A2 2 0 0110.07 4h3.86a2 2 0 011.664.89l.812 1.22A2 2 0 0018.07 7H19a2 2 0 012 2v9a2 2 0 01-2 2H5a2 2 0 01-2-2V9z" />
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 13a3 3 0 11-6 0 3 3 0 016 0z" />
                        </svg>
                        <input
                          type="file"
                          accept="image/*"
                          onChange={handleFileChange}
                          className="hidden"
                        />
                      </label>
                    )}
                  </div>
                  <h3 className="text-xl font-black text-black mt-4">
                    {profileData.firstName} {profileData.lastName}
                  </h3>
                  <p className="text-sm text-gray-600 mt-1">{profileData.email}</p>
                  {/* Quick Stats */}
                  <div className="grid grid-cols-3 gap-2 mt-4 text-xs">
                    <div className="bg-white rounded-lg p-2 border">
                      <div className="font-bold text-black">{stats.totalInquiries}</div>
                      <div className="text-gray-500">Inquiries</div>
                    </div>
                    <div className="bg-white rounded-lg p-2 border">
                      <div className="font-bold text-black">{stats.activeInquiries}</div>
                      <div className="text-gray-500">Active</div>
                    </div>
                    <div className="bg-white rounded-lg p-2 border">
                      <div className="font-bold text-black">{stats.memberSince ? new Date(stats.memberSince).getFullYear() : '-'}</div>
                      <div className="text-gray-500">Member</div>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            {/* Right Column - Profile Information */}
            <div className="lg:col-span-2">
              <div className="space-y-6">
                {/* Personal Information */}
                <div className="bg-white rounded-xl p-6 border border-gray-200 shadow-sm">
                  <h3 className="text-lg font-black text-black mb-4 flex items-center">
                    <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                    </svg>
                    Personal Information
                  </h3>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">First Name</label>
                      <input
                        type="text"
                        name="firstName"
                        value={profileData.firstName}
                        onChange={handleInputChange}
                        disabled={!isEditing}
                        className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black text-black placeholder-gray-400 ${
                          isEditing 
                            ? 'border-gray-300 focus:border-transparent' 
                            : 'border-gray-200 bg-gray-50'
                        }`}
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">Last Name</label>
                      <input
                        type="text"
                        name="lastName"
                        value={profileData.lastName}
                        onChange={handleInputChange}
                        disabled={!isEditing}
                        className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black text-black placeholder-gray-400 ${
                          isEditing 
                            ? 'border-gray-300 focus:border-transparent' 
                            : 'border-gray-200 bg-gray-50'
                        }`}
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">Email</label>
                      <input
                        type="email"
                        name="email"
                        value={profileData.email}
                        onChange={handleInputChange}
                        disabled={!isEditing}
                        className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black text-black placeholder-gray-400 ${
                          isEditing 
                            ? 'border-gray-300 focus:border-transparent' 
                            : 'border-gray-200 bg-gray-50'
                        }`}
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">Phone</label>
                      <input
                        type="tel"
                        name="phone"
                        value={profileData.phone}
                        onChange={handleInputChange}
                        disabled={!isEditing}
                        className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black text-black placeholder-gray-400 ${
                          isEditing 
                            ? 'border-gray-300 focus:border-transparent' 
                            : 'border-gray-200 bg-gray-50'
                        }`}
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">Date of Birth</label>
                      <input
                        type="date"
                        name="dateOfBirth"
                        value={profileData.dateOfBirth || ''}
                        onChange={handleInputChange}
                        disabled={!isEditing}
                        className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black text-black placeholder-gray-400 ${
                          isEditing 
                            ? 'border-gray-300 focus:border-transparent' 
                            : 'border-gray-200 bg-gray-50'
                        }`}
                      />
                    </div>
                  </div>
                </div>

                {/* Address Information */}
                <div className="bg-white rounded-xl p-6 border border-gray-200 shadow-sm">
                  <h3 className="text-lg font-black text-black mb-4 flex items-center">
                    <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                    </svg>
                    Address Information
                  </h3>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">Address Line 1</label>
                      <input
                        type="text"
                        name="addressLine1"
                        value={profileData.addressLine1}
                        onChange={handleInputChange}
                        disabled={!isEditing}
                        className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black text-black placeholder-gray-400 ${
                          isEditing 
                            ? 'border-gray-300 focus:border-transparent' 
                            : 'border-gray-200 bg-gray-50'
                        }`}
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">Address Line 2</label>
                      <input
                        type="text"
                        name="addressLine2"
                        value={profileData.addressLine2}
                        onChange={handleInputChange}
                        disabled={!isEditing}
                        className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black text-black placeholder-gray-400 ${
                          isEditing 
                            ? 'border-gray-300 focus:border-transparent' 
                            : 'border-gray-200 bg-gray-50'
                        }`}
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">City</label>
                      <input
                        type="text"
                        name="city"
                        value={profileData.city}
                        onChange={handleInputChange}
                        disabled={!isEditing}
                        className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black text-black placeholder-gray-400 ${
                          isEditing 
                            ? 'border-gray-300 focus:border-transparent' 
                            : 'border-gray-200 bg-gray-50'
                        }`}
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">Province/State</label>
                      <input
                        type="text"
                        name="province"
                        value={profileData.province}
                        onChange={handleInputChange}
                        disabled={!isEditing}
                        className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black text-black placeholder-gray-400 ${
                          isEditing 
                            ? 'border-gray-300 focus:border-transparent' 
                            : 'border-gray-200 bg-gray-50'
                        }`}
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">Postal Code</label>
                      <input
                        type="text"
                        name="postalCode"
                        value={profileData.postalCode}
                        onChange={handleInputChange}
                        disabled={!isEditing}
                        className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black text-black placeholder-gray-400 ${
                          isEditing 
                            ? 'border-gray-300 focus:border-transparent' 
                            : 'border-gray-200 bg-gray-50'
                        }`}
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">Country</label>
                      <input
                        type="text"
                        name="country"
                        value={profileData.country}
                        onChange={handleInputChange}
                        disabled={!isEditing}
                        className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black text-black placeholder-gray-400 ${
                          isEditing 
                            ? 'border-gray-300 focus:border-transparent' 
                            : 'border-gray-200 bg-gray-50'
                        }`}
                      />
                    </div>
                  </div>
                </div>

                {/* Bio */}
                <div className="bg-white rounded-xl p-6 border border-gray-200 shadow-sm">
                  <h3 className="text-lg font-black text-black mb-4 flex items-center">
                    <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 8h10M7 12h4m1 8l-4-4H5a2 2 0 01-2-2V6a2 2 0 012-2h14a2 2 0 012 2v8a2 2 0 01-2 2h-3l-4 4z" />
                    </svg>
                    About Me
                  </h3>
                  <textarea
                    name="bio"
                    value={profileData.bio}
                    onChange={handleInputChange}
                    disabled={!isEditing}
                    rows={4}
                    className={`w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black text-black placeholder-gray-400 resize-none ${
                      isEditing 
                        ? 'border-gray-300 focus:border-transparent' 
                        : 'border-gray-200 bg-gray-50'
                    }`}
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
