import React, { useEffect, useState, useRef } from 'react';

const SearchSection = ({ filters = {}, onChange = () => {} }) => {
  const [classificationOpen, setClassificationOpen] = useState(false);
  const [priceRangeOpen, setPriceRangeOpen] = useState(false);
  const [locationModalOpen, setLocationModalOpen] = useState(false);
  const [selectedClassification, setSelectedClassification] = useState(filters.type || '');
  const [selectedLocation, setSelectedLocation] = useState(filters.location || '');
  const [minPrice, setMinPrice] = useState(filters.min_price || '');
  const [maxPrice, setMaxPrice] = useState(filters.max_price || '');
  const [searchQuery, setSearchQuery] = useState(filters.search || '');

  // Use backend enum values with human labels
  const classifications = [
    { value: '', label: 'All Types' },
    { value: 'bed_space', label: 'Bed Space' },
    { value: 'dormitory', label: 'Dormitory' },
    { value: 'boarding_house', label: 'Boarding House' },
    { value: 'studio_apartment', label: 'Studio Apartment' },
    { value: 'room_for_rent', label: 'Room for Rent' }
  ];

  // Location options
  const locations = [
    { value: '', label: 'All Locations' },
    { value: 'cebu_city', label: 'Cebu City' },
    { value: 'mandaue', label: 'Mandaue City' },
    { value: 'lapu_lapu', label: 'Lapu-Lapu City' },
    { value: 'talisay', label: 'Talisay City' },
    { value: 'consolacion', label: 'Consolacion' },
    { value: 'minglanilla', label: 'Minglanilla' }
  ];

  const toggleClassification = () => {
    setClassificationOpen(!classificationOpen);
    setPriceRangeOpen(false);
  };

  const togglePriceRange = () => {
    setPriceRangeOpen(!priceRangeOpen);
    setClassificationOpen(false);
  };

  const openLocationModal = () => {
    setLocationModalOpen(true);
    setClassificationOpen(false);
    setPriceRangeOpen(false);
  };

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleClassificationSelect = (classification) => {
    setSelectedClassification(classification);
    setClassificationOpen(false);
    onChange({ ...filters, type: classification });
  };

  const handleLocationSelect = (location, locationName) => {
    setSelectedLocation(locationName || location);
    setLocationModalOpen(false);
    onChange({ ...filters, location: location, locationName: locationName });
  };

  const getCurrentLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          // You can use a reverse geocoding service here to get the address
          const locationString = `${latitude.toFixed(4)}, ${longitude.toFixed(4)}`;
          handleLocationSelect(`${latitude},${longitude}`, `Current Location (${locationString})`);
        },
        (error) => {
          console.error('Error getting location:', error);
          alert('Unable to get your current location. Please select a location manually.');
        }
      );
    } else {
      alert('Geolocation is not supported by this browser.');
    }
  };

  const handleApplyPriceRange = () => {
    setPriceRangeOpen(false);
    onChange({ ...filters, min_price: minPrice, max_price: maxPrice });
  };

  const handleClearFilters = () => {
    setSelectedClassification('');
    setSelectedLocation('');
    setMinPrice('');
    setMaxPrice('');
    setSearchQuery('');
    onChange({ search: '', type: '', location: '', min_price: '', max_price: '' });
  };

  // Debounce search changes to avoid chatty requests
  useEffect(() => {
    const id = setTimeout(() => {
      onChange({ ...filters, search: searchQuery });
    }, 300);
    return () => clearTimeout(id);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [searchQuery]);

  const getSelectedClassificationLabel = () => {
    const selected = classifications.find(c => c.value === selectedClassification);
    return selected ? selected.label : 'All Types';
  };


  const hasActiveFilters = selectedClassification || selectedLocation || minPrice || maxPrice || searchQuery;

  return (
    <div className="bg-gradient-to-r from-gray-50 to-white border border-gray-200 rounded-2xl p-6 mb-8 shadow-sm">

      {/* Main Search Bar */}
      <div className="mb-6">
        <div className="relative">
          <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none">
            <svg className="h-6 w-6 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
            </svg>
          </div>
          <input
            type="text"
            placeholder="Search by location, property name, amenities, or any keyword..."
            value={searchQuery}
            onChange={handleSearchChange}
            className="block w-full pl-12 pr-4 py-4 text-lg border-2 border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-black focus:border-black transition-all duration-200 bg-white shadow-sm"
          />
        </div>
      </div>

      {/* Filters Row - Single Line Layout */}
      <div className="flex items-center gap-3 mb-4 overflow-x-auto">
        {/* Classification Filter */}
        <div className="relative flex-shrink-0">
          <button 
            onClick={toggleClassification}
            className={`flex items-center space-x-2 px-4 py-2 border-2 rounded-lg transition-all duration-200 font-medium whitespace-nowrap ${
              selectedClassification 
                ? 'border-black bg-black text-white shadow-lg' 
                : 'border-gray-200 hover:border-gray-300 bg-white text-gray-700 hover:bg-gray-50'
            }`}
          >
            <svg className="h-4 w-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16m14 0h2m-2 0h-5m-9 0H3m2 0h5M9 7h1m-1 4h1m4-4h1m-1 4h1m-5 10v-5a1 1 0 011-1h2a1 1 0 011 1v5m-4 0h4" />
            </svg>
            <span>{getSelectedClassificationLabel()}</span>
            <svg className={`w-4 h-4 transition-transform duration-200 ${classificationOpen ? 'rotate-180' : ''}`} fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
            </svg>
          </button>

          {/* Classification Dropdown */}
          {classificationOpen && (
            <div className="absolute top-full left-0 mt-2 w-full sm:w-72 bg-white border-2 border-black rounded-xl shadow-2xl z-50 overflow-hidden">
              <div className="p-2">
                <div className="text-xs font-semibold text-gray-500 mb-3 px-3 uppercase tracking-wide">Property Type</div>
                <div className="space-y-1">
                  {classifications.map((c) => (
                    <button
                      key={c.value}
                      onClick={() => handleClassificationSelect(c.value)}
                      className={`w-full text-left px-4 py-3 text-sm font-medium rounded-lg transition-all duration-150 ${
                        selectedClassification === c.value 
                          ? 'bg-black text-white shadow-sm' 
                          : 'text-gray-700 hover:bg-gray-100'
                      }`}
                    >
                      {c.label}
                    </button>
                  ))}
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Location Filter */}
        <div className="relative flex-shrink-0">
          <button 
            onClick={openLocationModal}
            className={`flex items-center space-x-2 px-4 py-2 border-2 rounded-lg transition-all duration-200 font-medium whitespace-nowrap ${
              selectedLocation 
                ? 'border-black bg-black text-white shadow-lg' 
                : 'border-gray-200 hover:border-gray-300 bg-white text-gray-700 hover:bg-gray-50'
            }`}
          >
            <svg className="h-4 w-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
            </svg>
            <span>{selectedLocation || 'Location'}</span>
          </button>
        </div>

        {/* Price Range Filter */}
        <div className="relative flex-shrink-0">
          <button 
            onClick={togglePriceRange}
            className={`flex items-center space-x-2 px-4 py-2 border-2 rounded-lg transition-all duration-200 font-medium whitespace-nowrap ${
              (minPrice || maxPrice)
                ? 'border-black bg-black text-white shadow-lg' 
                : 'border-gray-200 hover:border-gray-300 bg-white text-gray-700 hover:bg-gray-50'
            }`}
          >
            <svg className="h-4 w-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.599 1M12 8V7m0 1v8m0 0v1m0-1c-1.11 0-2.08-.402-2.599-1" />
            </svg>
            <span>
              {(minPrice || maxPrice) 
                ? `₱${minPrice || '0'} - ₱${maxPrice || '∞'}` 
                : 'Price Range'
              }
            </span>
            <svg className={`w-4 h-4 transition-transform duration-200 ${priceRangeOpen ? 'rotate-180' : ''}`} fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
            </svg>
          </button>

          {/* Price Range Dropdown */}
          {priceRangeOpen && (
            <div className="absolute top-full left-0 mt-2 w-full sm:w-80 bg-white border-2 border-black rounded-xl shadow-2xl z-50">
              <div className="p-6">
                <div className="text-xs font-semibold text-gray-500 mb-4 uppercase tracking-wide">Price Range (₱)</div>
                
                {/* Min Price */}
                <div className="mb-4">
                  <label className="block text-sm font-semibold text-gray-700 mb-2">Minimum Price</label>
                  <input
                    type="number"
                    value={minPrice}
                    onChange={(e) => setMinPrice(e.target.value)}
                    placeholder="0"
                    className="w-full px-4 py-3 border-2 border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-black focus:border-black transition-all duration-200"
                  />
                </div>

                {/* Max Price */}
                <div className="mb-6">
                  <label className="block text-sm font-semibold text-gray-700 mb-2">Maximum Price</label>
                  <input
                    type="number"
                    value={maxPrice}
                    onChange={(e) => setMaxPrice(e.target.value)}
                    placeholder="10000"
                    className="w-full px-4 py-3 border-2 border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-black focus:border-black transition-all duration-200"
                  />
                </div>

                {/* Apply Button */}
                <button
                  onClick={handleApplyPriceRange}
                  className="w-full bg-black text-white py-3 px-4 rounded-lg font-semibold hover:bg-gray-800 transition-all duration-200 shadow-sm"
                >
                  Apply Price Range
                </button>
              </div>
            </div>
          )}
        </div>

        {/* Clear Filters Button */}
        {hasActiveFilters && (
          <button
            onClick={handleClearFilters}
            className="flex items-center space-x-2 px-4 py-2 text-gray-600 hover:text-gray-800 hover:bg-gray-100 rounded-lg transition-all duration-200 font-medium flex-shrink-0"
          >
            <svg className="h-4 w-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
            <span>Clear All</span>
          </button>
        )}
      </div>

      {/* Active Filters Display */}
      {hasActiveFilters && (
        <div className="flex flex-wrap gap-2">
          {searchQuery && (
            <span className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-black text-white">
              Search: "{searchQuery}"
            </span>
          )}
          {selectedClassification && (
            <span className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-black text-white">
              {getSelectedClassificationLabel()}
            </span>
          )}
          {selectedLocation && (
            <span className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-black text-white">
              {selectedLocation}
            </span>
          )}
          {(minPrice || maxPrice) && (
            <span className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-black text-white">
              ₱{minPrice || '0'} - ₱{maxPrice || '∞'}
            </span>
          )}
        </div>
      )}

      {/* Location Modal */}
      {locationModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-2xl w-full max-w-md mx-auto shadow-2xl">
            {/* Modal Header */}
            <div className="flex items-center justify-between p-6 border-b border-gray-200">
              <h3 className="text-xl font-bold text-gray-900">Select Location</h3>
              <button
                onClick={() => setLocationModalOpen(false)}
                className="text-gray-400 hover:text-gray-600 transition-colors"
              >
                <svg className="h-6 w-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>

            {/* Modal Content */}
            <div className="p-6">
              {/* Current Location Button */}
              <button
                onClick={getCurrentLocation}
                className="w-full flex items-center justify-center space-x-3 px-6 py-4 bg-black text-white rounded-xl hover:bg-gray-800 transition-all duration-200 font-medium mb-4"
              >
                <svg className="h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
                </svg>
                <span>Use Current Location</span>
              </button>

              {/* Divider */}
              <div className="flex items-center my-4">
                <div className="flex-1 border-t border-gray-200"></div>
                <span className="px-3 text-sm text-gray-500">or select manually</span>
                <div className="flex-1 border-t border-gray-200"></div>
              </div>

              {/* Predefined Locations */}
              <div className="space-y-2">
                <h4 className="text-sm font-semibold text-gray-700 mb-3">Popular Locations</h4>
                {locations.slice(1).map((location) => (
                  <button
                    key={location.value}
                    onClick={() => handleLocationSelect(location.value, location.label)}
                    className="w-full text-left px-4 py-3 rounded-lg border border-gray-200 hover:border-black hover:bg-gray-50 transition-all duration-200"
                  >
                    <div className="flex items-center space-x-3">
                      <svg className="h-4 w-4 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                      </svg>
                      <span className="font-medium text-gray-900">{location.label}</span>
                    </div>
                  </button>
                ))}
              </div>

              {/* Custom Location Input */}
              <div className="mt-6">
                <h4 className="text-sm font-semibold text-gray-700 mb-3">Custom Location</h4>
                <div className="flex space-x-2">
                  <input
                    type="text"
                    placeholder="Enter address or coordinates..."
                    className="flex-1 px-4 py-3 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-black focus:border-black"
                    onKeyPress={(e) => {
                      if (e.key === 'Enter') {
                        const value = e.target.value.trim();
                        if (value) {
                          handleLocationSelect(value, value);
                        }
                      }
                    }}
                  />
                  <button
                    onClick={(e) => {
                      const input = e.target.previousElementSibling;
                      const value = input.value.trim();
                      if (value) {
                        handleLocationSelect(value, value);
                      }
                    }}
                    className="px-4 py-3 bg-gray-100 text-gray-700 rounded-lg hover:bg-gray-200 transition-colors"
                  >
                    <svg className="h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                    </svg>
                  </button>
                </div>
              </div>

              {/* Clear Location */}
              {selectedLocation && (
                <button
                  onClick={() => handleLocationSelect('', '')}
                  className="w-full mt-4 px-4 py-2 text-gray-600 hover:text-gray-800 hover:bg-gray-100 rounded-lg transition-all duration-200 font-medium"
                >
                  Clear Location
                </button>
              )}
            </div>
          </div>
        </div>
      )}
      </div>
  );
};

export default SearchSection;
