import React, { useState, useEffect } from 'react';
import ApiService from '../../services/api';

const BillingPayment = () => {
  const [currentPlan, setCurrentPlan] = useState(null);
  const [billingHistory, setBillingHistory] = useState([]);
  const [paymentMethods, setPaymentMethods] = useState([]);
  const [subscriptionPlans, setSubscriptionPlans] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedPlan, setSelectedPlan] = useState(null);
  const [showUpgradeModal, setShowUpgradeModal] = useState(false);
  const [showAddPaymentModal, setShowAddPaymentModal] = useState(false);
  const [showPaymentModal, setShowPaymentModal] = useState(false);
  const [selectedBilling, setSelectedBilling] = useState(null);
  const [processingPayment, setProcessingPayment] = useState(false);
  const [error, setError] = useState(null);
  const [paymentForm, setPaymentForm] = useState({
    payment_method: 'credit_card',
    card_number: '',
    expiry_month: '',
    expiry_year: '',
    cvv: '',
    cardholder_name: ''
  });

  // Utility functions
  const isSubscriptionExpiring = (nextBilling) => {
    if (!nextBilling) return false;
    const now = new Date();
    const billingDate = new Date(nextBilling);
    const daysUntilExpiry = Math.ceil((billingDate - now) / (1000 * 60 * 60 * 24));
    return daysUntilExpiry <= 7 && daysUntilExpiry > 0;
  };

  const isSubscriptionExpired = (nextBilling, status) => {
    if (!nextBilling || status === 'cancelled') return true;
    const now = new Date();
    const billingDate = new Date(nextBilling);
    return billingDate < now;
  };


  useEffect(() => {
    fetchBillingData();
  }, []);

  const fetchBillingData = async () => {
    try {
      setLoading(true);
      setError(null);

      const [planRes, historyRes, methodsRes, plansRes] = await Promise.all([
        ApiService.getCurrentSubscription(),
        ApiService.getBillingHistory(),
        ApiService.getPaymentMethods(),
        ApiService.getSubscriptionPlans()
      ]);

      setCurrentPlan(planRes?.subscription || planRes);
      setBillingHistory(historyRes?.billing_history || historyRes || []);
      setPaymentMethods(methodsRes?.payment_methods || methodsRes || []);

      // Use plans list from backend ({ plans: [...] })
      setSubscriptionPlans(plansRes?.plans || plansRes?.data || plansRes || []);

    } catch (error) {
      console.error('Error fetching billing data:', error);
      setError('Failed to load billing information. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handlePlanUpgrade = (plan) => {
    setSelectedPlan(plan);
    setShowUpgradeModal(true);
  };

  const confirmUpgrade = async () => {
    try {
      setProcessingPayment(true);
      setError(null);

      // Check for default payment method
      const defaultPaymentMethod = paymentMethods.find(pm => pm.isDefault);

      const response = await ApiService.upgradePlan(selectedPlan.id, defaultPaymentMethod?.id || null);

      // Show success message
      alert(`Success! ${response.message}\nBilling entry created with invoice: ${response.billing?.invoice_number}`);

      // Refresh billing data
      await fetchBillingData();

      setShowUpgradeModal(false);
      setSelectedPlan(null);
    } catch (error) {
      console.error('Error upgrading plan:', error);
      setError(error.message || 'Failed to upgrade plan. Please try again.');
    } finally {
      setProcessingPayment(false);
    }
  };

  const handleAddPaymentMethod = async () => {
    try {
      setProcessingPayment(true);
      setError(null);

      // Validate required fields
      if (!paymentForm.cardholder_name || !paymentForm.card_number || !paymentForm.expiry_month || !paymentForm.expiry_year || !paymentForm.cvv) {
        setError('Please fill in all required fields');
        return;
      }

      // Prepare payment method data
      const paymentMethodData = {
        type: paymentForm.payment_method,
        cardholder_name: paymentForm.cardholder_name,
        card_number: paymentForm.card_number,
        expiry_month: paymentForm.expiry_month,
        expiry_year: paymentForm.expiry_year,
        cvv: paymentForm.cvv,
        brand: 'Visa' // In real app, detect from card number
      };

      const response = await ApiService.addPaymentMethod(paymentMethodData);

      if (response.success) {
        // Add the new payment method to local state
        const newPaymentMethod = {
          id: response.payment_method.id,
          brand: response.payment_method.brand,
          last4: response.payment_method.last4,
          isDefault: response.payment_method.is_default
        };

        setPaymentMethods(prev => [...prev, newPaymentMethod]);

        alert('Payment method added successfully!');
        setShowAddPaymentModal(false);

        // Reset form
        setPaymentForm({
          payment_method: 'credit_card',
          card_number: '',
          expiry_month: '',
          expiry_year: '',
          cvv: '',
          cardholder_name: ''
        });
      } else {
        setError('Failed to add payment method');
      }
    } catch (error) {
      console.error('Error adding payment method:', error);
      setError(error.message || 'Failed to add payment method');
    } finally {
      setProcessingPayment(false);
    }
  };

  const handleRemovePaymentMethod = async (paymentMethodId) => {
    if (!window.confirm('Are you sure you want to remove this payment method?')) return;

    try {
      await ApiService.removePaymentMethod(paymentMethodId);

      // Remove from local state
      setPaymentMethods(prev => prev.filter(method => method.id !== paymentMethodId));

      alert('Payment method removed successfully!');
    } catch (error) {
      console.error('Error removing payment method:', error);
      setError('Failed to remove payment method');
    }
  };

  const handleSetDefaultPayment = async (paymentMethodId) => {
    try {
      await ApiService.setDefaultPaymentMethod(paymentMethodId);

      // Update local state
      setPaymentMethods(prev => prev.map(method => ({
        ...method,
        isDefault: method.id === paymentMethodId
      })));

      alert('Default payment method updated!');
    } catch (error) {
      console.error('Error setting default payment method:', error);
      setError('Failed to update default payment method');
    }
  };

  const handlePayBill = (billing) => {
    setSelectedBilling(billing);
    setShowPaymentModal(true);
    // Reset payment form
    setPaymentForm({
      payment_method: 'credit_card',
      card_number: '',
      expiry_month: '',
      expiry_year: '',
      cvv: '',
      cardholder_name: ''
    });
  };

  const handlePaymentInputChange = (e) => {
    const { name, value } = e.target;
    setPaymentForm(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const processPayment = async () => {
    try {
      setProcessingPayment(true);
      setError(null);

      const response = await ApiService.processPayment(selectedBilling.id, paymentForm);

      if (response.success) {
        alert(`Payment Successful!\nTransaction ID: ${response.transaction_id}\nAmount: ₱${response.amount_paid.toLocaleString()}`);

        // Refresh billing data
        await fetchBillingData();

        setShowPaymentModal(false);
        setSelectedBilling(null);
      } else {
        setError(response.message || 'Payment failed');
      }
    } catch (error) {
      console.error('Payment error:', error);
      setError(error.message || 'Payment processing failed');
    } finally {
      setProcessingPayment(false);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-white flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-black mx-auto mb-4"></div>
          <p className="text-gray-600 font-medium">Loading billing information...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-white">

      {/* Error Banner */}
      {error && (
        <div className="bg-red-50 border border-red-200 rounded-lg p-4 mb-6">
          <div className="flex">
            <svg className="w-5 h-5 text-red-400 mt-0.5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
            <div className="ml-3">
              <p className="text-red-800 font-medium">{error}</p>
              <button
                onClick={fetchBillingData}
                className="mt-2 text-red-600 hover:text-red-800 font-medium"
              >
                Try Again
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Header */}
      <div className="bg-gradient-to-r from-gray-900 to-black text-white rounded-2xl">
        <div className="max-w-7xl mx-auto px-6 py-12">
          <div className="text-center">
            <h1 className="text-4xl md:text-5xl font-bold mb-4">Billing & Payments</h1>
            <p className="text-gray-300 text-lg max-w-2xl mx-auto">
              Manage your subscription plan, billing history, and payment methods
            </p>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-6 py-8">
        {/* Current Plan */}
        <div className="bg-white rounded-2xl shadow-lg border border-gray-100 mb-8">
          <div className="px-8 py-6 border-b border-gray-100">
            <h2 className="text-2xl font-bold text-gray-900">Current Subscription</h2>
            <p className="text-gray-600 mt-1">Your active plan and billing information</p>
          </div>
          <div className="p-8">
            <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between">
              <div className="flex items-center space-x-6 mb-6 lg:mb-0">
                <div className={`w-16 h-16 rounded-2xl flex items-center justify-center ${
                  isSubscriptionExpired(currentPlan?.next_billing_date, currentPlan?.status)
                    ? 'bg-red-500'
                    : isSubscriptionExpiring(currentPlan?.next_billing_date)
                    ? 'bg-yellow-500'
                    : 'bg-gray-900'
                }`}>
                  <svg className="w-8 h-8 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 3v4M3 5h4M6 17v4m-2-2h4m5-16l2.286 6.857L21 12l-5.714 2.143L13 21l-2.286-6.857L5 12l5.714-2.143L13 3z" />
                  </svg>
                </div>
                <div>
                  <h3 className="text-2xl font-bold text-gray-900">
                    {currentPlan?.plan?.name || (currentPlan?.status === 'inactive' ? 'No Active Plan' : 'No Plan')}
                  </h3>
                  <p className="text-gray-600 text-lg">
                    {currentPlan?.plan?.monthly_price ? 
                      `₱${parseFloat(currentPlan.plan.monthly_price).toLocaleString()}/month` : 
                      'Free'
                    }
                  </p>
                  <p className="text-sm text-gray-500 mt-1">
                    {currentPlan?.next_billing_date ? (
                      <>Next billing: {new Date(currentPlan.next_billing_date).toLocaleDateString()}</>
                    ) : (
                      currentPlan?.status === 'inactive' ? 'No active subscription' : 'No billing scheduled'
                    )}
                  </p>
                  {isSubscriptionExpired(currentPlan?.next_billing_date, currentPlan?.status) && (
                    <p className="text-sm text-red-600 font-medium mt-1">⚠️ Subscription Expired</p>
                  )}
                  {isSubscriptionExpiring(currentPlan?.next_billing_date) && (
                    <p className="text-sm text-yellow-600 font-medium mt-1">⚠️ Expires Soon</p>
                  )}
                  {currentPlan?.status === 'inactive' && (
                    <p className="text-sm text-blue-600 font-medium mt-1">💡 Choose a plan to get started</p>
                  )}
                </div>
              </div>
              <div className="flex flex-col sm:flex-row gap-4">
                <span className={`px-4 py-2 rounded-xl text-sm font-semibold ${
                  currentPlan?.status === 'active' && !isSubscriptionExpired(currentPlan?.next_billing_date, currentPlan?.status)
                    ? 'bg-green-100 text-green-800' 
                    : currentPlan?.status === 'cancelled'
                    ? 'bg-red-100 text-red-800'
                    : isSubscriptionExpired(currentPlan?.next_billing_date, currentPlan?.status)
                    ? 'bg-red-100 text-red-800'
                    : 'bg-gray-100 text-gray-500'
                }`}>
                  {isSubscriptionExpired(currentPlan?.next_billing_date, currentPlan?.status) 
                    ? 'EXPIRED' 
                    : currentPlan?.status?.toUpperCase() || 'INACTIVE'
                  }
                </span>
                {isSubscriptionExpired(currentPlan?.next_billing_date, currentPlan?.status) || currentPlan?.status === 'inactive' ? (
                  <button 
                    onClick={() => {
                      if (subscriptionPlans.length > 0) {
                        handlePlanUpgrade(subscriptionPlans[0]);
                      }
                    }}
                    className="bg-blue-600 text-white px-6 py-2 rounded-xl hover:bg-blue-700 transition-colors font-semibold"
                  >
                    {currentPlan?.status === 'inactive' ? 'Choose Plan' : 'Renew Now'}
                  </button>
                ) : (
                  <button 
                    onClick={() => setShowAddPaymentModal(true)}
                    className="bg-black text-white px-6 py-2 rounded-xl hover:bg-gray-800 transition-colors font-semibold"
                  >
                    Manage Plan
                  </button>
                )}
              </div>
            </div>
          </div>
        </div>

        {/* Subscription Plans */}
        <div className="bg-white rounded-2xl shadow-lg border border-gray-100 mb-8">
          <div className="px-8 py-6 border-b border-gray-100">
            <h2 className="text-2xl font-bold text-gray-900">Subscription Plans</h2>
            <p className="text-gray-600 mt-1">Choose the plan that best fits your needs</p>
          </div>
          <div className="p-8">
            {loading ? (
              <div className="text-center py-12">
                <div className="w-16 h-16 bg-gray-100 rounded-2xl flex items-center justify-center mx-auto mb-4">
                  <svg className="w-8 h-8 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                  </svg>
                </div>
                <p className="text-gray-600">Loading subscription plans...</p>
              </div>
            ) : subscriptionPlans.length === 0 ? (
              <div className="text-center py-12">
                <div className="w-16 h-16 bg-gray-100 rounded-2xl flex items-center justify-center mx-auto mb-4">
                  <svg className="w-8 h-8 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20 13V6a2 2 0 00-2-2H6a2 2 0 00-2 2v7m16 0v5a2 2 0 01-2 2H6a2 2 0 01-2-2v-5m16 0h-2.586a1 1 0 00-.707.293l-2.414 2.414a1 1 0 01-.707.293h-3.172a1 1 0 01-.707-.293l-2.414-2.414A1 1 0 006.586 13H4" />
                  </svg>
                </div>
                <h3 className="text-lg font-semibold text-gray-900 mb-2">No Subscription Plans Available</h3>
                <p className="text-gray-600 mb-4">Subscription plans haven't been set up yet.</p>
                <p className="text-sm text-gray-500">Please contact the administrator to configure subscription plans.</p>
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
                {subscriptionPlans.map((plan) => (
                  <div
                    key={plan.id}
                    className={`relative rounded-2xl border-2 p-8 border-gray-200 bg-white hover:border-gray-300 transition-all`}
                  >

                    <div className="text-center mb-8">
                      <h3 className="text-2xl font-bold text-gray-900 mb-2">{plan.name}</h3>
                      <div className="mb-4">
                        <span className="text-4xl font-bold text-gray-900">
                          ₱{parseFloat(plan.monthly_price || 0).toLocaleString()}
                        </span>
                        <span className="text-gray-600">/month</span>
                      </div>
                      {plan.yearly_price && (
                        <p className="text-sm text-green-600">
                          Save {plan.yearly_discount_percentage}% with yearly billing
                        </p>
                      )}
                    </div>

                    <ul className="space-y-4 mb-8">
                      <li className="flex items-start space-x-3">
                        <svg className="w-5 h-5 text-gray-900 mt-0.5 flex-shrink-0" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                        </svg>
                        <span className="text-gray-700">
                          {plan.max_properties === -1 ? 'Unlimited properties' : `Up to ${plan.max_properties || 0} properties`}
                        </span>
                      </li>
                      {plan.analytics_enabled && (
                        <li className="flex items-start space-x-3">
                          <svg className="w-5 h-5 text-gray-900 mt-0.5 flex-shrink-0" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                          </svg>
                          <span className="text-gray-700">Advanced analytics & reports</span>
                        </li>
                      )}
                      {plan.priority_support ? (
                        <li className="flex items-start space-x-3">
                          <svg className="w-5 h-5 text-gray-900 mt-0.5 flex-shrink-0" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                          </svg>
                          <span className="text-gray-700">Priority support</span>
                        </li>
                      ) : (
                        <li className="flex items-start space-x-3">
                          <svg className="w-5 h-5 text-gray-900 mt-0.5 flex-shrink-0" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                          </svg>
                          <span className="text-gray-700">Email support</span>
                        </li>
                      )}
                      {plan.api_access && (
                        <li className="flex items-start space-x-3">
                          <svg className="w-5 h-5 text-gray-900 mt-0.5 flex-shrink-0" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                          </svg>
                          <span className="text-gray-700">API access</span>
                        </li>
                      )}
                      {plan.advanced_reporting && (
                        <li className="flex items-start space-x-3">
                          <svg className="w-5 h-5 text-gray-900 mt-0.5 flex-shrink-0" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                          </svg>
                          <span className="text-gray-700">Advanced reporting</span>
                        </li>
                      )}
                      {plan.trial_days > 0 && (
                        <li className="flex items-start space-x-3">
                          <svg className="w-5 h-5 text-blue-600 mt-0.5 flex-shrink-0" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                          </svg>
                          <span className="text-blue-700 font-medium">{plan.trial_days}-day free trial</span>
                        </li>
                      )}
                    </ul>

                    <button
                      onClick={() => handlePlanUpgrade(plan)}
                      disabled={currentPlan?.plan?.id === plan.id}
                      className={`w-full py-3 px-6 rounded-xl font-semibold transition-colors ${
                        currentPlan?.plan?.id === plan.id
                          ? 'bg-gray-100 text-gray-500 cursor-not-allowed'
                          : 'bg-gray-900 text-white hover:bg-gray-700'
                      }`}
                    >
                      {currentPlan?.plan?.id === plan.id ? 'Current Plan' : `Choose ${plan.name}`}
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Billing History */}
          <div className="bg-white rounded-2xl shadow-lg border border-gray-100">
            <div className="px-8 py-6 border-b border-gray-100">
              <h2 className="text-2xl font-bold text-gray-900">Billing History</h2>
              <p className="text-gray-600 mt-1">Your recent transactions</p>
            </div>
            <div className="p-8">
              {billingHistory.length === 0 ? (
                <div className="text-center py-8">
                  <div className="w-16 h-16 bg-gray-100 rounded-2xl flex items-center justify-center mx-auto mb-4">
                    <svg className="w-8 h-8 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                    </svg>
                  </div>
                  <h4 className="text-lg font-medium text-gray-900 mb-2">No Billing History</h4>
                  <p className="text-gray-600">Your billing transactions will appear here once you have an active subscription.</p>
                </div>
              ) : (
                <div className="space-y-4">
                  {billingHistory.map((transaction) => (
                    <div key={transaction.id} className="flex items-center justify-between p-4 border border-gray-200 rounded-xl hover:bg-gray-50 transition-colors">
                      <div>
                        <p className="font-semibold text-gray-900">{transaction.plan_name || transaction.plan} Plan</p>
                        <p className="text-sm text-gray-600">
                          {transaction.billing_date ? new Date(transaction.billing_date).toLocaleDateString() : 
                           transaction.date ? new Date(transaction.date).toLocaleDateString() : 'N/A'}
                        </p>
                        {transaction.invoice_number && (
                          <p className="text-xs text-gray-500">Invoice: {transaction.invoice_number}</p>
                        )}
                      </div>
                      <div className="text-right flex items-center space-x-4">
                        <div>
                          <p className="font-bold text-gray-900">₱{transaction.amount.toLocaleString()}</p>
                          <span className={`text-sm px-2 py-1 rounded-full ${
                            transaction.status === 'paid' 
                              ? 'bg-green-100 text-green-800' 
                              : transaction.status === 'pending'
                              ? 'bg-yellow-100 text-yellow-800'
                              : 'bg-gray-100 text-gray-500'
                          }`}>
                            {transaction.status.toUpperCase()}
                          </span>
                        </div>
                        {transaction.status === 'pending' && (
                          <button
                            onClick={() => handlePayBill(transaction)}
                            className="bg-black text-white px-3 py-1 rounded-lg hover:bg-gray-800 transition-colors text-sm font-medium"
                          >
                            Pay Now
                          </button>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>

          {/* Payment Methods */}
          <div className="bg-white rounded-2xl shadow-lg border border-gray-100">
            <div className="px-8 py-6 border-b border-gray-100">
              <div className="flex items-center justify-between">
                <div>
                  <h2 className="text-2xl font-bold text-gray-900">Payment Methods</h2>
                  <p className="text-gray-600 mt-1">Manage your payment options</p>
                </div>
                <button 
                  onClick={() => setShowAddPaymentModal(true)}
                  className="bg-black text-white px-4 py-2 rounded-xl hover:bg-gray-800 transition-colors font-semibold"
                >
                  Add New
                </button>
              </div>
            </div>
            <div className="p-8">
              {paymentMethods.length === 0 ? (
                <div className="text-center py-8">
                  <div className="w-16 h-16 bg-gray-100 rounded-2xl flex items-center justify-center mx-auto mb-4">
                    <svg className="w-8 h-8 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 10h18M7 15h1m4 0h1m-7 4h12a3 3 0 003-3V8a3 3 0 00-3-3H6a3 3 0 00-3 3v8a3 3 0 003 3z" />
                    </svg>
                  </div>
                  <h4 className="text-lg font-medium text-gray-900 mb-2">No Payment Methods</h4>
                  <p className="text-gray-600 mb-4">Add a payment method to manage your subscription billing.</p>
                  <button 
                    onClick={() => setShowAddPaymentModal(true)}
                    className="bg-black text-white px-4 py-2 rounded-xl hover:bg-gray-800 transition-colors font-semibold"
                  >
                    Add Payment Method
                  </button>
                </div>
              ) : (
                <div className="space-y-4">
                  {paymentMethods.map((method) => (
                    <div key={method.id} className="flex items-center justify-between p-4 border border-gray-200 rounded-xl hover:bg-gray-50 transition-colors">
                      <div className="flex items-center space-x-4">
                        <div className={`w-12 h-8 rounded flex items-center justify-center ${
                          method.brand === 'Visa' ? 'bg-blue-600' :
                          method.brand === 'Mastercard' ? 'bg-red-600' :
                          method.brand === 'Amex' ? 'bg-green-600' :
                          'bg-gray-900'
                        }`}>
                          <svg className="w-6 h-4 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 10h18M7 15h1m4 0h1m-7 4h12a3 3 0 003-3V8a3 3 0 00-3-3H6a3 3 0 00-3 3v8a3 3 0 003 3z" />
                          </svg>
                        </div>
                        <div>
                          <p className="font-semibold text-gray-900">{method.brand} •••• {method.last4}</p>
                          <div className="flex items-center space-x-2 mt-1">
                            {method.isDefault && (
                              <span className="text-sm bg-green-100 text-green-700 px-2 py-1 rounded-full font-medium">Default</span>
                            )}
                            {!method.isDefault && (
                              <button
                                onClick={() => handleSetDefaultPayment(method.id)}
                                className="text-sm text-blue-600 hover:text-blue-800 font-medium"
                              >
                                Set as Default
                              </button>
                            )}
                            <span className="text-xs text-gray-500">
                              {method.type === 'credit_card' ? 'Credit Card' : 
                               method.type === 'debit_card' ? 'Debit Card' : 
                               'PayPal'}
                            </span>
                          </div>
                        </div>
                      </div>
                      <div className="flex items-center space-x-2">
                        <button 
                          onClick={() => handleRemovePaymentMethod(method.id)}
                          className="text-red-600 hover:text-red-800 transition-colors p-2 rounded-lg hover:bg-red-50"
                          title="Remove payment method"
                        >
                          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                          </svg>
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Upgrade Modal */}
      {showUpgradeModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-2xl p-8 max-w-md w-full mx-4">
            <h3 className="text-2xl font-bold text-gray-900 mb-4">Confirm Plan Upgrade</h3>
            <p className="text-gray-600 mb-6">
              Are you sure you want to select the {selectedPlan?.name} plan for ₱{parseFloat(selectedPlan?.monthly_price || 0).toLocaleString()}/month?
            </p>
            <div className={`border rounded-lg p-4 mb-4 ${
              paymentMethods.length > 0 
                ? 'bg-green-50 border-green-200' 
                : 'bg-blue-50 border-blue-200'
            }`}>
              <p className={`text-sm ${
                paymentMethods.length > 0 
                  ? 'text-green-800' 
                  : 'text-blue-800'
              }`}>
                {paymentMethods.length > 0 
                  ? `✓ This will create a subscription and generate a billing entry. You can pay using your saved payment method.`
                  : 'This will create a subscription and generate a billing entry. You can add a payment method and pay later.'
                }
              </p>
            </div>
            <div className="flex space-x-4">
              <button
                onClick={() => setShowUpgradeModal(false)}
                disabled={processingPayment}
                className="flex-1 px-6 py-3 border border-gray-300 rounded-xl text-gray-700 hover:bg-gray-50 transition-colors font-semibold disabled:opacity-50"
              >
                Cancel
              </button>
              <button
                onClick={confirmUpgrade}
                disabled={processingPayment}
                className="flex-1 px-6 py-3 bg-black text-white rounded-xl hover:bg-gray-800 transition-colors font-semibold disabled:opacity-50 flex items-center justify-center"
              >
                {processingPayment ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                    Processing...
                  </>
                ) : (
                  'Select Plan'
                )}
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Add Payment Method Modal */}
      {showAddPaymentModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-2xl p-8 max-w-md w-full mx-4">
            <h3 className="text-2xl font-bold text-gray-900 mb-4">Add Payment Method</h3>

            {error && (
              <div className="mb-4 bg-red-50 border border-red-200 text-red-600 px-4 py-3 rounded-lg">
                {error}
              </div>
            )}

            <form onSubmit={(e) => { e.preventDefault(); handleAddPaymentMethod(); }} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Payment Method Type</label>
                <select
                  name="type"
                  value={paymentForm.payment_method}
                  onChange={handlePaymentInputChange}
                  className="w-full border-2 border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:border-black"
                >
                  <option value="credit_card">Credit Card</option>
                  <option value="debit_card">Debit Card</option>
                  <option value="paypal">PayPal</option>
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Cardholder Name</label>
                <input
                  type="text"
                  name="cardholder_name"
                  value={paymentForm.cardholder_name}
                  onChange={handlePaymentInputChange}
                  className="w-full border-2 border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:border-black"
                  placeholder="John Doe"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Card Number</label>
                <input
                  type="text"
                  name="card_number"
                  value={paymentForm.card_number}
                  onChange={handlePaymentInputChange}
                  className="w-full border-2 border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:border-black"
                  placeholder="1234 5678 9012 3456"
                  maxLength="19"
                  required
                />
              </div>

              <div className="grid grid-cols-3 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Month</label>
                  <select
                    name="expiry_month"
                    value={paymentForm.expiry_month}
                    onChange={handlePaymentInputChange}
                    className="w-full border-2 border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:border-black"
                    required
                  >
                    <option value="">MM</option>
                    {Array.from({ length: 12 }, (_, i) => (
                      <option key={i + 1} value={String(i + 1).padStart(2, '0')}>
                        {String(i + 1).padStart(2, '0')}
                      </option>
                    ))}
                  </select>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Year</label>
                  <select
                    name="expiry_year"
                    value={paymentForm.expiry_year}
                    onChange={handlePaymentInputChange}
                    className="w-full border-2 border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:border-black"
                    required
                  >
                    <option value="">YY</option>
                    {Array.from({ length: 10 }, (_, i) => (
                      <option key={i} value={String(new Date().getFullYear() + i).slice(-2)}>
                        {String(new Date().getFullYear() + i).slice(-2)}
                      </option>
                    ))}
                  </select>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">CVV</label>
                  <input
                    type="text"
                    name="cvv"
                    value={paymentForm.cvv}
                    onChange={handlePaymentInputChange}
                    className="w-full border-2 border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:border-black"
                    placeholder="123"
                    maxLength="4"
                    required
                  />
                </div>
              </div>

              <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                <p className="text-blue-800 text-sm">
                  <strong>Secure:</strong> Payment methods are encrypted and stored securely. This is a demo implementation.
                </p>
              </div>

              <div className="flex space-x-4 mt-6">
                <button
                  type="button"
                  onClick={() => { setShowAddPaymentModal(false); setError(null); }}
                  disabled={processingPayment}
                  className="flex-1 px-6 py-3 border border-gray-300 rounded-xl text-gray-700 hover:bg-gray-50 transition-colors font-semibold disabled:opacity-50"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  disabled={processingPayment}
                  className="flex-1 px-6 py-3 bg-black text-white rounded-xl hover:bg-gray-800 transition-colors font-semibold disabled:opacity-50 flex items-center justify-center"
                >
                  {processingPayment ? (
                    <>
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                      Adding...
                    </>
                  ) : (
                    'Add Payment Method'
                  )}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Payment Processing Modal */}
      {showPaymentModal && selectedBilling && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-2xl p-8 max-w-md w-full mx-4">
            <h3 className="text-2xl font-bold text-gray-900 mb-4">Process Payment</h3>
            <div className="mb-6">
              <div className="bg-gray-50 rounded-lg p-4">
                <p className="text-sm text-gray-600">Invoice: {selectedBilling.invoice_number}</p>
                <p className="text-sm text-gray-600">Plan: {selectedBilling.plan_name}</p>
                <p className="text-lg font-bold text-gray-900">Amount: ₱{selectedBilling.amount.toLocaleString()}</p>
              </div>
            </div>

            {error && (
              <div className="mb-4 bg-red-50 border border-red-200 text-red-600 px-4 py-3 rounded-lg">
                {error}
              </div>
            )}

            <form onSubmit={(e) => { e.preventDefault(); processPayment(); }} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Cardholder Name</label>
                <input
                  type="text"
                  name="cardholder_name"
                  value={paymentForm.cardholder_name}
                  onChange={handlePaymentInputChange}
                  className="w-full border-2 border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:border-black"
                  placeholder="John Doe"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Card Number</label>
                <input
                  type="text"
                  name="card_number"
                  value={paymentForm.card_number}
                  onChange={handlePaymentInputChange}
                  className="w-full border-2 border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:border-black"
                  placeholder="1234 5678 9012 3456"
                  maxLength="19"
                  required
                />
              </div>

              <div className="grid grid-cols-3 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Month</label>
                  <select
                    name="expiry_month"
                    value={paymentForm.expiry_month}
                    onChange={handlePaymentInputChange}
                    className="w-full border-2 border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:border-black"
                    required
                  >
                    <option value="">MM</option>
                    {Array.from({ length: 12 }, (_, i) => (
                      <option key={i + 1} value={String(i + 1).padStart(2, '0')}>
                        {String(i + 1).padStart(2, '0')}
                      </option>
                    ))}
                  </select>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Year</label>
                  <select
                    name="expiry_year"
                    value={paymentForm.expiry_year}
                    onChange={handlePaymentInputChange}
                    className="w-full border-2 border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:border-black"
                    required
                  >
                    <option value="">YY</option>
                    {Array.from({ length: 10 }, (_, i) => (
                      <option key={i} value={String(new Date().getFullYear() + i).slice(-2)}>
                        {String(new Date().getFullYear() + i).slice(-2)}
                      </option>
                    ))}
                  </select>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">CVV</label>
                  <input
                    type="text"
                    name="cvv"
                    value={paymentForm.cvv}
                    onChange={handlePaymentInputChange}
                    className="w-full border-2 border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:border-black"
                    placeholder="123"
                    maxLength="4"
                    required
                  />
                </div>
              </div>

              <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mt-4">
                <p className="text-blue-800 text-sm">
                  <strong>Demo Mode:</strong> This is a simulated payment system. Use any card details for testing. 
                  90% success rate simulation.
                </p>
              </div>

              <div className="flex space-x-4 mt-6">
                <button
                  type="button"
                  onClick={() => { setShowPaymentModal(false); setSelectedBilling(null); setError(null); }}
                  disabled={processingPayment}
                  className="flex-1 px-6 py-3 border border-gray-300 rounded-xl text-gray-700 hover:bg-gray-50 transition-colors font-semibold disabled:opacity-50"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  disabled={processingPayment}
                  className="flex-1 px-6 py-3 bg-black text-white rounded-xl hover:bg-gray-800 transition-colors font-semibold disabled:opacity-50 flex items-center justify-center"
                >
                  {processingPayment ? (
                    <>
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                      Processing...
                    </>
                  ) : (
                    `Pay ₱${selectedBilling.amount.toLocaleString()}`
                  )}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default BillingPayment;
