import React, { useState, useEffect } from 'react';
import ApiService from '../../services/api';

const BillingPayment = () => {
  const [currentPlan, setCurrentPlan] = useState(null);
  const [billingHistory, setBillingHistory] = useState([]);
  const [paymentMethods, setPaymentMethods] = useState([]);
  const [subscriptionPlans, setSubscriptionPlans] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedPlan, setSelectedPlan] = useState(null);
  const [showUpgradeModal, setShowUpgradeModal] = useState(false);
  const [showAddPaymentModal, setShowAddPaymentModal] = useState(false);
  const [processingPayment, setProcessingPayment] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [error, setError] = useState(null);

  // Utility functions
  const isSubscriptionExpiring = (nextBilling) => {
    if (!nextBilling) return false;
    const now = new Date();
    const billingDate = new Date(nextBilling);
    const daysUntilExpiry = Math.ceil((billingDate - now) / (1000 * 60 * 60 * 24));
    return daysUntilExpiry <= 7 && daysUntilExpiry > 0;
  };

  const isSubscriptionExpired = (nextBilling, status) => {
    if (!nextBilling || status === 'cancelled') return true;
    const now = new Date();
    const billingDate = new Date(nextBilling);
    return billingDate < now;
  };

  const addNotification = (message, type = 'info') => {
    const id = Date.now();
    setNotifications(prev => [...prev, { id, message, type }]);
    setTimeout(() => {
      setNotifications(prev => prev.filter(n => n.id !== id));
    }, 5000);
  };

  useEffect(() => {
    fetchBillingData();
  }, []);

  const fetchBillingData = async () => {
    try {
      setLoading(true);
      setError(null);

      const [planRes, historyRes, methodsRes, plansRes] = await Promise.all([
        ApiService.getCurrentSubscription(),
        ApiService.getBillingHistory(),
        ApiService.getPaymentMethods(),
        ApiService.getSubscriptionPlans()
      ]);

      setCurrentPlan(planRes);
      setBillingHistory(historyRes);
      setPaymentMethods(methodsRes);
      setSubscriptionPlans(plansRes);

      // Check for expiration warnings
      if (planRes && planRes.nextBilling) {
        if (isSubscriptionExpired(planRes.nextBilling, planRes.status)) {
          addNotification('Your subscription has expired. Please renew to continue using our services.', 'error');
        } else if (isSubscriptionExpiring(planRes.nextBilling)) {
          const daysLeft = Math.ceil((new Date(planRes.nextBilling) - new Date()) / (1000 * 60 * 60 * 24));
          addNotification(`Your subscription expires in ${daysLeft} day${daysLeft > 1 ? 's' : ''}. Consider renewing soon.`, 'warning');
        }
      }
    } catch (error) {
      console.error('Error fetching billing data:', error);
      setError('Failed to load billing information. Please try again.');
      addNotification('Failed to load billing information', 'error');
    } finally {
      setLoading(false);
    }
  };

  const handlePlanUpgrade = (plan) => {
    setSelectedPlan(plan);
    setShowUpgradeModal(true);
  };

  const confirmUpgrade = async () => {
    try {
      setProcessingPayment(true);

      // Check if user has a default payment method
      const defaultPaymentMethod = paymentMethods.find(pm => pm.isDefault);
      if (!defaultPaymentMethod) {
        addNotification('Please add a payment method before upgrading', 'error');
        setShowAddPaymentModal(true);
        return;
      }

      await ApiService.upgradePlan(selectedPlan.id, defaultPaymentMethod.id);

      // Refresh billing data
      await fetchBillingData();

      setShowUpgradeModal(false);
      setSelectedPlan(null);
      addNotification(`Successfully upgraded to ${selectedPlan.name} plan!`, 'success');
    } catch (error) {
      console.error('Error upgrading plan:', error);
      addNotification(error.message || 'Failed to upgrade plan. Please try again.', 'error');
    } finally {
      setProcessingPayment(false);
    }
  };

  const handleAddPaymentMethod = async (paymentData) => {
    try {
      setProcessingPayment(true);
      await ApiService.addPaymentMethod(paymentData);
      await fetchBillingData();
      setShowAddPaymentModal(false);
      addNotification('Payment method added successfully!', 'success');
    } catch (error) {
      console.error('Error adding payment method:', error);
      addNotification(error.message || 'Failed to add payment method', 'error');
    } finally {
      setProcessingPayment(false);
    }
  };

  const handleRemovePaymentMethod = async (paymentMethodId) => {
    if (!window.confirm('Are you sure you want to remove this payment method?')) return;

    try {
      await ApiService.removePaymentMethod(paymentMethodId);
      await fetchBillingData();
      addNotification('Payment method removed successfully!', 'success');
    } catch (error) {
      console.error('Error removing payment method:', error);
      addNotification(error.message || 'Failed to remove payment method', 'error');
    }
  };

  const handleSetDefaultPayment = async (paymentMethodId) => {
    try {
      await ApiService.setDefaultPaymentMethod(paymentMethodId);
      await fetchBillingData();
      addNotification('Default payment method updated!', 'success');
    } catch (error) {
      console.error('Error setting default payment method:', error);
      addNotification(error.message || 'Failed to update default payment method', 'error');
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-white flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-black mx-auto mb-4"></div>
          <p className="text-gray-600 font-medium">Loading billing information...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-white">
      {/* Header */}
      <div className="bg-gradient-to-r from-gray-900 to-black text-white rounded-2xl">
        <div className="max-w-7xl mx-auto px-6 py-12">
          <div className="text-center">
            <h1 className="text-4xl md:text-5xl font-bold mb-4">Billing & Payments</h1>
            <p className="text-gray-300 text-lg max-w-2xl mx-auto">
              Manage your subscription plan, billing history, and payment methods
            </p>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-6 py-8">
        {/* Current Plan */}
        <div className="bg-white rounded-2xl shadow-lg border border-gray-100 mb-8">
          <div className="px-8 py-6 border-b border-gray-100">
            <h2 className="text-2xl font-bold text-gray-900">Current Subscription</h2>
            <p className="text-gray-600 mt-1">Your active plan and billing information</p>
          </div>
          <div className="p-8">
            <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between">
              <div className="flex items-center space-x-6 mb-6 lg:mb-0">
                <div className="w-16 h-16 bg-gray-900 rounded-2xl flex items-center justify-center">
                  <svg className="w-8 h-8 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 3v4M3 5h4M6 17v4m-2-2h4m5-16l2.286 6.857L21 12l-5.714 2.143L13 21l-2.286-6.857L5 12l5.714-2.143L13 3z" />
                  </svg>
                </div>
                <div>
                  <h3 className="text-2xl font-bold text-gray-900">{currentPlan?.plan?.name} Plan</h3>
                  <p className="text-gray-600 text-lg">â‚±{currentPlan?.plan?.price?.toLocaleString()}/month</p>
                  <p className="text-sm text-gray-500 mt-1">
                    Next billing: {new Date(currentPlan?.nextBilling).toLocaleDateString()}
                  </p>
                </div>
              </div>
              <div className="flex flex-col sm:flex-row gap-4">
                <span className={`px-4 py-2 rounded-xl text-sm font-semibold ${
                  currentPlan?.status === 'active' 
                    ? 'bg-gray-100 text-gray-800' 
                    : 'bg-gray-100 text-gray-500'
                }`}>
                  {currentPlan?.status?.toUpperCase()}
                </span>
                <button className="bg-black text-white px-6 py-2 rounded-xl hover:bg-gray-800 transition-colors font-semibold">
                  Manage Plan
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Subscription Plans */}
        <div className="bg-white rounded-2xl shadow-lg border border-gray-100 mb-8">
          <div className="px-8 py-6 border-b border-gray-100">
            <h2 className="text-2xl font-bold text-gray-900">Subscription Plans</h2>
            <p className="text-gray-600 mt-1">Choose the plan that best fits your needs</p>
          </div>
          <div className="p-8">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
              {subscriptionPlans.map((plan) => (
                <div
                  key={plan.id}
                  className={`relative rounded-2xl border-2 p-8 ${
                    plan.popular
                      ? 'border-black bg-gray-50'
                      : 'border-gray-200 bg-white hover:border-gray-300'
                  } transition-all`}
                >
                  {plan.popular && (
                    <div className="absolute -top-4 left-1/2 transform -translate-x-1/2">
                      <span className="bg-black text-white px-4 py-1 rounded-full text-sm font-semibold">
                        Most Popular
                      </span>
                    </div>
                  )}

                  <div className="text-center mb-8">
                    <h3 className="text-2xl font-bold text-gray-900 mb-2">{plan.name}</h3>
                    <div className="mb-4">
                      <span className="text-4xl font-bold text-gray-900">â‚±{plan.price.toLocaleString()}</span>
                      <span className="text-gray-600">/{plan.period}</span>
                    </div>
                  </div>

                  <ul className="space-y-4 mb-8">
                    {plan.features.map((feature, index) => (
                      <li key={index} className="flex items-start space-x-3">
                        <svg className="w-5 h-5 text-gray-900 mt-0.5 flex-shrink-0" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                        </svg>
                        <span className="text-gray-700">{feature}</span>
                      </li>
                    ))}
                  </ul>

                  <button
                    onClick={() => handlePlanUpgrade(plan)}
                    disabled={currentPlan?.plan?.id === plan.id}
                    className={`w-full py-3 px-6 rounded-xl font-semibold transition-colors ${
                      currentPlan?.plan?.id === plan.id
                        ? 'bg-gray-100 text-gray-500 cursor-not-allowed'
                        : plan.popular
                        ? 'bg-black text-white hover:bg-gray-800'
                        : 'bg-gray-900 text-white hover:bg-gray-700'
                    }`}
                  >
                    {currentPlan?.plan?.id === plan.id ? 'Current Plan' : 'Upgrade to ' + plan.name}
                  </button>
                </div>
              ))}
            </div>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Billing History */}
          <div className="bg-white rounded-2xl shadow-lg border border-gray-100">
            <div className="px-8 py-6 border-b border-gray-100">
              <h2 className="text-2xl font-bold text-gray-900">Billing History</h2>
              <p className="text-gray-600 mt-1">Your recent transactions</p>
            </div>
            <div className="p-8">
              {billingHistory.length === 0 ? (
                <div className="text-center py-8">
                  <div className="w-16 h-16 bg-gray-100 rounded-2xl flex items-center justify-center mx-auto mb-4">
                    <svg className="w-8 h-8 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                    </svg>
                  </div>
                  <p className="text-gray-600">No billing history available</p>
                </div>
              ) : (
                <div className="space-y-4">
                  {billingHistory.map((transaction) => (
                    <div key={transaction.id} className="flex items-center justify-between p-4 border border-gray-200 rounded-xl hover:bg-gray-50 transition-colors">
                      <div>
                        <p className="font-semibold text-gray-900">{transaction.plan} Plan</p>
                        <p className="text-sm text-gray-600">{new Date(transaction.date).toLocaleDateString()}</p>
                      </div>
                      <div className="text-right">
                        <p className="font-bold text-gray-900">â‚±{transaction.amount.toLocaleString()}</p>
                        <span className={`text-sm px-2 py-1 rounded-full ${
                          transaction.status === 'paid' 
                            ? 'bg-gray-100 text-gray-800' 
                            : 'bg-gray-100 text-gray-500'
                        }`}>
                          {transaction.status.toUpperCase()}
                        </span>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>

          {/* Payment Methods */}
          <div className="bg-white rounded-2xl shadow-lg border border-gray-100">
            <div className="px-8 py-6 border-b border-gray-100">
              <div className="flex items-center justify-between">
                <div>
                  <h2 className="text-2xl font-bold text-gray-900">Payment Methods</h2>
                  <p className="text-gray-600 mt-1">Manage your payment options</p>
                </div>
                <button className="bg-black text-white px-4 py-2 rounded-xl hover:bg-gray-800 transition-colors font-semibold">
                  Add New
                </button>
              </div>
            </div>
            <div className="p-8">
              {paymentMethods.length === 0 ? (
                <div className="text-center py-8">
                  <div className="w-16 h-16 bg-gray-100 rounded-2xl flex items-center justify-center mx-auto mb-4">
                    <svg className="w-8 h-8 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 10h18M7 15h1m4 0h1m-7 4h12a3 3 0 003-3V8a3 3 0 00-3-3H6a3 3 0 00-3 3v8a3 3 0 003 3z" />
                    </svg>
                  </div>
                  <p className="text-gray-600">No payment methods added</p>
                </div>
              ) : (
                <div className="space-y-4">
                  {paymentMethods.map((method) => (
                    <div key={method.id} className="flex items-center justify-between p-4 border border-gray-200 rounded-xl">
                      <div className="flex items-center space-x-4">
                        <div className="w-12 h-8 bg-gray-900 rounded flex items-center justify-center">
                          <svg className="w-6 h-4 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 10h18M7 15h1m4 0h1m-7 4h12a3 3 0 003-3V8a3 3 0 00-3-3H6a3 3 0 00-3 3v8a3 3 0 003 3z" />
                          </svg>
                        </div>
                        <div>
                          <p className="font-semibold text-gray-900">{method.brand} â€¢â€¢â€¢â€¢ {method.last4}</p>
                          {method.isDefault && (
                            <span className="text-sm bg-gray-100 text-gray-700 px-2 py-1 rounded-full">Default</span>
                          )}
                        </div>
                      </div>
                      <button className="text-gray-600 hover:text-gray-900 transition-colors">
                        <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 5v.01M12 12v.01M12 19v.01M12 6a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2z" />
                        </svg>
                      </button>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Upgrade Modal */}
      {showUpgradeModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-2xl p-8 max-w-md w-full mx-4">
            <h3 className="text-2xl font-bold text-gray-900 mb-4">Confirm Plan Upgrade</h3>
            <p className="text-gray-600 mb-6">
              Are you sure you want to upgrade to the {selectedPlan?.name} plan for â‚±{selectedPlan?.price?.toLocaleString()}/month?
            </p>
            <div className="flex space-x-4">
              <button
                onClick={() => setShowUpgradeModal(false)}
                className="flex-1 px-6 py-3 border border-gray-300 rounded-xl text-gray-700 hover:bg-gray-50 transition-colors font-semibold"
              >
                Cancel
              </button>
              <button
                onClick={confirmUpgrade}
                className="flex-1 px-6 py-3 bg-black text-white rounded-xl hover:bg-gray-800 transition-colors font-semibold"
              >
                Confirm Upgrade
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default BillingPayment;
