import React, { useState, useEffect } from 'react';
import PropertyCard from './PropertyCard';
import UnitDetails from './UnitDetails';
import Inquiries from './Inquiries';
import Pagination from './Pagination';
import ApiService from '../../services/api';

const PropertyGrid = ({ filters = {} }) => {
  const [selectedProperty, setSelectedProperty] = useState(null);
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true); // retained for future spinner, but not used
  const [page, setPage] = useState(1);
  const [perPage] = useState(9);
  const [pagination, setPagination] = useState({ page: 1, per_page: 9, total_pages: 1, total_items: 0 });
  const [showInquiries, setShowInquiries] = useState(false);
  const [inquiryContext, setInquiryContext] = useState(null);

  // Fetch properties from API
  useEffect(() => {
    const fetchProperties = async () => {
      try {
        // Use tenant-accessible endpoint to get active properties
        const response = await ApiService.getActiveProperties();
        const properties = Array.isArray(response?.properties) ? response.properties : [];
        
        // Apply filters on the client side (for tenant properties)
        let filteredProperties = properties;
        
        const term = (filters.city || '').toLowerCase().trim();
        if (term) {
          filteredProperties = filteredProperties.filter(prop => (prop.city || '').toLowerCase().includes(term));
        }
        if (filters.type) {
          filteredProperties = filteredProperties.filter(prop => String(prop.property_type || '').toLowerCase() === String(filters.type).toLowerCase());
        }
        if (filters.bedrooms) {
          const minBeds = parseInt(filters.bedrooms, 10) || 0;
          filteredProperties = filteredProperties.filter(prop => Number(prop.bedrooms) >= minBeds);
        }
        if (filters.min_price) {
          const min = Number(filters.min_price);
          if (!Number.isNaN(min)) filteredProperties = filteredProperties.filter(prop => Number(prop.monthly_rent) >= min);
        }
        if (filters.max_price) {
          const max = Number(filters.max_price);
          if (!Number.isNaN(max)) filteredProperties = filteredProperties.filter(prop => Number(prop.monthly_rent) <= max);
        }

        const cards = filteredProperties.map((property) => {
          const images = Array.isArray(property.images) ? property.images : [];
          return {
            id: `prop-${property.id}`,
            name: property.title,
            images,
            location: `${property.city}, ${property.province || ''}`,
            price: `₱${Number(property.monthly_rent || 0).toLocaleString()}/month`,
            imageAlt: `${property.title} - Property image`,
            // Basic unit information
            bedrooms: property.bedrooms,
            bathrooms: property.bathrooms,
            kitchens: property.kitchens || 1,
            area: property.floor_area,
            description: property.description,
            propertyType: property.property_type,
            status: property.status,
            // Owner/Manager information
            company: property.owner?.company || '',
            propertyManager: property.owner ? `${property.owner.first_name} ${property.owner.last_name}` : '',
            managerId: property.owner?.id || null,
            owner: property.owner || {},
            // Contact information
            contactEmail: property.contact_info?.email || property.owner?.email || '',
            contactPhone: property.contact_info?.phone || property.owner?.phone || '',
            contactPerson: property.contact_info?.contact_person || '',
            // Address details
            address: property.property_info?.address || '',
            buildingName: property.property_info?.building_name || '',
            city: property.city,
            province: property.province,
            // Unit details
            unitDetails: property.unit_details || {},
            amenities: property.unitDetails?.amenities || {},
            furnished: property.unitDetails?.furnished || false,
            parkingSpaces: property.unitDetails?.parking_spaces || 0,
            floorNumber: property.unitDetails?.floor_number || '',
            unitStatus: property.unitDetails?.unit_status || property.status,
            unitDescription: property.unitDetails?.unit_description || property.description,
            // Financial information
            monthlyRent: property.financial_info?.monthly_rent || property.monthly_rent,
            securityDeposit: property.financial_info?.security_deposit || property.unitDetails?.security_deposit || 0,
            advancePayment: property.financial_info?.advance_payment || 0,
            utilityBillsIncluded: property.financial_info?.utility_bills_included || false,
            // Property information
            propertyInfo: property.property_info || {},
            propertyTitle: property.propertyInfo?.property_title || property.title,
            propertyDescription: property.propertyInfo?.property_description || property.description,
            totalUnits: property.propertyInfo?.total_units || 0,
            propertyFloorArea: property.propertyInfo?.property_floor_area || property.floor_area,
            lotArea: property.propertyInfo?.lot_area || 0,
            floorLevel: property.propertyInfo?.floor_level || 0,
            totalFloors: property.propertyInfo?.total_floors || 0,
            furnishing: property.propertyInfo?.furnishing || 'unfurnished',
            propertyAmenities: property.propertyInfo?.amenities || [],
            // Company information
            companyInfo: property.company_info || {},
            companyName: property.companyInfo?.name || property.owner?.company || '',
            companyDescription: property.companyInfo?.description || '',
            companyWebsite: property.companyInfo?.website || '',
            companyLogo: property.companyInfo?.logo || '',
            // Availability information
            availabilityInfo: property.availability_info || {},
            availableFrom: property.availabilityInfo?.available_from || '',
            minimumLeaseTerm: property.availabilityInfo?.minimum_lease_term || 12,
            // Reviews and ratings (placeholder for future implementation)
            rating: property.rating || 0,
            reviewCount: property.reviewCount || 0,
            reviews: property.reviews || [],
            landmarks: property.landmarks || [],
            // Timestamps
            createdAt: property.unitDetails?.created_at || '',
            updatedAt: property.unitDetails?.updated_at || ''
          };
        });

        setProperties(cards);
        setPagination({ page: 1, per_page: perPage, total_pages: 1, total_items: cards.length });
      } catch (error) {
        console.error('Error fetching properties:', error);
        setProperties([]);
      } finally {
        setLoading(false);
      }
    };

    fetchProperties();
  }, [filters, page, perPage]);


  const handleCardClick = (property) => {
    setSelectedProperty(property);
  };

  const handleCloseModal = () => {
    setSelectedProperty(null);
  };

  const handleInquireNow = (unit) => {
    // unit is the selectedProperty card; map needed fields
    setInquiryContext({
      managerName: unit.propertyManager || 'Property Manager',
      property: unit.name,
      unitId: unit.id,
      managerId: unit.managerId || null
    });
    setShowInquiries(true);
  };

  return (
    <>
      <div className="mb-8">
        <h2 className="text-xl md:text-2xl font-bold text-black mb-6">RENT SPACE</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 md:gap-6">
          {properties.map((property) => (
            <PropertyCard
              key={property.id}
              images={property.images}
              location={property.location}
              price={property.price}
              imageAlt={property.imageAlt}
              onCardClick={() => handleCardClick(property)}
            />
          ))}
        </div>
      </div>

      <Pagination 
        currentPage={pagination.page || page}
        totalPages={pagination.total_pages || 1}
        onPageChange={(p) => setPage(p)}
      />

      {/* Unit Details Modal */}
      {selectedProperty && (
        <UnitDetails 
          property={selectedProperty} 
          onClose={handleCloseModal}
          onInquireNow={() => handleInquireNow(selectedProperty)}
        />
      )}

      {showInquiries && (
        <Inquiries onClose={() => setShowInquiries(false)} initialChat={inquiryContext} />
      )}
    </>
  );
};

export default PropertyGrid;
