import React, { useState } from 'react';
import defaultUnit from '../../assets/images/default_unit.png';

const UnitDetails = ({ property, onClose, onInquireNow = () => {} }) => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [showRatingModal, setShowRatingModal] = useState(false);
  const [newRating, setNewRating] = useState(0);
  const [newComment, setNewComment] = useState('');
  const [reviewerName, setReviewerName] = useState('');

  if (!property) return null;

  // Normalize images and provide a reliable placeholder
  const images = Array.isArray(property.images) && property.images.length > 0
    ? property.images.filter(Boolean)
    : [defaultUnit];

  const nextImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === images.length - 1 ? 0 : prevIndex + 1
    );
  };

  const prevImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  const goToImage = (index) => {
    setCurrentImageIndex(index);
  };

  const handleInquiry = () => {
    onInquireNow();
  };

  const handleSave = () => {
    // TODO: Implement save to favorites logic
    alert('Property saved to your favorites!');
  };

  const handleSubmitRating = () => {
    if (newRating === 0) {
      alert('Please select a rating before submitting.');
      return;
    }
    if (!reviewerName.trim()) {
      alert('Please enter your name.');
      return;
    }
    if (!newComment.trim()) {
      alert('Please write a comment.');
      return;
    }

    // TODO: Implement API call to submit rating and comment
    const newReview = {
      id: Date.now(),
      reviewerName: reviewerName.trim(),
      rating: newRating,
      comment: newComment.trim(),
      date: new Date().toLocaleDateString()
    };

    // For now, just show success message
    alert('Thank you for your feedback! Your review has been submitted.');

    // Reset form
    setNewRating(0);
    setNewComment('');
    setReviewerName('');
    setShowRatingModal(false);
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'available': return 'bg-green-100 text-green-800';
      case 'occupied': return 'bg-red-100 text-red-800';
      case 'rented': return 'bg-blue-100 text-blue-800';
      case 'reserved': return 'bg-yellow-100 text-yellow-800';
      case 'maintenance': return 'bg-orange-100 text-orange-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getAmenityIcon = (amenity) => {
    switch (amenity) {
      case 'balcony': return 'ðŸ ';
      case 'air_conditioning': return 'â„ï¸';
      case 'wifi': return 'ðŸ“¶';
      case 'security': return 'ðŸ”’';
      case 'furnished': return 'ðŸ›‹ï¸';
      case 'parking': return 'ðŸš—';
      default: return 'âœ“';
    }
  };

  const getAmenityLabel = (amenity) => {
    switch (amenity) {
      case 'air_conditioning': return 'Air Conditioning';
      case 'balcony': return 'Balcony';
      case 'wifi': return 'WiFi';
      case 'security': return 'Security';
      case 'furnished': return 'Furnished';
      case 'parking': return 'Parking';
      default: return amenity.charAt(0).toUpperCase() + amenity.slice(1);
    }
  };

  return (
    <>
    <div className="fixed inset-0 bg-gray-900 bg-opacity-75 flex items-center justify-center p-3 z-50">
        <div className="bg-white rounded-xl w-full max-w-4xl max-h-[90vh] shadow-2xl overflow-hidden">
        {/* Header */}
        <div className="flex items-center justify-between p-4 border-b border-gray-200 bg-gradient-to-r from-gray-50 to-white">
          <div className="flex items-center space-x-3">
            <button
              onClick={onClose}
              className="w-9 h-9 bg-black rounded-full flex items-center justify-center hover:bg-gray-800 transition-all duration-300 shadow-lg"
            >
              <svg className="w-4 h-4 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
              </svg>
            </button>
            <div>
              <h1 className="text-lg font-black text-black">Unit Details</h1>
              <p className="text-xs text-gray-500">Property Information</p>
            </div>
          </div>
        </div>

        {/* Scrollable Content */}
          <div className="overflow-y-auto max-h-[calc(90vh-56px-72px)]">
          {/* Image Carousel */}
          <div className="p-4">
            <div className="relative h-60 md:h-96 bg-gray-200 flex items-center justify-center">
              <img
                  src={images[currentImageIndex] || defaultUnit}
                  alt={`${property.imageAlt || 'Unit'} - Image ${currentImageIndex + 1}`}
                className="w-full h-full object-cover rounded-lg"
                  onError={(e) => { if (e.currentTarget.src !== defaultUnit) e.currentTarget.src = defaultUnit; }}
              />

                {images.length > 1 && (
                <>
                  <button
                    onClick={prevImage}
                    className="absolute left-2 top-1/2 -translate-y-1/2 bg-black bg-opacity-60 text-white p-1.5 rounded-full hover:bg-opacity-80 transition-all"
                  >
                    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                    </svg>
                  </button>
                  <button
                    onClick={nextImage}
                    className="absolute right-2 top-1/2 -translate-y-1/2 bg-black bg-opacity-60 text-white p-1.5 rounded-full hover:bg-opacity-80 transition-all"
                  >
                    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                    </svg>
                  </button>

                  <div className="absolute top-2 left-2 bg-black bg-opacity-70 text-white text-[10px] px-2 py-0.5 rounded-md">
                      {currentImageIndex + 1} / {images.length}
                  </div>

                  <div className="absolute bottom-2 left-1/2 -translate-x-1/2 flex space-x-1.5">
                      {images.map((_, index) => (
                      <button
                        key={index}
                        onClick={() => goToImage(index)}
                        className={`w-1.5 h-1.5 rounded-full transition-all ${
                          index === currentImageIndex ? 'bg-white scale-150' : 'bg-white/60 hover:bg-white'
                        }`}
                      />
                    ))}
                  </div>
                </>
              )}
            </div>
          </div>

          {/* Title / Location / Price */}
          <div className="px-4 pb-4">
            <h2 className="text-2xl font-black text-black uppercase tracking-wide mb-2">
              {property.name || 'Property'}
            </h2>
            <div className="flex items-center justify-between">
              <div className="flex items-center">
                <div className="w-5 h-5 bg-black rounded-full flex items-center justify-center mr-2">
                  <svg className="w-3 h-3 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                  </svg>
                </div>
                <span className="text-sm text-gray-600 font-medium">{property.location}</span>
              </div>
              <div className="text-right">
                <span className="text-xl font-black text-black">{property.price}</span>
                <p className="text-[11px] text-gray-500">per month</p>
              </div>
            </div>
          </div>

          {/* Features */}
          <div className="px-4 pb-4">
            <div className="grid grid-cols-2 sm:grid-cols-4 gap-3">
              <div className="bg-gray-50 rounded-lg p-3 text-center border border-gray-200">
                <div className="text-2xl mb-1">ðŸ›ï¸</div>
                <div className="text-xs font-bold text-gray-800">{property.bedrooms || 'N/A'} bed{property.bedrooms !== 1 ? 's' : ''}</div>
              </div>
              <div className="bg-gray-50 rounded-lg p-3 text-center border border-gray-200">
                <div className="text-2xl mb-1">ðŸ›</div>
                <div className="text-xs font-bold text-gray-800">{property.bathrooms || 'N/A'} bathroom{property.bathrooms !== 1 ? 's' : ''}</div>
              </div>
              <div className="bg-gray-50 rounded-lg p-3 text-center border border-gray-200">
                  <div className="text-2xl mb-1">ðŸš—</div>
                  <div className="text-xs font-bold text-gray-800">{property.parkingSpaces || 0} parking</div>
              </div>
              <div className="bg-gray-50 rounded-lg p-3 text-center border border-gray-200">
                <div className="text-2xl mb-1">ðŸ“</div>
                <div className="text-xs font-bold text-gray-800">{property.area || 'N/A'}mÂ²</div>
              </div>
            </div>
          </div>

            {/* Financial Information */}
            <div className="px-4 pb-4">
              <h3 className="text-base font-black text-black mb-2 flex items-center">
                <span className="w-1 h-4 bg-black rounded-full mr-2"></span>
                Financial Information
              </h3>
              <div className="bg-white rounded-lg p-4 border border-gray-200 shadow-sm">
                <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                  <div className="flex items-center justify-between">
                    <span className="text-sm text-gray-600">Monthly Rent</span>
                    <span className="text-lg font-bold text-black">â‚±{Number(property.monthlyRent || 0).toLocaleString()}</span>
                  </div>
                  <div className="flex items-center justify-between">
                    <span className="text-sm text-gray-600">Security Deposit</span>
                    <span className="text-lg font-bold text-black">â‚±{Number(property.securityDeposit || 0).toLocaleString()}</span>
                  </div>
                  <div className="flex items-center justify-between">
                    <span className="text-sm text-gray-600">Floor Number</span>
                    <span className="text-sm font-semibold text-gray-800">{property.floorNumber || 'Not specified'}</span>
                  </div>
                  <div className="flex items-center justify-between">
                    <span className="text-sm text-gray-600">Status</span>
                    <span className={`px-2 py-1 rounded-full text-xs font-semibold ${getStatusColor(property.unitStatus || property.status)}`}>
                      {(property.unitStatus || property.status)?.charAt(0).toUpperCase() + (property.unitStatus || property.status)?.slice(1) || 'Available'}
                    </span>
                  </div>
                </div>
              </div>
            </div>

            {/* Unit Amenities */}
            <div className="px-4 pb-4">
              <h3 className="text-base font-black text-black mb-2 flex items-center">
                <span className="w-1 h-4 bg-black rounded-full mr-2"></span>
                Unit Amenities
              </h3>
              <div className="bg-white rounded-lg p-4 border border-gray-200 shadow-sm">
                {(() => {
                  // Get amenities from unit_details or directly from property
                  const unitAmenities = property.unitDetails?.amenities || property.amenities || {};
                  const selectedAmenities = Object.entries(unitAmenities).filter(([_, available]) => available);

                  if (selectedAmenities.length > 0 || property.furnished || property.parkingSpaces > 0) {
                    return (
                      <div className="grid grid-cols-2 sm:grid-cols-3 gap-3">
                        {selectedAmenities.map(([amenity, _]) => (
                          <div key={amenity} className="flex items-center space-x-2 p-2 rounded-lg bg-green-50 border border-green-200">
                            <span className="text-lg">{getAmenityIcon(amenity)}</span>
                            <span className="text-sm font-medium text-green-800">
                              {getAmenityLabel(amenity)}
                            </span>
                          </div>
                        ))}
                        {property.furnished && (
                          <div className="flex items-center space-x-2 p-2 rounded-lg bg-green-50 border border-green-200">
                            <span className="text-lg">ðŸ›‹ï¸</span>
                            <span className="text-sm font-medium text-green-800">Furnished</span>
                          </div>
                        )}
                        {property.parkingSpaces > 0 && (
                          <div className="flex items-center space-x-2 p-2 rounded-lg bg-green-50 border border-green-200">
                            <span className="text-lg">ðŸš—</span>
                            <span className="text-sm font-medium text-green-800">Parking ({property.parkingSpaces})</span>
                          </div>
                        )}
                      </div>
                    );
                  } else {
                    return (
                      <div className="text-center text-sm text-gray-500 py-4">
                        No unit amenities specified
                      </div>
                    );
                  }
                })()}
              </div>
            </div>

            {/* Property Amenities */}
            {property.propertyAmenities && property.propertyAmenities.length > 0 && (
              <div className="px-4 pb-4">
                <h3 className="text-base font-black text-black mb-2 flex items-center">
                  <span className="w-1 h-4 bg-black rounded-full mr-2"></span>
                  Property Amenities
                </h3>
                <div className="bg-white rounded-lg p-4 border border-gray-200 shadow-sm">
                  <div className="grid grid-cols-2 sm:grid-cols-3 gap-3">
                    {property.propertyAmenities.map((amenity, index) => (
                      <div key={index} className="flex items-center space-x-2 p-2 rounded-lg bg-blue-50 border border-blue-200">
                        <span className="text-lg">ðŸ¢</span>
                        <span className="text-sm font-medium text-blue-800">
                          {amenity}
                        </span>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            )}

            {/* Description */}
          <div className="px-4 pb-4">
            <h3 className="text-base font-black text-black mb-2 flex items-center">
              <span className="w-1 h-4 bg-black rounded-full mr-2"></span>
                Description
            </h3>
            <div className="bg-white rounded-lg p-4 border border-gray-200 shadow-sm">
              <p className="text-gray-700 leading-relaxed text-sm">
                  {property.description || `A comfortable ${property.bedrooms}-bedroom unit with ${property.bathrooms} bathroom${property.bathrooms !== 1 ? 's' : ''} spanning ${property.area} square meters. This well-designed space offers modern living with ${property.parkingSpaces || 0} parking space${property.parkingSpaces !== 1 ? 's' : ''} and is located on ${property.floorNumber ? `floor ${property.floorNumber}` : 'an unspecified floor'}. Perfect for ${property.bedrooms === 1 ? 'individuals or couples' : 'families or roommates'} seeking quality accommodation.`}
              </p>
            </div>
          </div>


            {/* Contact Information */}
          <div className="px-4 pb-4">
            <h3 className="text-base font-black text-black mb-2 flex items-center">
              <span className="w-1 h-4 bg-black rounded-full mr-2"></span>
                Contact Information
            </h3>
            <div className="bg-white rounded-lg p-4 border border-gray-200 shadow-sm">
              <div className="space-y-3">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center">
                      <div className="w-8 h-8 bg-purple-100 rounded-full flex items-center justify-center mr-3">
                        <svg className="w-4 h-4 text-purple-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 8l7.89 4.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
                        </svg>
                      </div>
                      <div>
                        <span className="text-xs text-gray-500">Contact Email</span>
                        <p className="text-sm font-semibold text-gray-800">{property.contactEmail || 'N/A'}</p>
                    </div>
                  </div>
                </div>
                  <div className="flex items-center justify-between">
                    <div className="flex items-center">
                      <div className="w-8 h-8 bg-orange-100 rounded-full flex items-center justify-center mr-3">
                        <svg className="w-4 h-4 text-orange-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 5a2 2 0 012-2h3.28a1 1 0 01.948.684l1.498 4.493a1 1 0 01-.502 1.21l-2.257 1.13a11.042 11.042 0 005.516 5.516l1.13-2.257a1 1 0 011.21-.502l4.493 1.498a1 1 0 01.684.949V19a2 2 0 01-2 2h-1C9.716 21 3 14.284 3 6V5z" />
                        </svg>
                      </div>
                      <div>
                        <span className="text-xs text-gray-500">Contact Phone</span>
                        <p className="text-sm font-semibold text-gray-800">{property.contactPhone || 'N/A'}</p>
                      </div>
                    </div>
                  </div>
                  {property.contactPerson && (
                <div className="flex items-center justify-between">
                  <div className="flex items-center">
                    <div className="w-8 h-8 bg-green-100 rounded-full flex items-center justify-center mr-3">
                      <svg className="w-4 h-4 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                      </svg>
                    </div>
                    <div>
                          <span className="text-xs text-gray-500">Contact Person</span>
                          <p className="text-sm font-semibold text-gray-800">{property.contactPerson}</p>
                        </div>
                      </div>
                    </div>
                  )}
                    </div>
                  </div>
            </div>

            {/* Property Rating */}
          <div className="px-4 pb-4">
            <h3 className="text-base font-black text-black mb-2 flex items-center">
              <span className="w-1 h-4 bg-black rounded-full mr-2"></span>
              Property Rating
            </h3>
            <div className="bg-white rounded-lg p-4 border border-gray-200 shadow-sm">
              <div className="flex items-center justify-between mb-3">
                <div className="flex items-center">
                  <div className="flex items-center mr-3">
                    {[1, 2, 3, 4, 5].map((star) => (
                      <svg
                        key={star}
                        className={`w-5 h-5 ${star <= (property.rating || 0) ? 'text-yellow-400' : 'text-gray-300'}`}
                        fill="currentColor"
                        viewBox="0 0 20 20"
                      >
                        <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
                      </svg>
                    ))}
                  </div>
                  <div>
                    <span className="text-lg font-bold text-gray-800">{property.rating || '0.0'}</span>
                    <span className="text-sm text-gray-500 ml-1">/ 5.0</span>
                  </div>
                </div>
                <div className="text-right">
                  <span className="text-sm text-gray-500">Based on</span>
                  <p className="text-sm font-semibold text-gray-800">{property.reviewCount || '0'} reviews</p>
                </div>
              </div>
                <button
                  onClick={() => setShowRatingModal(true)}
                  className="w-full bg-gray-100 hover:bg-gray-200 text-gray-700 py-2 px-4 rounded-lg text-sm font-medium transition-colors"
                >
                  Rate & Review This Property
                </button>
            </div>
          </div>

            {/* Recent Reviews */}
          <div className="px-4 pb-4">
            <h3 className="text-base font-black text-black mb-2 flex items-center">
              <span className="w-1 h-4 bg-black rounded-full mr-2"></span>
              Recent Reviews
            </h3>
            <div className="bg-white rounded-lg p-4 border border-gray-200 shadow-sm">
              <div className="space-y-4">
                {property.reviews && property.reviews.length > 0 ? (
                  property.reviews.slice(0, 3).map((review, index) => (
                    <div key={index} className="border-b border-gray-100 pb-3 last:border-b-0">
                      <div className="flex items-center justify-between mb-2">
                        <div className="flex items-center">
                          <div className="w-8 h-8 bg-gray-200 rounded-full flex items-center justify-center mr-3">
                            <span className="text-sm font-semibold text-gray-600">
                              {review.reviewerName ? review.reviewerName.charAt(0).toUpperCase() : 'A'}
                            </span>
                          </div>
                          <div>
                            <p className="text-sm font-semibold text-gray-800">
                              {review.reviewerName || `Anonymous User ${index + 1}`}
                            </p>
                            <div className="flex items-center">
                              {[1, 2, 3, 4, 5].map((star) => (
                                <svg
                                  key={star}
                                  className={`w-3 h-3 ${star <= review.rating ? 'text-yellow-400' : 'text-gray-300'}`}
                                  fill="currentColor"
                                  viewBox="0 0 20 20"
                                >
                                  <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
                                </svg>
                              ))}
                              <span className="text-xs text-gray-500 ml-2">{review.rating}/5</span>
                            </div>
                          </div>
                        </div>
                        <span className="text-xs text-gray-400">{review.date || 'N/A'}</span>
                      </div>
                      <p className="text-sm text-gray-700 leading-relaxed">
                        {review.comment || 'No comment available.'}
                      </p>
                    </div>
                  ))
                ) : (
                  <div className="text-center py-4">
                    <div className="w-12 h-12 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-3">
                      <svg className="w-6 h-6 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z" />
                      </svg>
                    </div>
                    <p className="text-sm text-gray-500">No reviews yet</p>
                    <p className="text-xs text-gray-400 mt-1">Be the first to review this property!</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>

        {/* Sticky Footer Actions */}
        <div className="px-6 py-2 border-t border-gray-200 bg-white">
          <div className="flex flex-col sm:flex-row gap-4">
              <button
                onClick={handleSave}
                className="w-full sm:flex-1 bg-gray-100 text-gray-700 py-3 rounded-lg font-black text-sm hover:bg-gray-200 transition-all shadow-md"
              >
                SAVE TO FAVORITES
              </button>
            <button
              onClick={handleInquiry}
              className="w-full sm:flex-1 bg-black text-white py-3 rounded-lg font-black text-sm hover:bg-gray-800 transition-all shadow-md"
            >
              INQUIRE NOW
            </button>
          </div>
        </div>
      </div>
    </div>

      {/* Rating Modal */}
      {showRatingModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-60 p-4">
          <div className="bg-white rounded-xl w-full max-w-md shadow-2xl">
            <div className="p-6">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-bold text-gray-900">Rate & Review Property</h3>
                <button
                  onClick={() => setShowRatingModal(false)}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                  </svg>
                </button>
              </div>

              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Your Rating</label>
                  <div className="flex space-x-1">
                    {[1, 2, 3, 4, 5].map((star) => (
                      <button
                        key={star}
                        onClick={() => setNewRating(star)}
                        className={`w-8 h-8 ${
                          star <= newRating ? 'text-yellow-400' : 'text-gray-300'
                        } hover:text-yellow-400 transition-colors`}
                      >
                        <svg className="w-full h-full" fill="currentColor" viewBox="0 0 20 20">
                          <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
                        </svg>
                      </button>
                    ))}
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Your Name</label>
                  <input
                    type="text"
                    value={reviewerName}
                    onChange={(e) => setReviewerName(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-black focus:border-transparent"
                    placeholder="Enter your name"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Your Review</label>
                  <textarea
                    value={newComment}
                    onChange={(e) => setNewComment(e.target.value)}
                    rows={4}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-black focus:border-transparent"
                    placeholder="Share your experience with this property..."
                  />
                </div>

                <div className="flex space-x-3 pt-4">
                  <button
                    onClick={() => setShowRatingModal(false)}
                    className="flex-1 bg-gray-100 text-gray-700 py-2 px-4 rounded-lg font-medium hover:bg-gray-200 transition-colors"
                  >
                    Cancel
                  </button>
                  <button
                    onClick={handleSubmitRating}
                    className="flex-1 bg-black text-white py-2 px-4 rounded-lg font-medium hover:bg-gray-800 transition-colors"
                  >
                    Submit Review
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default UnitDetails;
