import React, { useState, useEffect } from 'react';
import apiService from '../../services/api';

const DocumentManagement = () => {
  const [documents, setDocuments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedDocument, setSelectedDocument] = useState(null);
  const [filterStatus, setFilterStatus] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchDocuments();
  }, []);

  const fetchDocuments = async () => {
    try {
      setLoading(true);
      // Mock data for now - replace with actual API call
      const mockDocuments = [
        {
          id: 1,
          propertyId: 101,
          propertyTitle: 'Sunset Apartments',
          ownerName: 'John Smith',
          ownerEmail: 'john@example.com',
          documentType: 'Property Title',
          fileName: 'property_title_sunset_apt.pdf',
          fileSize: '2.5 MB',
          uploadDate: '2024-10-20',
          status: 'pending',
          filePath: '/documents/legal/property_title_sunset_apt.pdf'
        },
        {
          id: 2,
          propertyId: 102,
          propertyTitle: 'Green Valley Dormitory',
          ownerName: 'Maria Garcia',
          ownerEmail: 'maria@example.com',
          documentType: 'Business Permit',
          fileName: 'business_permit_green_valley.pdf',
          fileSize: '1.8 MB',
          uploadDate: '2024-10-19',
          status: 'approved',
          filePath: '/documents/legal/business_permit_green_valley.pdf'
        },
        {
          id: 3,
          propertyId: 103,
          propertyTitle: 'City Center Boarding House',
          ownerName: 'Robert Chen',
          ownerEmail: 'robert@example.com',
          documentType: 'Zoning Certificate',
          fileName: 'zoning_cert_city_center.pdf',
          fileSize: '3.2 MB',
          uploadDate: '2024-10-18',
          status: 'rejected',
          filePath: '/documents/legal/zoning_cert_city_center.pdf'
        }
      ];
      setDocuments(mockDocuments);
    } catch (error) {
      console.error('Error fetching documents:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleStatusUpdate = async (documentId, newStatus) => {
    try {
      // API call to update document status
      setDocuments(prev => 
        prev.map(doc => 
          doc.id === documentId 
            ? { ...doc, status: newStatus }
            : doc
        )
      );
      alert(`Document ${newStatus} successfully!`);
    } catch (error) {
      console.error('Error updating document status:', error);
      alert('Failed to update document status');
    }
  };

  const handleDownload = (document) => {
    // Simulate file download
    const link = document.createElement('a');
    link.href = document.filePath;
    link.download = document.fileName;
    link.click();
    alert(`Downloading ${document.fileName}...`);
  };

  const filteredDocuments = documents.filter(doc => {
    const matchesStatus = filterStatus === 'all' || doc.status === filterStatus;
    const matchesSearch = doc.propertyTitle.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         doc.ownerName.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         doc.documentType.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesStatus && matchesSearch;
  });

  const getStatusBadge = (status) => {
    const statusConfig = {
      pending: { bg: 'bg-yellow-100', text: 'text-yellow-800', label: 'Pending Review' },
      approved: { bg: 'bg-green-100', text: 'text-green-800', label: 'Approved' },
      rejected: { bg: 'bg-red-100', text: 'text-red-800', label: 'Rejected' }
    };
    const config = statusConfig[status] || statusConfig.pending;
    return (
      <span className={`px-3 py-1 rounded-full text-xs font-medium ${config.bg} ${config.text}`}>
        {config.label}
      </span>
    );
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-64">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-black mx-auto mb-4"></div>
          <p className="text-gray-800">Loading documents...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-white">
      {/* Header */}
      <div className="bg-gradient-to-r from-gray-900 to-black text-white rounded-2xl mb-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-6">
            <div>
              <h1 className="text-3xl font-bold text-white flex items-center">
                <span className="text-4xl mr-3">üìÅ</span>
                Document Management
              </h1>
              <p className="text-gray-300 mt-1">Review and manage legal documents from property owners</p>
            </div>
            <div className="flex items-center space-x-4">
              <div className="bg-white/10 px-4 py-2 rounded-lg">
                <span className="text-sm font-medium">Total Documents: {documents.length}</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Filters and Search */}
        <div className="bg-white rounded-xl shadow-sm border-2 border-black p-6 mb-8">
          <div className="flex flex-col md:flex-row md:items-center md:justify-between space-y-4 md:space-y-0">
            <div className="flex items-center space-x-4">
              <div>
                <label className="block text-sm font-bold text-black mb-2">Filter by Status</label>
                <select
                  value={filterStatus}
                  onChange={(e) => setFilterStatus(e.target.value)}
                  className="px-4 py-2 border-2 border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-black focus:border-transparent text-black"
                >
                  <option value="all">All Documents</option>
                  <option value="pending">Pending Review</option>
                  <option value="approved">Approved</option>
                  <option value="rejected">Rejected</option>
                </select>
              </div>
            </div>

            <div className="flex-1 max-w-md">
              <label className="block text-sm font-bold text-black mb-2">Search Documents</label>
              <input
                type="text"
                placeholder="Search by property, owner, or document type..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full px-4 py-2 border-2 border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-black focus:border-transparent text-black"
              />
            </div>
          </div>
        </div>

        {/* Documents Table */}
        <div className="bg-white rounded-xl shadow-sm border-2 border-black overflow-hidden">
          <div className="px-6 py-4 bg-gray-50 border-b-2 border-black">
            <h2 className="text-lg font-bold text-black">Legal Documents</h2>
          </div>

          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-bold text-black uppercase tracking-wider">Property</th>
                  <th className="px-6 py-3 text-left text-xs font-bold text-black uppercase tracking-wider">Owner</th>
                  <th className="px-6 py-3 text-left text-xs font-bold text-black uppercase tracking-wider">Document</th>
                  <th className="px-6 py-3 text-left text-xs font-bold text-black uppercase tracking-wider">Upload Date</th>
                  <th className="px-6 py-3 text-left text-xs font-bold text-black uppercase tracking-wider">Status</th>
                  <th className="px-6 py-3 text-left text-xs font-bold text-black uppercase tracking-wider">Actions</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {filteredDocuments.map((document) => (
                  <tr key={document.id} className="hover:bg-gray-50">
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div>
                        <div className="text-sm font-medium text-black">{document.propertyTitle}</div>
                        <div className="text-sm text-gray-500">ID: {document.propertyId}</div>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div>
                        <div className="text-sm font-medium text-black">{document.ownerName}</div>
                        <div className="text-sm text-gray-500">{document.ownerEmail}</div>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div>
                        <div className="text-sm font-medium text-black">{document.documentType}</div>
                        <div className="text-sm text-gray-500">{document.fileName}</div>
                        <div className="text-xs text-gray-400">{document.fileSize}</div>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-black">
                      {new Date(document.uploadDate).toLocaleDateString()}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      {getStatusBadge(document.status)}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                      <div className="flex items-center space-x-2">
                        <button
                          onClick={() => handleDownload(document)}
                          className="bg-black text-white px-3 py-1 rounded-lg hover:bg-gray-800 transition-colors text-xs"
                        >
                          üì• Download
                        </button>
                        {document.status === 'pending' && (
                          <>
                            <button
                              onClick={() => handleStatusUpdate(document.id, 'approved')}
                              className="bg-green-600 text-white px-3 py-1 rounded-lg hover:bg-green-700 transition-colors text-xs"
                            >
                              ‚úÖ Approve
                            </button>
                            <button
                              onClick={() => handleStatusUpdate(document.id, 'rejected')}
                              className="bg-red-600 text-white px-3 py-1 rounded-lg hover:bg-red-700 transition-colors text-xs"
                            >
                              ‚ùå Reject
                            </button>
                          </>
                        )}
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {filteredDocuments.length === 0 && (
            <div className="text-center py-12">
              <div className="text-6xl mb-4">üìÑ</div>
              <h3 className="text-lg font-medium text-black mb-2">No documents found</h3>
              <p className="text-gray-500">Try adjusting your search or filter criteria.</p>
            </div>
          )}
        </div>

        {/* Statistics Cards */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mt-8">
          <div className="bg-white rounded-xl shadow-sm border-2 border-black p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Pending Review</p>
                <p className="text-2xl font-bold text-yellow-600">
                  {documents.filter(d => d.status === 'pending').length}
                </p>
              </div>
              <div className="text-3xl">‚è≥</div>
            </div>
          </div>

          <div className="bg-white rounded-xl shadow-sm border-2 border-black p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Approved</p>
                <p className="text-2xl font-bold text-green-600">
                  {documents.filter(d => d.status === 'approved').length}
                </p>
              </div>
              <div className="text-3xl">‚úÖ</div>
            </div>
          </div>

          <div className="bg-white rounded-xl shadow-sm border-2 border-black p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Rejected</p>
                <p className="text-2xl font-bold text-red-600">
                  {documents.filter(d => d.status === 'rejected').length}
                </p>
              </div>
              <div className="text-3xl">‚ùå</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DocumentManagement;
