import React, { useEffect, useMemo, useRef, useState } from 'react';
import api from '../services/api';

// Props:
// - onClose(): close modal
// - initialChat: { id?, managerName, property, unitId, propertyId }
const Inquiries = ({ onClose, initialChat = null }) => {
  const [message, setMessage] = useState('');
  const [chats, setChats] = useState([]); // [{id, managerName, property, avatar, status, messages: [{id, sender, text, time}]}]
  const [selectedChatId, setSelectedChatId] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const hydratedRef = useRef(false);

  // Load inquiries from backend API
  const loadInquiries = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await api.getTenantInquiries();
      if (response && response.inquiries) {
        const formattedChats = response.inquiries.map(inquiry => ({
          id: inquiry.id,
          managerName: inquiry.property_manager?.first_name + ' ' + inquiry.property_manager?.last_name || 'Property Manager',
          property: inquiry.property?.building_name || inquiry.property?.title || 'Property',
          propertyId: inquiry.property_id,
          unitId: inquiry.property_id, // Using property_id as unitId for now
          avatar: 'ðŸ¢',
          status: inquiry.status,
          messages: [
            {
              id: 1,
              sender: 'tenant',
              text: inquiry.message,
              time: new Date(inquiry.created_at).toLocaleTimeString()
            },
            ...(inquiry.response_message ? [{
              id: 2,
              sender: 'manager',
              text: inquiry.response_message,
              time: new Date(inquiry.responded_at).toLocaleTimeString()
            }] : [])
          ],
          inquiry: inquiry
        }));
        setChats(formattedChats);
      }
    } catch (err) {
      console.error('Failed to load inquiries:', err);
      setError('Failed to load inquiries. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Load inquiries on mount
  useEffect(() => {
    loadInquiries();
    hydratedRef.current = true;
  }, []);

  // Persist chats and selection whenever they change
  useEffect(() => {
    if (!hydratedRef.current) return; // avoid wiping storage with initial empty state
    try {
      if (Array.isArray(chats)) localStorage.setItem('tenant_chats', JSON.stringify(chats));
      if (selectedChatId) {
        localStorage.setItem('tenant_selected_chat_id', selectedChatId);
      }
    } catch (_) {
      // storage may be unavailable
    }
  }, [chats, selectedChatId]);

  // Create new inquiry when opened from "INQUIRE NOW"
  useEffect(() => {
    if (!initialChat || !initialChat.propertyId) return;
    
    const createInquiry = async () => {
      try {
        setLoading(true);
        const response = await api.startTenantInquiry(
          initialChat.propertyId,
          `I'm interested in ${initialChat.property}. Please provide more information about availability and viewing options.`
        );
        
        if (response && response.inquiry) {
          const newChat = {
            id: response.inquiry.id,
            managerName: response.inquiry.property_manager?.first_name + ' ' + response.inquiry.property_manager?.last_name || 'Property Manager',
            property: response.inquiry.property?.building_name || response.inquiry.property?.title || 'Property',
            propertyId: response.inquiry.property_id,
            unitId: response.inquiry.property_id,
            avatar: 'ðŸ¢',
            status: response.inquiry.status,
            messages: [{
              id: 1,
              sender: 'tenant',
              text: response.inquiry.message,
              time: new Date(response.inquiry.created_at).toLocaleTimeString()
            }],
            inquiry: response.inquiry
          };
          
          setChats(prev => {
            const exists = prev.find(c => c.id === newChat.id);
            if (exists) {
              setSelectedChatId(exists.id);
              return prev;
            }
            setSelectedChatId(newChat.id);
            return [...prev, newChat];
          });
        }
      } catch (err) {
        console.error('Failed to create inquiry:', err);
        setError('Failed to create inquiry. Please try again.');
      } finally {
        setLoading(false);
      }
    };
    
    createInquiry();
  }, [initialChat]);

  const selectedChat = useMemo(() => chats.find(c => c.id === selectedChatId) || null, [chats, selectedChatId]);

  const handleSendMessage = async () => {
    if (!message.trim() || !selectedChat) return;
    
    try {
      setLoading(true);
      const response = await api.sendTenantMessage(selectedChat.id, message.trim());
      
      if (response && response.success) {
        // Add the message to the chat
        setChats(prev => prev.map(c => c.id === selectedChat.id ? {
          ...c,
          messages: [...c.messages, { 
            id: Date.now(), 
            sender: 'tenant', 
            text: message.trim(), 
            time: new Date().toLocaleTimeString() 
          }]
        } : c));
        setMessage('');
      } else {
        setError('Failed to send message. Please try again.');
      }
    } catch (err) {
      console.error('Failed to send message:', err);
      setError('Failed to send message. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') handleSendMessage();
  };

  return (
    <div className="fixed inset-0 bg-gray-900 bg-opacity-75 flex items-center justify-center p-3 z-50">
      <div className="bg-white rounded-xl w-full max-w-6xl max-h-[90vh] shadow-2xl overflow-hidden">
        {/* Header */}
        <div className="flex items-center justify-between p-4 border-b border-gray-200 bg-gradient-to-r from-gray-50 to-white">
          <div className="flex items-center space-x-3">
            <button
              onClick={onClose}
              className="w-9 h-9 bg-black rounded-full flex items-center justify-center hover:bg-gray-800 transition-all duration-300 shadow-lg"
            >
              <svg className="w-4 h-4 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
              </svg>
            </button>
            <div>
              <h1 className="text-lg font-black text-black">My Property Inquiries</h1>
              <p className="text-xs text-gray-500">Chat with property managers about available properties</p>
            </div>
          </div>
        </div>

        {/* Error Message */}
        {error && (
          <div className="bg-red-50 border-l-4 border-red-400 p-4 mx-4 mt-2">
            <div className="flex">
              <div className="ml-3">
                <p className="text-sm text-red-700">{error}</p>
                <button 
                  onClick={() => setError(null)}
                  className="text-xs text-red-600 underline mt-1"
                >
                  Dismiss
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Main Content */}
        <div className="flex h-[calc(90vh-72px)]">
          {/* Left Panel - Chat List */}
          <div className="w-80 border-r border-gray-200 bg-gray-50">
            {/* Tabs */}
            <div className="flex border-b border-gray-200">
              <button
                onClick={() => {}}
                className={`flex-1 py-3 px-4 text-sm font-medium transition-colors ${
                  'bg-white text-black border-b-2 border-black'
                }`}
              >
                Active Chats
              </button>
              <button
                onClick={() => {}}
                className={`flex-1 py-3 px-4 text-sm font-medium transition-colors ${
                  'text-gray-600 hover:text-black hover:bg-gray-100'
                }`}
              >
                New Properties
              </button>
            </div>

            {/* Search */}
            <div className="p-4">
              <div className="relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <svg className="h-4 w-4 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                  </svg>
                </div>
                <input
                  type="text"
                  placeholder="Search chats..."
                  className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-black focus:border-transparent text-sm"
                />
              </div>
            </div>

            {/* Chat/Property List */}
            <div className="flex-1 overflow-y-auto">
              {/* Loading State */}
              {loading && chats.length === 0 ? (
                <div className="p-4 text-center">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-black mx-auto"></div>
                  <p className="text-sm text-gray-500 mt-2">Loading inquiries...</p>
                </div>
              ) : (
                /* Active Chats */
                <div className="space-y-1 px-2">
                  {chats.length === 0 ? (
                    <div className="p-4 text-sm text-gray-500">No conversations yet. Start an inquiry from a unit to chat with a manager.</div>
                  ) : (
                  chats.map((chat) => (
                    <div
                      key={chat.id}
                      onClick={() => setSelectedChatId(chat.id)}
                      className={`p-3 rounded-lg cursor-pointer transition-colors ${
                        selectedChatId === chat.id ? 'bg-gray-200' : 'hover:bg-gray-100'
                      }`}
                    >
                      <div className="flex items-center space-x-3">
                        <div className="relative">
                          <div className="w-10 h-10 bg-blue-100 rounded-full flex items-center justify-center">
                            <span className="text-sm font-medium text-blue-600">{chat.avatar}</span>
                          </div>
                          {chat.status === 'online' && (
                            <div className="absolute -bottom-1 -right-1 w-3 h-3 bg-green-500 rounded-full border-2 border-white"></div>
                          )}
                        </div>
                        <div className="flex-1 min-w-0">
                          <div className="flex items-center justify-between">
                            <p className="text-sm font-medium text-gray-900 truncate">{chat.managerName}</p>
                            <span className="text-xs text-gray-500">{chat.messages[chat.messages.length - 1]?.time}</span>
                          </div>
                          <p className="text-xs text-gray-600 truncate">{chat.property}</p>
                          <p className="text-xs text-gray-500 truncate">{chat.messages[chat.messages.length - 1]?.text}</p>
                        </div>
                      </div>
                    </div>
                  ))
                  )}
                </div>
              )}
            </div>
          </div>

          {/* Right Panel - Chat Conversation */}
          <div className="flex-1 flex flex-col bg-white">
            {/* Chat Header */}
            <div className="flex items-center justify-between p-4 border-b border-gray-200">
              <div className="flex items-center space-x-3">
                <div className="relative">
                  <div className="w-10 h-10 bg-blue-100 rounded-full flex items-center justify-center">
                    <span className="text-sm font-medium text-blue-600">
                      {selectedChat?.avatar || 'PM'}
                    </span>
                  </div>
                    {selectedChat?.status === 'online' && (
                    <div className="absolute -bottom-1 -right-1 w-3 h-3 bg-green-500 rounded-full border-2 border-white"></div>
                  )}
                </div>
                <div>
                  <h3 className="text-sm font-medium text-gray-900">
                    {selectedChat?.managerName || 'Property Manager'}
                  </h3>
                  <p className="text-xs text-gray-500">
                    {selectedChat?.property || 'Property'}
                  </p>
                </div>
              </div>
              <div className="flex items-center space-x-2">
                <button className="p-2 text-gray-500 hover:text-black hover:bg-gray-100 rounded-lg transition-colors">
                  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 5a2 2 0 012-2h3.28a1 1 0 01.948.684l1.498 4.493a1 1 0 01-.502 1.21l-2.257 1.13a11.042 11.042 0 005.516 5.516l1.13-2.257a1 1 0 011.21-.502l4.493 1.498a1 1 0 01.684.949V19a2 2 0 01-2 2h-1C9.716 21 3 14.284 3 6V5z" />
                  </svg>
                </button>
                <button className="p-2 text-gray-500 hover:text-black hover:bg-gray-100 rounded-lg transition-colors">
                  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 10l4.553-2.276A1 1 0 0121 8.618v6.764a1 1 0 01-1.447.894L15 14M5 18h8a2 2 0 002-2V8a2 2 0 00-2-2H5a2 2 0 00-2 2v8a2 2 0 002 2z" />
                  </svg>
                </button>
              </div>
            </div>

            {/* Messages */}
            <div className="flex-1 overflow-y-auto p-4 space-y-4">
              {selectedChat?.messages?.map((msg) => (
                <div
                  key={msg.id}
                  className={`flex ${msg.sender === 'tenant' ? 'justify-end' : 'justify-start'}`}
                >
                  <div
                    className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${
                      msg.sender === 'tenant'
                        ? 'bg-black text-white'
                        : 'bg-gray-200 text-gray-900'
                    }`}
                  >
                    <p className="text-sm">{msg.text}</p>
                    <p className={`text-xs mt-1 ${
                      msg.sender === 'tenant' ? 'text-gray-300' : 'text-gray-500'
                    }`}>
                      {msg.time}
                    </p>
                  </div>
                </div>
              ))}
            </div>

            {/* Message Input */}
            <div className="p-4 border-t border-gray-200">
              <div className="flex items-center space-x-3">
                <button className="w-10 h-10 bg-gray-100 rounded-full flex items-center justify-center hover:bg-gray-200 transition-colors">
                  <svg className="w-5 h-5 text-gray-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                  </svg>
                </button>
                <input
                  type="text"
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder="Type your message to the property manager..."
                  className="flex-1 px-4 py-2 border border-gray-300 rounded-full focus:outline-none focus:ring-2 focus:ring-black focus:border-transparent"
                />
                <button
                  onClick={handleSendMessage}
                  className="w-10 h-10 bg-black rounded-full flex items-center justify-center hover:bg-gray-800 transition-colors"
                >
                  <svg className="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
                  </svg>
                </button>
              </div>
              <p className="text-xs text-gray-500 mt-2 text-center">
                You're chatting with the property manager about {selectedChat?.property || 'this property'}
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Inquiries;
