import { useState, useEffect } from 'react';
import ApiService from '../services/api';

export const useSubdomain = () => {
    const [subdomain, setSubdomain] = useState(null);
    const [isPropertyPortal, setIsPropertyPortal] = useState(false);
    const [property, setProperty] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const host = window.location.hostname;
        const parts = host.split('.');
        
        console.log('ðŸ” useSubdomain Debug:', { host, parts });
        
        if (parts.length > 1 && parts[parts.length - 1] === 'localhost') {
            const sub = parts[0];
            setSubdomain(sub);
            
            // Check if it's a property portal (not www, localhost, or empty)
            const isPortal = sub !== 'www' && sub !== 'localhost' && sub !== '';
            setIsPropertyPortal(isPortal);
            
            console.log('âœ… Detected subdomain:', sub, 'isPortal:', isPortal);
            
            if (isPortal) {
                fetchPropertyInfo(sub);
            } else {
                setLoading(false);
            }
        } else {
            // For non-localhost environments, check if current domain is a property portal
            const currentHost = host.toLowerCase();
            if (currentHost !== 'localhost' && currentHost !== 'www.localhost') {
                setSubdomain(currentHost);
                setIsPropertyPortal(true);
                fetchPropertyInfo(currentHost);
            } else {
                setLoading(false);
            }
        }
    }, []);

    const fetchPropertyInfo = async (subdomain) => {
        try {
            console.log('ðŸ“¡ Fetching property info for subdomain:', subdomain);
            const data = await ApiService.portalInfo();
            console.log('âœ… Got property data:', data);
            setProperty(data.property);
        } catch (error) {
            console.error('âŒ Error fetching property info:', error);
        } finally {
            setLoading(false);
        }
    };

    return { 
        subdomain, 
        isPropertyPortal, 
        property, 
        loading,
        refreshProperty: () => fetchPropertyInfo(subdomain)
    };
};
