import React, { useState, useEffect } from 'react';

const PortalCustomization = () => {
    const [portals, setPortals] = useState([]);
    const [selectedPortal, setSelectedPortal] = useState(null);
    const [themes, setThemes] = useState([]);
    const [features, setFeatures] = useState([]);
    const [loading, setLoading] = useState(true);
    const [saving, setSaving] = useState(false);
    const [customization, setCustomization] = useState({
        theme: 'default',
        custom_css: '',
        logo_url: '',
        banner_url: '',
        contact_info: {
            phone: '',
            email: '',
            whatsapp: '',
            office_hours: ''
        },
        features: {}
    });

    useEffect(() => {
        fetchMyPortals();
        fetchThemes();
        fetchFeatures();
    }, []);

    const fetchMyPortals = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('/api/manager/properties/my-properties', {
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                }
            });

            if (response.ok) {
                const data = await response.json();
                // Filter only ACTIVE properties that can have portals
                const activeProperties = data.properties.filter(prop => 
                    prop.status === 'ACTIVE' || prop.status === 'active'
                );
                
                // Transform to portal format
                const portals = activeProperties.map(prop => ({
                    id: prop.id,
                    name: prop.title,
                    subdomain: prop.portal_subdomain || 'not-set',
                    url: prop.portal_subdomain ? `${prop.portal_subdomain}.localhost:3000` : null,
                    property: prop,
                    portal: {
                        config: {
                            theme: prop.portal_theme || 'default',
                            custom_css: prop.portal_custom_css || '',
                            logo_url: prop.portal_logo_url || '',
                            banner_url: prop.portal_banner_url || '',
                            contact_info: (() => {
                                try {
                                    return prop.portal_contact_info ? JSON.parse(prop.portal_contact_info) : {
                                        phone: '',
                                        email: '',
                                        whatsapp: '',
                                        office_hours: ''
                                    };
                                } catch (e) {
                                    return { phone: '', email: '', whatsapp: '', office_hours: '' };
                                }
                            })(),
                            features: (() => {
                                try {
                                    return prop.portal_features ? JSON.parse(prop.portal_features) : {};
                                } catch (e) {
                                    return {};
                                }
                            })()
                        }
                    }
                }));
                
                setPortals(portals);
                if (portals.length > 0) {
                    setSelectedPortal(portals[0]);
                    loadPortalCustomization(portals[0]);
                }
            }
        } catch (error) {
            console.error('Fetch portals error:', error);
        } finally {
            setLoading(false);
        }
    };

    const fetchThemes = async () => {
        // Mock themes data
        const mockThemes = [
            { 
                id: 'default', 
                name: 'Default', 
                description: 'Clean and professional',
                colors: { primary: '#3B82F6' },
                preview: '/api/static/themes/default.jpg' 
            },
            { 
                id: 'modern', 
                name: 'Modern', 
                description: 'Sleek and contemporary',
                colors: { primary: '#1F2937' },
                preview: '/api/static/themes/modern.jpg' 
            },
            { 
                id: 'classic', 
                name: 'Classic', 
                description: 'Traditional and elegant',
                colors: { primary: '#059669' },
                preview: '/api/static/themes/classic.jpg' 
            },
            { 
                id: 'minimal', 
                name: 'Minimal', 
                description: 'Simple and clean',
                colors: { primary: '#6B7280' },
                preview: '/api/static/themes/minimal.jpg' 
            }
        ];
        setThemes(mockThemes);
    };

    const fetchFeatures = async () => {
        // Mock features data
        const mockFeatures = [
            { id: 'tenant_login', name: 'Tenant Login', description: 'Allow tenants to log in and access their dashboard' },
            { id: 'anonymous_inquiry', name: 'Anonymous Inquiry', description: 'Allow visitors to send inquiries without registration' },
            { id: 'property_gallery', name: 'Property Gallery', description: 'Display property photos and virtual tours' },
            { id: 'contact_form', name: 'Contact Form', description: 'Contact form for inquiries' },
            { id: 'social_sharing', name: 'Social Sharing', description: 'Share property on social media' },
            { id: 'analytics_tracking', name: 'Analytics', description: 'Track visitor behavior and inquiries' }
        ];
        setFeatures(mockFeatures);
    };

    const loadPortalCustomization = (portal) => {
        const config = portal.portal.config;
        setCustomization({
            theme: config.theme || 'default',
            custom_css: config.custom_css || '',
            logo_url: config.logo_url || '',
            banner_url: config.banner_url || '',
            contact_info: config.contact_info || {
                phone: '',
                email: '',
                whatsapp: '',
                office_hours: ''
            },
            features: config.features || {}
        });
    };

    const handlePortalSelect = (portal) => {
        setSelectedPortal(portal);
        loadPortalCustomization(portal);
    };

    const handleCustomizationChange = (field, value) => {
        setCustomization(prev => ({
            ...prev,
            [field]: value
        }));
    };

    const handleContactInfoChange = (field, value) => {
        setCustomization(prev => ({
            ...prev,
            contact_info: {
                ...prev.contact_info,
                [field]: value
            }
        }));
    };

    const handleFeatureToggle = (featureId, enabled) => {
        setCustomization(prev => ({
            ...prev,
            features: {
                ...prev.features,
                [featureId]: enabled
            }
        }));
    };

    const saveCustomization = async () => {
        if (!selectedPortal) return;
        
        setSaving(true);
        try {
            const response = await fetch(`/api/manager/portals/customize/${selectedPortal.property.id}`, {
                method: 'PUT',
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('access_token')}`,
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(customization)
            });

            if (response.ok) {
                alert('‚úÖ Portal customization saved successfully!');
                // Refresh portals to get updated config
                fetchMyPortals();
            } else {
                const errorData = await response.json();
                alert(`‚ùå Save failed: ${errorData.message}`);
            }
        } catch (error) {
            console.error('Save customization error:', error);
            alert('‚ùå Network error during save');
        } finally {
            setSaving(false);
        }
    };

    if (loading) {
        return (
            <div className="flex items-center justify-center py-12">
                <div className="text-center">
                    <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
                    <p className="text-gray-600">Loading your portals...</p>
                </div>
            </div>
        );
    }

    if (portals.length === 0) {
        return (
            <div className="text-center py-12">
                <div className="text-6xl mb-4">üè†</div>
                <h3 className="text-xl font-semibold text-gray-900 mb-2">No Portals Available</h3>
                <p className="text-gray-600 mb-4">
                    You don't have any approved properties with enabled portals yet.
                </p>
                <p className="text-sm text-gray-500">
                    Submit a property for admin approval to get your first portal!
                </p>
            </div>
        );
    }

    return (
        <div className="space-y-6">
            <div>
                <h2 className="text-2xl font-bold text-gray-900">Portal Customization</h2>
                <p className="text-gray-600">Customize the appearance and features of your property portals</p>
            </div>

            {/* Portal Selector */}
            <div className="bg-white rounded-xl shadow-lg p-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">Select Portal to Customize</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {portals.map((portal) => (
                        <div
                            key={portal.property.id}
                            onClick={() => handlePortalSelect(portal)}
                            className={`cursor-pointer border-2 rounded-lg p-4 transition-colors ${
                                selectedPortal?.property.id === portal.property.id
                                    ? 'border-blue-500 bg-blue-50'
                                    : 'border-gray-200 hover:border-gray-300'
                            }`}
                        >
                            <h4 className="font-semibold text-gray-900 mb-2">{portal.property.title}</h4>
                            <p className="text-sm text-gray-600 mb-2">{portal.property.address.city}</p>
                            <div className="flex items-center justify-between">
                                <span className="text-xs bg-green-100 text-green-800 px-2 py-1 rounded">
                                    Portal Active
                                </span>
                                <a
                                    href={portal.portal.url}
                                    target="_blank"
                                    rel="noopener noreferrer"
                                    className="text-xs text-blue-600 hover:underline"
                                    onClick={(e) => e.stopPropagation()}
                                >
                                    View Portal ‚Üí
                                </a>
                            </div>
                        </div>
                    ))}
                </div>
            </div>

            {selectedPortal && (
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                    {/* Customization Panel */}
                    <div className="space-y-6">
                        {/* Theme Selection */}
                        <div className="bg-white rounded-xl shadow-lg p-6">
                            <h3 className="text-lg font-semibold text-gray-900 mb-4">üé® Theme</h3>
                            <div className="grid grid-cols-1 sm:grid-cols-2 gap-3">
                                {themes.map((theme) => (
                                    <div
                                        key={theme.id}
                                        onClick={() => handleCustomizationChange('theme', theme.id)}
                                        className={`cursor-pointer border-2 rounded-lg p-3 transition-colors ${
                                            customization.theme === theme.id
                                                ? 'border-blue-500 bg-blue-50'
                                                : 'border-gray-200 hover:border-gray-300'
                                        }`}
                                    >
                                        <div className="flex items-center space-x-3">
                                            <div 
                                                className="w-8 h-8 rounded"
                                                style={{ backgroundColor: theme.colors.primary }}
                                            ></div>
                                            <div>
                                                <h4 className="font-medium text-gray-900">{theme.name}</h4>
                                                <p className="text-xs text-gray-600">{theme.description}</p>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>

                        {/* Branding */}
                        <div className="bg-white rounded-xl shadow-lg p-6">
                            <h3 className="text-lg font-semibold text-gray-900 mb-4">üñºÔ∏è Branding</h3>
                            <div className="space-y-4">
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        Logo URL
                                    </label>
                                    <input
                                        type="url"
                                        value={customization.logo_url}
                                        onChange={(e) => handleCustomizationChange('logo_url', e.target.value)}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                        placeholder="https://example.com/logo.png"
                                    />
                                </div>
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        Banner Image URL
                                    </label>
                                    <input
                                        type="url"
                                        value={customization.banner_url}
                                        onChange={(e) => handleCustomizationChange('banner_url', e.target.value)}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                        placeholder="https://example.com/banner.jpg"
                                    />
                                </div>
                            </div>
                        </div>

                        {/* Contact Information */}
                        <div className="bg-white rounded-xl shadow-lg p-6">
                            <h3 className="text-lg font-semibold text-gray-900 mb-4">üìû Contact Information</h3>
                            <div className="space-y-4">
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        Phone Number
                                    </label>
                                    <input
                                        type="tel"
                                        value={customization.contact_info.phone}
                                        onChange={(e) => handleContactInfoChange('phone', e.target.value)}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                        placeholder="+63 917 123 4567"
                                    />
                                </div>
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        Email Address
                                    </label>
                                    <input
                                        type="email"
                                        value={customization.contact_info.email}
                                        onChange={(e) => handleContactInfoChange('email', e.target.value)}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                        placeholder="manager@example.com"
                                    />
                                </div>
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        WhatsApp Number
                                    </label>
                                    <input
                                        type="tel"
                                        value={customization.contact_info.whatsapp}
                                        onChange={(e) => handleContactInfoChange('whatsapp', e.target.value)}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                        placeholder="+63 917 123 4567"
                                    />
                                </div>
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        Office Hours
                                    </label>
                                    <input
                                        type="text"
                                        value={customization.contact_info.office_hours}
                                        onChange={(e) => handleContactInfoChange('office_hours', e.target.value)}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                        placeholder="Mon-Fri 9AM-6PM"
                                    />
                                </div>
                            </div>
                        </div>

                        {/* Features */}
                        <div className="bg-white rounded-xl shadow-lg p-6">
                            <h3 className="text-lg font-semibold text-gray-900 mb-4">‚ö° Features</h3>
                            <div className="space-y-3">
                                {features.map((feature) => (
                                    <div key={feature.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                                        <div className="flex-1">
                                            <h4 className="font-medium text-gray-900">{feature.name}</h4>
                                            <p className="text-sm text-gray-600">{feature.description}</p>
                                            <span className="text-xs text-gray-500 capitalize">{feature.category}</span>
                                        </div>
                                        <label className="relative inline-flex items-center cursor-pointer">
                                            <input
                                                type="checkbox"
                                                checked={customization.features[feature.id] !== undefined 
                                                    ? customization.features[feature.id] 
                                                    : feature.default_enabled}
                                                onChange={(e) => handleFeatureToggle(feature.id, e.target.checked)}
                                                className="sr-only peer"
                                            />
                                            <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-blue-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-blue-600"></div>
                                        </label>
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>

                    {/* Preview Panel */}
                    <div className="space-y-6">
                        <div className="bg-white rounded-xl shadow-lg p-6">
                            <div className="flex items-center justify-between mb-4">
                                <h3 className="text-lg font-semibold text-gray-900">üëÅÔ∏è Preview</h3>
                                <a
                                    href={selectedPortal.portal.url}
                                    target="_blank"
                                    rel="noopener noreferrer"
                                    className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors text-sm"
                                >
                                    Open Portal ‚Üí
                                </a>
                            </div>
                            
                            <div className="border rounded-lg p-4 bg-gray-50">
                                <div className="text-center mb-4">
                                    <h4 className="font-bold text-lg">{selectedPortal.property.title}</h4>
                                    <p className="text-sm text-gray-600">{selectedPortal.portal.subdomain}.localhost</p>
                                </div>
                                
                                <div className="space-y-2 text-sm">
                                    <div><strong>Theme:</strong> {customization.theme}</div>
                                    <div><strong>Location:</strong> {selectedPortal.property.address.city}</div>
                                    <div><strong>Rent:</strong> ‚Ç±{Number(selectedPortal.property.pricing.monthly_rent).toLocaleString()}/month</div>
                                </div>
                                
                                {customization.contact_info.phone && (
                                    <div className="mt-4 p-3 bg-blue-50 rounded">
                                        <div className="text-sm">
                                            <strong>Contact:</strong> {customization.contact_info.phone}
                                        </div>
                                        {customization.contact_info.office_hours && (
                                            <div className="text-xs text-gray-600">
                                                {customization.contact_info.office_hours}
                                            </div>
                                        )}
                                    </div>
                                )}
                                
                                <div className="mt-4 text-xs text-gray-500">
                                    Features: {Object.entries(customization.features).filter(([_, enabled]) => enabled).length} enabled
                                </div>
                            </div>
                        </div>

                        {/* Save Button */}
                        <button
                            onClick={saveCustomization}
                            disabled={saving}
                            className={`w-full py-3 px-4 rounded-lg font-medium transition-colors ${
                                saving
                                    ? 'bg-gray-400 cursor-not-allowed'
                                    : 'bg-green-600 hover:bg-green-700'
                            } text-white`}
                        >
                            {saving ? (
                                <div className="flex items-center justify-center">
                                    <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                                    Saving Changes...
                                </div>
                            ) : (
                                'üíæ Save Customization'
                            )}
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default PortalCustomization;
