import React from 'react';

import api from '../../services/api';

const PropertyDetails = ({ property, subdomain }) => {
    const formatPrice = (price) => {
        return Number(price).toLocaleString('en-PH', {
            style: 'currency',
            currency: 'PHP',
            minimumFractionDigits: 0
        });
    };

    const getPropertyTypeIcon = (type) => {
        switch (type?.toLowerCase()) {
            case 'apartment': return 'üè¢';
            case 'house': return 'üè†';
            case 'condo': return 'üèóÔ∏è';
            case 'studio': return 'üè†';
            default: return 'üè†';
        }
    };

    const getFurnishingBadge = (furnishing) => {
        const colors = {
            'furnished': 'bg-green-100 text-green-800',
            'semi_furnished': 'bg-yellow-100 text-yellow-800',
            'unfurnished': 'bg-gray-100 text-gray-800'
        };
        
        return colors[furnishing] || 'bg-gray-100 text-gray-800';
    };

    const [units, setUnits] = React.useState([]);
    const [loading, setLoading] = React.useState(false);
    const [error, setError] = React.useState(null);

    React.useEffect(() => {
        let mounted = true;
        async function loadUnits() {
            try {
                setLoading(true);
                setError(null);
                // Property portals are tied to a single property; manager side uses managed_property ids.
                // For tenant view, list units for this property id if available.
                const res = await api.listUnits(property.id);
                const items = Array.isArray(res?.items) ? res.items : [];
                if (mounted) setUnits(items);
            } catch (e) {
                if (mounted) setError('Failed to load available spaces');
            } finally {
                if (mounted) setLoading(false);
            }
        }
        if (property?.id) loadUnits();
        return () => { mounted = false; };
    }, [property?.id]);

    return (
        <div className="bg-white rounded-xl shadow-lg overflow-hidden">
            {/* Property Images Placeholder */}
            <div className="h-64 bg-gradient-to-br from-blue-400 to-blue-600 flex items-center justify-center text-white">
                <div className="text-center">
                    <div className="text-6xl mb-2">{getPropertyTypeIcon(property.property_type)}</div>
                    <p className="text-lg font-medium">Property Images</p>
                    <p className="text-sm opacity-75">Coming Soon</p>
                </div>
            </div>

            <div className="p-6">
                {/* Property Title & Type */}
                <div className="flex items-start justify-between mb-4">
                    <div>
                        <h2 className="text-2xl font-bold text-gray-900 mb-2">
                            {property.title}
                        </h2>
                        <div className="flex items-center space-x-2">
                            <span className={`px-3 py-1 rounded-full text-xs font-medium ${getFurnishingBadge(property.furnishing)}`}>
                                {property.furnishing?.replace('_', ' ').toUpperCase()}
                            </span>
                            <span className="px-3 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                                {property.property_type?.toUpperCase()}
                            </span>
                        </div>
                    </div>
                    <div className="text-right">
                        <div className="text-3xl font-bold text-blue-600">
                            {formatPrice(property.monthly_rent)}
                        </div>
                        <div className="text-sm text-gray-500">per month</div>
                    </div>
                </div>

                {/* Property Description */}
                <div className="mb-6">
                    <h3 className="text-lg font-semibold text-gray-900 mb-2">Description</h3>
                    <p className="text-gray-600 leading-relaxed">
                        {property.description || 'No description available for this property.'}
                    </p>
                </div>

                {/* Property Features Grid */}
                <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6">
                    <div className="text-center p-3 bg-gray-50 rounded-lg">
                        <div className="text-2xl mb-1">üõèÔ∏è</div>
                        <div className="text-sm text-gray-600">Bedrooms</div>
                        <div className="font-semibold">{property.bedrooms || 'N/A'}</div>
                    </div>
                    <div className="text-center p-3 bg-gray-50 rounded-lg">
                        <div className="text-2xl mb-1">üöø</div>
                        <div className="text-sm text-gray-600">Bathrooms</div>
                        <div className="font-semibold">{property.bathrooms || 'N/A'}</div>
                    </div>
                    <div className="text-center p-3 bg-gray-50 rounded-lg">
                        <div className="text-2xl mb-1">üìê</div>
                        <div className="text-sm text-gray-600">Floor Area</div>
                        <div className="font-semibold">{property.floor_area || 'N/A'}m¬≤</div>
                    </div>
                    <div className="text-center p-3 bg-gray-50 rounded-lg">
                        <div className="text-2xl mb-1">üÖøÔ∏è</div>
                        <div className="text-sm text-gray-600">Parking</div>
                        <div className="font-semibold">{property.parking_spaces || 'N/A'}</div>
                    </div>
                </div>

                {/* Location */}
                <div className="mb-6">
                    <h3 className="text-lg font-semibold text-gray-900 mb-3">Location</h3>
                    <div className="bg-gray-50 rounded-lg p-4">
                        <div className="flex items-start space-x-3">
                            <div className="text-xl">üìç</div>
                            <div>
                                <div className="font-medium text-gray-900">
                                    {property.address_line1}
                                </div>
                                {property.address_line2 && (
                                    <div className="text-gray-600">
                                        {property.address_line2}
                                    </div>
                                )}
                                <div className="text-gray-600">
                                    {property.city}, {property.province} {property.postal_code}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Additional Details */}
                {(property.security_deposit || property.lot_area) && (
                    <div className="border-t pt-6">
                        <h3 className="text-lg font-semibold text-gray-900 mb-3">Additional Details</h3>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                            {property.security_deposit && (
                                <div className="flex justify-between">
                                    <span className="text-gray-600">Security Deposit:</span>
                                    <span className="font-medium">{formatPrice(property.security_deposit)}</span>
                                </div>
                            )}
                            {property.lot_area && (
                                <div className="flex justify-between">
                                    <span className="text-gray-600">Lot Area:</span>
                                    <span className="font-medium">{property.lot_area}m¬≤</span>
                                </div>
                            )}
                        </div>
                    </div>
                )}

                {/* Contact Property Manager */}
                <div className="mt-6 p-4 bg-blue-50 rounded-lg">
                    <h4 className="font-semibold text-blue-900 mb-2">Property Manager</h4>
                    <p className="text-blue-700 text-sm">
                        For inquiries and viewing appointments, please use the inquiry form or login to your tenant account.
                    </p>
                </div>

                {/* Available Units / Spaces */}
                <div className="mt-8">
                    <h3 className="text-lg font-semibold text-gray-900 mb-3">Available Spaces</h3>
                    {loading ? (
                        <div className="text-gray-600">Loading spaces...</div>
                    ) : error ? (
                        <div className="text-red-600 text-sm">{error}</div>
                    ) : units.length === 0 ? (
                        <div className="text-gray-600 text-sm">No spaces available at the moment.</div>
                    ) : (
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                            {units.filter(u => u.status === 'Vacant').map((u) => (
                                <div key={u.id} className="border rounded-lg p-4 flex items-start gap-4">
                                    <div className="w-24 h-24 bg-gray-100 rounded overflow-hidden flex-shrink-0">
                                        <img src={(u.image || (u.images && u.images[0])) || ''} alt={u.unitName} className="w-full h-full object-cover" />
                                    </div>
                                    <div className="flex-1">
                                        <div className="flex items-center justify-between">
                                            <h4 className="font-semibold text-gray-900">{u.unitName}</h4>
                                            <div className="text-blue-700 font-bold">‚Ç±{Number(u.price).toLocaleString()}</div>
                                        </div>
                                        <div className="text-sm text-gray-600">{u.bedrooms} bed ‚Ä¢ {u.bathrooms} bath ‚Ä¢ {u.sizeSqm} m¬≤</div>
                                        <div className="text-xs text-gray-500 mt-1">Status: {u.status}</div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default PropertyDetails;
