import React, { useEffect, useMemo, useRef, useState } from 'react';

const Inquiries = ({ isOpen, onClose }) => {
  const [activeTab, setActiveTab] = useState('chats');
  const [selectedChatId, setSelectedChatId] = useState(null);
  const [newMessage, setNewMessage] = useState('');
  const [inquiries, setInquiries] = useState([]); // chats from tenant local storage
  const [loading, setLoading] = useState(true);
  const hydratedRef = useRef(false);

  // Hydrate inquiries from tenant storage, scoped strictly to this manager
  useEffect(() => {
    try {
      const raw = localStorage.getItem('tenant_chats');
      let chats = raw ? JSON.parse(raw) : [];
      if (!Array.isArray(chats)) chats = [];
      // Filter by current manager
      const meRaw = localStorage.getItem('user_id');
      const meId = meRaw ? parseInt(meRaw, 10) : null;
      // Only show chats explicitly addressed to this manager
      const scoped = meId ? chats.filter(c => c.managerId != null && String(c.managerId) === String(meId)) : [];
      setInquiries(scoped);
      const savedSelected = localStorage.getItem('pm_selected_chat_id');
      if (savedSelected) setSelectedChatId(savedSelected);
    } catch (_) {}
    hydratedRef.current = true;
    setLoading(false);
  }, []);

  // Persist PM selection back so it opens same thread again
  useEffect(() => {
    if (!hydratedRef.current) return;
    try { if (selectedChatId) localStorage.setItem('pm_selected_chat_id', selectedChatId); } catch (_) {}
  }, [selectedChatId]);

  const selectedChat = useMemo(() => inquiries.find(c => c.id === selectedChatId) || null, [inquiries, selectedChatId]);

  const handleSendMessage = () => {
    if (!newMessage.trim() || !selectedChat) return;
    const meRaw = localStorage.getItem('user_id');
    const meId = meRaw ? parseInt(meRaw, 10) : null;
    if (!meId || selectedChat.managerId == null || String(selectedChat.managerId) !== String(meId)) {
      return; // hard block sending to chats not owned by this manager
    }
    const text = newMessage.trim();
    setInquiries(prev => {
      const next = prev.map(c => c.id === selectedChat.id ? {
        ...c,
        messages: [...(c.messages || []), { id: Date.now(), sender: 'manager', text, time: new Date().toLocaleTimeString() }]
      } : c);
      try { localStorage.setItem('tenant_chats', JSON.stringify(next)); } catch (_) {}
      return next;
    });
    setNewMessage('');
  };

  const markAsRead = (inquiryId) => {
    // no-op for local prototype; could update a flag in local storage
  };

  if (!isOpen) return null;

  if (loading) {
    return (
      <div className="fixed inset-0 z-50 flex items-center justify-center">
        <div className="bg-black bg-opacity-50 fixed inset-0" onClick={onClose}></div>
        <div className="bg-white rounded-lg shadow-xl max-w-4xl w-full mx-4 h-[80vh] flex items-center justify-center relative z-10">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
            <p className="text-gray-600">Loading inquiries...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center">
      {/* Backdrop */}
      <div className="bg-black bg-opacity-50 fixed inset-0" onClick={onClose}></div>
      
      {/* Modal Content */}
      <div className="bg-white rounded-lg shadow-xl max-w-6xl w-full mx-4 h-[80vh] flex flex-col relative z-10">
        {/* Header */}
        <div className="p-6 border-b flex items-center justify-between">
          <div>
            <h2 className="text-xl font-semibold text-gray-900">Property Inquiries</h2>
            <p className="text-sm text-gray-600 mt-1">Manage tenant inquiries for your properties</p>
          </div>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600 transition-colors"
          >
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>

      {/* Tabs */}
      <div className="flex border-b">
        <button
          onClick={() => setActiveTab('chats')}
          className={`px-6 py-3 font-medium ${
            activeTab === 'chats'
              ? 'text-blue-600 border-b-2 border-blue-600'
              : 'text-gray-500 hover:text-gray-700'
          }`}
        >
          Active Chats ({inquiries.length})
        </button>
        <button
          onClick={() => setActiveTab('new')}
          className={`px-6 py-3 font-medium ${
            activeTab === 'new'
              ? 'text-blue-600 border-b-2 border-blue-600'
              : 'text-gray-500 hover:text-gray-700'
          }`}
        >
          New Inquiries ({inquiries.filter(i => i.status === 'new').length})
        </button>
      </div>

      {/* Content */}
      <div className="flex-1 overflow-hidden">
        {activeTab === 'chats' ? (
          <div className="flex h-full">
            {/* Chat List */}
            <div className="w-1/3 border-r overflow-y-auto">
                {inquiries.length === 0 ? (
                <div className="p-6 text-center text-gray-500">
                  <div className="mb-4">
                    <svg className="mx-auto h-12 w-12 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-3.582 8-8 8a8.959 8.959 0 01-4.906-1.456L3 21l2.456-5.094A8.959 8.959 0 013 12c0-4.418 3.582-8 8-8s8 3.582 8 8z" />
                    </svg>
                  </div>
                  <p>No inquiries yet.</p>
                  <p className="text-sm mt-1">Inquiries will appear here when tenants contact you about your properties.</p>
                </div>
                ) : (
                inquiries.map((inquiry) => (
                  <div
                    key={inquiry.id}
                    onClick={() => {
                      setSelectedChatId(inquiry.id);
                      if (inquiry.unread_count > 0) {
                        markAsRead(inquiry.id);
                      }
                    }}
                    className={`p-4 border-b cursor-pointer hover:bg-gray-50 ${
                      selectedChatId === inquiry.id ? 'bg-blue-50 border-blue-200' : ''
                    }`}
                  >
                    <div className="flex items-center space-x-3">
                      <div className="w-10 h-10 bg-gray-100 rounded-full flex items-center justify-center">
                        <span className="text-gray-600 font-medium">
                          {(inquiry.avatar || (inquiry.managerName || 'PM')).toString().slice(0,2).toUpperCase()}
                        </span>
                      </div>
                      <div className="flex-1 min-w-0">
                        <p className="text-sm font-medium text-gray-900 truncate">
                          {inquiry.managerName || 'Property Manager'}
                        </p>
                        <p className="text-sm text-gray-500 truncate">
                          {inquiry.property || 'Property'}
                        </p>
                        <p className="text-xs text-gray-400">
                          {inquiry.messages?.[inquiry.messages.length - 1]?.time || 'No messages'}
                        </p>
                      </div>
                      <div className="flex flex-col items-end space-y-1">
                        {inquiry.unread_count > 0 && (
                          <span className="bg-blue-600 text-white text-xs rounded-full px-2 py-1">
                            {inquiry.unread_count}
                          </span>
                        )}
                        <span className={`text-xs px-2 py-1 rounded-full ${
                          inquiry.status === 'new' ? 'bg-green-100 text-green-800' :
                          inquiry.status === 'active' ? 'bg-blue-100 text-blue-800' :
                          'bg-gray-100 text-gray-800'
                        }`}>
                          {inquiry.status || 'active'}
                        </span>
                      </div>
                    </div>
                  </div>
                ))
              )}
            </div>

            {/* Chat Messages */}
            <div className="flex-1 flex flex-col">
              {selectedChat ? (
                <>
                  {/* Chat Header */}
                  <div className="p-4 border-b bg-gray-50">
                    <div className="flex items-center justify-between">
                      <div>
                        <h3 className="font-medium text-gray-900">{selectedChat.tenant?.name}</h3>
                        <p className="text-sm text-gray-500">
                          Interested in: {selectedChat.property || 'Property'}
                        </p>
                        {(selectedChat.tenant?.email || selectedChat.tenant?.phone) && (
                          <p className="text-xs text-gray-400">
                            {[selectedChat.tenant?.email, selectedChat.tenant?.phone].filter(Boolean).join(' • ')}
                          </p>
                        )}
                      </div>
                      <div className="text-right">
                        {selectedChat.price && (
                          <p className="text-sm font-medium text-blue-600">
                            {selectedChat.price}
                          </p>
                        )}
                      </div>
                    </div>
                  </div>

                  {/* Messages */}
                  <div className="flex-1 overflow-y-auto p-4 space-y-4">
                    {selectedChat.messages?.length === 0 ? (
                      <div className="text-center text-gray-500 py-8">
                        <p>No messages yet. The tenant is waiting for your response!</p>
                      </div>
                    ) : (
                      (selectedChat.messages || []).map((message) => (
                        <div
                          key={message.id}
                          className={`flex ${message.sender === 'manager' ? 'justify-end' : 'justify-start'}`}
                        >
                          <div
                            className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${
                              message.sender === 'manager' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-900'
                            }`}
                          >
                            <p className="text-sm">{message.text || message.content}</p>
                            <p className={`text-xs mt-1 ${message.sender === 'manager' ? 'text-blue-100' : 'text-gray-500'}`}>
                              {message.time || (message.created_at ? new Date(message.created_at).toLocaleTimeString() : '')}
                            </p>
                          </div>
                        </div>
                      ))
                    )}
                  </div>

                  {/* Message Input */}
                  <div className="p-4 border-t">
                    <div className="flex space-x-2">
                      <input
                        type="text"
                        value={newMessage}
                        onChange={(e) => setNewMessage(e.target.value)}
                        placeholder="Type your response..."
                        className="flex-1 px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                        onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                      />
                      <button
                        onClick={handleSendMessage}
                        disabled={!newMessage.trim()}
                        className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
                      >
                        Send
                      </button>
                    </div>
                  </div>
                </>
              ) : (
                <div className="flex-1 flex items-center justify-center text-gray-500">
                  <div className="text-center">
                    <svg className="mx-auto h-12 w-12 text-gray-400 mb-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-3.582 8-8 8a8.959 8.959 0 01-4.906-1.456L3 21l2.456-5.094A8.959 8.959 0 013 12c0-4.418 3.582-8 8-8s8 3.582 8 8z" />
                    </svg>
                    <p>Select an inquiry to view messages</p>
                  </div>
                </div>
              )}
            </div>
          </div>
        ) : (
          /* New Inquiries Tab */
          <div className="p-6 overflow-y-auto">
            <div className="space-y-4">
              {inquiries.filter(i => i.status === 'new').length === 0 ? (
                <div className="text-center text-gray-500 py-8">
                  <div className="mb-4">
                    <svg className="mx-auto h-12 w-12 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20 13V6a2 2 0 00-2-2H6a2 2 0 00-2 2v7m16 0v5a2 2 0 01-2 2H6a2 2 0 01-2 2v-5m16 0h-2.586a1 1 0 00-.707.293l-2.414 2.414a1 1 0 01-.707.293h-3.172a1 1 0 01-.707-.293l-2.414-2.414A1 1 0 006.586 13H4" />
                    </svg>
                  </div>
                  <p>No new inquiries</p>
                  <p className="text-sm mt-1">New tenant inquiries will appear here.</p>
                </div>
              ) : (
                inquiries.filter(i => i.status === 'new').map((inquiry) => (
                  <div key={inquiry.id} className="border rounded-lg p-4 hover:shadow-md transition-shadow">
                    <div className="flex items-start space-x-4">
                      <img
                        src={inquiry.property?.images?.[0] || '/placeholder-property.jpg'}
                        alt={inquiry.property?.title}
                        className="w-16 h-16 object-cover rounded-lg"
                      />
                      <div className="flex-1">
                        <div className="flex items-start justify-between">
                          <div>
                            <h4 className="font-medium text-gray-900">{inquiry.tenant?.name}</h4>
                            <p className="text-sm text-gray-600">{inquiry.tenant?.email}</p>
                            <p className="text-sm text-gray-500 mt-1">
                              Interested in: <span className="font-medium">{inquiry.property || 'Property'}</span>
                            </p>
                          </div>
                          <div className="text-right">
                            <span className="bg-green-100 text-green-800 text-xs px-2 py-1 rounded-full">
                              New
                            </span>
                            <p className="text-xs text-gray-500 mt-1">
                              {new Date(inquiry.created_at).toLocaleDateString()}
                            </p>
                          </div>
                        </div>
                        <div className="mt-3 flex space-x-2">
                          <button
                            onClick={() => {
                              setSelectedChatId(inquiry.id);
                              setActiveTab('chats');
                              markAsRead(inquiry.id);
                            }}
                            className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 text-sm"
                          >
                            Respond
                          </button>
                          <button className="bg-gray-100 text-gray-700 px-4 py-2 rounded-lg hover:bg-gray-200 text-sm">
                            View Property
                          </button>
                        </div>
                      </div>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>
        )}
      </div>
      </div>
    </div>
  );
};

export default Inquiries;
