import React, { useEffect, useState } from 'react';

const SearchSection = ({ filters = {}, onChange = () => {} }) => {
  const [classificationOpen, setClassificationOpen] = useState(false);
  const [priceRangeOpen, setPriceRangeOpen] = useState(false);
  const [selectedClassification, setSelectedClassification] = useState(filters.type || '');
  const [minPrice, setMinPrice] = useState(filters.min_price || '');
  const [maxPrice, setMaxPrice] = useState(filters.max_price || '');
  const [city, setCity] = useState(filters.city || '');
  const [bedrooms, setBedrooms] = useState(filters.bedrooms || '');
  const [bedroomsOpen, setBedroomsOpen] = useState(false);
  const [bathrooms, setBathrooms] = useState(filters.bathrooms || '');
  const [bathroomsOpen, setBathroomsOpen] = useState(false);

  // Use backend enum values with human labels
  const classifications = [
    { value: 'bed_space', label: 'Bed Space' },
    { value: 'dormitory', label: 'Dormitory' },
    { value: 'boarding_house', label: 'Boarding House' },
    { value: 'studio_apartment', label: 'Studio Apartment' },
    { value: 'room_for_rent', label: 'Room for Rent' }
  ];

  // Bathroom options
  const bathroomOptions = [
    { value: 'own', label: 'Own Bathroom' },
    { value: 'share', label: 'Share Bathroom' }
  ];

  const toggleClassification = () => {
    setClassificationOpen(!classificationOpen);
    setPriceRangeOpen(false);
    setBedroomsOpen(false);
    setBathroomsOpen(false);
  };

  const togglePriceRange = () => {
    setPriceRangeOpen(!priceRangeOpen);
    setClassificationOpen(false);
    setBedroomsOpen(false);
    setBathroomsOpen(false);
  };

  const toggleBedrooms = () => {
    setBedroomsOpen(!bedroomsOpen);
    setClassificationOpen(false);
    setPriceRangeOpen(false);
    setBathroomsOpen(false);
  };

  const toggleBathrooms = () => {
    setBathroomsOpen(!bathroomsOpen);
    setClassificationOpen(false);
    setPriceRangeOpen(false);
    setBedroomsOpen(false);
  };

  const handleClassificationSelect = (classification) => {
    setSelectedClassification(classification);
    setClassificationOpen(false);
    onChange({ ...filters, type: classification });
  };

  const handleApplyPriceRange = () => {
    setPriceRangeOpen(false);
    onChange({ ...filters, min_price: minPrice, max_price: maxPrice });
  };

  const handleBathroomSelect = (bathroom) => {
    setBathrooms(bathroom);
    setBathroomsOpen(false);
    onChange({ ...filters, bathrooms: bathroom });
  };

  const handleClearFilters = () => {
    setSelectedClassification('');
    setMinPrice('');
    setMaxPrice('');
    setCity('');
    setBedrooms('');
    setBathrooms('');
    onChange({ city: '', type: '', min_price: '', max_price: '', bedrooms: '', bathrooms: '' });
  };

  // Debounce city changes to avoid chatty requests
  useEffect(() => {
    const id = setTimeout(() => {
      onChange({ ...filters, city });
    }, 300);
    return () => clearTimeout(id);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [city]);

  return (
    <div className="mb-8">
      <div className="flex flex-col lg:flex-row items-stretch lg:items-center space-y-3 lg:space-y-0 lg:space-x-3">
        {/* Search Input */}
        <div className="flex-1 relative">
          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
            <svg className="h-5 w-5 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
            </svg>
          </div>
          <input
            type="text"
            placeholder="Search City"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            className="block w-full pl-10 pr-4 py-3 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-black focus:border-transparent shadow-sm"
          />
        </div>

        {/* Filter Buttons */}
        <div className="flex flex-col sm:flex-row space-y-2 sm:space-y-0 sm:space-x-3">
          {/* Classification Dropdown */}
          <div className="relative">
            <button 
              onClick={toggleClassification}
              className={`flex items-center justify-center space-x-2 px-4 py-3 border rounded-xl transition-colors shadow-sm ${
                selectedClassification 
                  ? 'border-blue-500 bg-blue-50 text-blue-700' 
                  : 'border-gray-200 hover:bg-gray-50 text-gray-700'
              }`}
            >
              <svg className="h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16m14 0h2m-2 0h-5m-9 0H3m2 0h5M9 7h1m-1 4h1m4-4h1m-1 4h1m-5 10v-5a1 1 0 011-1h2a1 1 0 011 1v5m-4 0h4" />
              </svg>
              <span>{selectedClassification || 'Classification'}</span>
              <svg className={`w-4 h-4 transition-transform ${classificationOpen ? 'rotate-180' : ''}`} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
              </svg>
            </button>

            {/* Classification Dropdown Menu */}
            {classificationOpen && (
              <div className="absolute top-full left-0 mt-2 w-64 bg-white border border-blue-500 rounded-xl shadow-2xl z-50">
                <div className="p-2">
                  <div className="text-xs text-gray-500 mb-2 px-2">classification</div>
                  <div className="space-y-0">
                    {classifications.map((c, index) => (
                      <button
                        key={c.value}
                        onClick={() => handleClassificationSelect(c.value)}
                        className={`w-full text-left px-3 py-3 text-sm font-medium hover:bg-gray-50 transition-colors ${
                          selectedClassification === c.value ? 'bg-blue-50 text-blue-700' : 'text-black'
                        } ${index !== classifications.length - 1 ? 'border-b border-gray-200' : ''}`}
                      >
                        {c.label}
                      </button>
                    ))}
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Bedrooms Selector (dropdown for consistent UI) */}
          <div className="relative">
            <button 
              onClick={toggleBedrooms}
              className={`flex items-center justify-center space-x-2 px-4 py-3 border rounded-xl transition-colors shadow-sm ${
                bedrooms ? 'border-blue-500 bg-blue-50 text-blue-700' : 'border-gray-200 hover:bg-gray-50 text-gray-700'
              }`}
            >
              <span>Any Beds</span>
              {bedrooms && <span className="font-semibold">({bedrooms}+)</span>}
              <svg className={`w-4 h-4 transition-transform ${bedroomsOpen ? 'rotate-180' : ''}`} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
              </svg>
            </button>
            {bedroomsOpen && (
              <div className="absolute top-full left-0 mt-2 w-40 bg-white border border-blue-500 rounded-xl shadow-2xl z-50">
                <div className="p-2">
                  {[ '', '1', '2', '3', '4' ].map((b, idx) => (
                    <button
                      key={idx}
                      onClick={() => { setBedrooms(b); setBedroomsOpen(false); onChange({ ...filters, bedrooms: b }); }}
                      className={`w-full text-left px-3 py-2 text-sm hover:bg-gray-50 ${b === bedrooms ? 'bg-blue-50 text-blue-700' : 'text-black'}`}
                    >
                      {b === '' ? 'Any Beds' : `${b}+`}
                    </button>
                  ))}
                </div>
              </div>
            )}
          </div>

          {/* Price Range Dropdown */}
          <div className="relative">
            <button 
              onClick={togglePriceRange}
              className={`flex items-center justify-center space-x-2 px-4 py-3 border rounded-xl transition-colors shadow-sm ${
                (minPrice || maxPrice) 
                  ? 'border-blue-500 bg-blue-50 text-blue-700' 
                  : 'border-gray-200 hover:bg-gray-50 text-gray-700'
              }`}
            >
              <span className="font-medium">₱</span>
              <span>Price Range</span>
              <svg className={`w-4 h-4 transition-transform ${priceRangeOpen ? 'rotate-180' : ''}`} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
              </svg>
            </button>

            {/* Price Range Dropdown Menu */}
            {priceRangeOpen && (
              <div className="absolute top-full left-0 mt-2 w-80 bg-white border border-blue-500 rounded-xl shadow-2xl z-50">
                <div className="p-4">
                  <div className="text-xs text-gray-500 mb-4">price range</div>
                  
                  {/* Min Price */}
                  <div className="mb-4">
                    <label className="block text-lg font-medium text-black mb-2">Min</label>
                    <input
                      type="number"
                      value={minPrice}
                      onChange={(e) => setMinPrice(e.target.value)}
                      placeholder="0"
                      className="w-full px-3 py-2 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-black focus:border-transparent"
                    />
                  </div>

                  {/* Max Price */}
                  <div className="mb-6">
                    <label className="block text-lg font-medium text-black mb-2">Max</label>
                    <input
                      type="number"
                      value={maxPrice}
                      onChange={(e) => setMaxPrice(e.target.value)}
                      placeholder="10000"
                      className="w-full px-3 py-2 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-black focus:border-transparent"
                    />
                  </div>

                  {/* Apply Button */}
                  <button
                    onClick={handleApplyPriceRange}
                    className="w-full bg-black text-white py-3 px-4 rounded-lg font-medium hover:bg-gray-800 transition-colors"
                  >
                    Apply
                  </button>
                </div>
              </div>
            )}
          </div>

          {/* Bedrooms Filter */}
          <div className="relative">
            <button 
              onClick={toggleBedrooms}
              className={`flex items-center justify-center space-x-2 px-4 py-3 border rounded-xl transition-colors shadow-sm ${
                bedrooms 
                  ? 'border-blue-500 bg-blue-50 text-blue-700' 
                  : 'border-gray-200 hover:bg-gray-50 text-gray-700'
              }`}
            >
              <svg className="h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 7v10a2 2 0 002 2h14a2 2 0 002-2V9a2 2 0 00-2-2H5a2 2 0 00-2-2z" />
              </svg>
              <span>{bedrooms ? `${bedrooms}+ Bedrooms` : 'Bedrooms'}</span>
              <svg className={`w-4 h-4 transition-transform ${bedroomsOpen ? 'rotate-180' : ''}`} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
              </svg>
            </button>

            {/* Bedrooms Dropdown Menu */}
            {bedroomsOpen && (
              <div className="absolute top-full left-0 mt-2 w-48 bg-white border border-blue-500 rounded-xl shadow-2xl z-50">
                <div className="p-2">
                  <div className="text-xs text-gray-500 mb-2 px-2">bedrooms</div>
                  <div className="space-y-0">
                    {[1, 2, 3, 4, 5].map((num, index) => (
                      <button
                        key={num}
                        onClick={() => { setBedrooms(num); setBedroomsOpen(false); onChange({ ...filters, bedrooms: num }); }}
                        className={`w-full text-left px-3 py-3 text-sm font-medium hover:bg-gray-50 transition-colors ${
                          bedrooms === num ? 'bg-blue-50 text-blue-700' : 'text-black'
                        } ${index !== 4 ? 'border-b border-gray-200' : ''}`}
                      >
                        {num}+ Bedrooms
                      </button>
                    ))}
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Bathrooms Filter */}
          <div className="relative">
            <button 
              onClick={toggleBathrooms}
              className={`flex items-center justify-center space-x-2 px-4 py-3 border rounded-xl transition-colors shadow-sm ${
                bathrooms 
                  ? 'border-blue-500 bg-blue-50 text-blue-700' 
                  : 'border-gray-200 hover:bg-gray-50 text-gray-700'
              }`}
            >
              <svg className="h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 14v3m4-3v3m4-3v3M3 21h18M3 10h18M3 7l9-4 9 4M4 10h16v11H4V10z" />
              </svg>
              <span>{bathrooms ? bathroomOptions.find(b => b.value === bathrooms)?.label : 'Bathroom'}</span>
              <svg className={`w-4 h-4 transition-transform ${bathroomsOpen ? 'rotate-180' : ''}`} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
              </svg>
            </button>

            {/* Bathrooms Dropdown Menu */}
            {bathroomsOpen && (
              <div className="absolute top-full left-0 mt-2 w-48 bg-white border border-blue-500 rounded-xl shadow-2xl z-50">
                <div className="p-2">
                  <div className="text-xs text-gray-500 mb-2 px-2">bathroom type</div>
                  <div className="space-y-0">
                    {bathroomOptions.map((option, index) => (
                      <button
                        key={option.value}
                        onClick={() => handleBathroomSelect(option.value)}
                        className={`w-full text-left px-3 py-3 text-sm font-medium hover:bg-gray-50 transition-colors ${
                          bathrooms === option.value ? 'bg-blue-50 text-blue-700' : 'text-black'
                        } ${index !== bathroomOptions.length - 1 ? 'border-b border-gray-200' : ''}`}
                      >
                        {option.label}
                      </button>
                    ))}
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Clear Filters Button */}
          {(selectedClassification || minPrice || maxPrice || bedrooms || bathrooms) && (
            <button
              onClick={handleClearFilters}
              className="flex items-center justify-center space-x-2 px-4 py-3 border border-gray-200 rounded-xl hover:bg-gray-50 transition-colors text-gray-700 shadow-sm"
            >
              <svg className="h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
              <span>Clear</span>
            </button>
          )}
          {city && (
            <div className="flex items-center bg-blue-100 text-blue-700 px-3 py-1 rounded-full text-sm">
              <span>City: {city}</span>
              <button onClick={() => { setCity(''); onChange({ ...filters, city: '' }); }} className="ml-2 text-blue-500 hover:text-blue-700">×</button>
            </div>
          )}
          {bedrooms && (
            <div className="flex items-center bg-blue-100 text-blue-700 px-3 py-1 rounded-full text-sm">
              <span>Bedrooms: {bedrooms}+</span>
              <button onClick={() => { setBedrooms(''); onChange({ ...filters, bedrooms: '' }); }} className="ml-2 text-blue-500 hover:text-blue-700">×</button>
            </div>
          )}
          {bathrooms && (
            <div className="flex items-center bg-blue-100 text-blue-700 px-3 py-1 rounded-full text-sm">
              <span>Bathroom: {bathroomOptions.find(b => b.value === bathrooms)?.label}</span>
              <button onClick={() => { setBathrooms(''); onChange({ ...filters, bathrooms: '' }); }} className="ml-2 text-blue-500 hover:text-blue-700">×</button>
            </div>
          )}
        </div>
      </div>

      {/* Active Filters Display */}
      {(selectedClassification || minPrice || maxPrice || city || bedrooms || bathrooms) && (
        <div className="mt-4 flex flex-wrap gap-2">
          {selectedClassification && (
            <div className="flex items-center bg-blue-100 text-blue-700 px-3 py-1 rounded-full text-sm">
              <span>Classification: {selectedClassification}</span>
              <button
                onClick={() => setSelectedClassification('')}
                className="ml-2 text-blue-500 hover:text-blue-700"
              >
                ×
              </button>
            </div>
          )}
          {(minPrice || maxPrice) && (
            <div className="flex items-center bg-blue-100 text-blue-700 px-3 py-1 rounded-full text-sm">
              <span>Price: ₱{minPrice || '0'} - ₱{maxPrice || '∞'}</span>
              <button
                onClick={() => { setMinPrice(''); setMaxPrice(''); }}
                className="ml-2 text-blue-500 hover:text-blue-700"
              >
                ×
              </button>
            </div>
          )}
        </div>
      )}

      {/* Backdrop to close dropdowns */}
      {(classificationOpen || priceRangeOpen || bedroomsOpen) && (
        <div
          className="fixed inset-0 z-40"
          onClick={() => {
            setClassificationOpen(false);
            setPriceRangeOpen(false);
            setBedroomsOpen(false);
          }}
        ></div>
      )}
    </div>
  );
};

export default SearchSection;
