import React, { useEffect, useState } from 'react';

const SearchSection = ({ filters = {}, onChange = () => {} }) => {
  // Simple state management
  const [showClassificationDropdown, setShowClassificationDropdown] = useState(false);
  const [showPriceDropdown, setShowPriceDropdown] = useState(false);
  const [showLocationModal, setShowLocationModal] = useState(false);
  const [selectedType, setSelectedType] = useState(filters.type || '');
  const [selectedLocation, setSelectedLocation] = useState(filters.location || '');
  const [minPrice, setMinPrice] = useState(filters.min_price || '');
  const [maxPrice, setMaxPrice] = useState(filters.max_price || '');
  const [searchQuery, setSearchQuery] = useState(filters.search || '');

  // Use backend enum values with human labels
  const classifications = [
    { value: '', label: 'All Types' },
    { value: 'bed_space', label: 'Bed Space' },
    { value: 'dormitory', label: 'Dormitory' },
    { value: 'boarding_house', label: 'Boarding House' },
    { value: 'studio_apartment', label: 'Studio Apartment' },
    { value: 'room_for_rent', label: 'Room for Rent' }
  ];

  // Location options
  const locations = [
    { value: '', label: 'All Locations' },
    { value: 'cebu_city', label: 'Cebu City' },
    { value: 'mandaue', label: 'Mandaue City' },
    { value: 'lapu_lapu', label: 'Lapu-Lapu City' },
    { value: 'talisay', label: 'Talisay City' },
    { value: 'consolacion', label: 'Consolacion' },
    { value: 'minglanilla', label: 'Minglanilla' }
  ];

  // Simple click handlers
  const handleClassificationClick = () => {
    console.log('Classification button clicked!');
    setShowClassificationDropdown(!showClassificationDropdown);
    setShowPriceDropdown(false);
    console.log('Classification dropdown should be:', !showClassificationDropdown);
  };

  const handlePriceClick = () => {
    console.log('Price button clicked!');
    setShowPriceDropdown(!showPriceDropdown);
    setShowClassificationDropdown(false);
    console.log('Price dropdown should be:', !showPriceDropdown);
  };

  const handleLocationClick = () => {
    setShowLocationModal(true);
    setShowClassificationDropdown(false);
    setShowPriceDropdown(false);
  };

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleTypeSelect = (type) => {
    console.log('Type selected:', type);
    setSelectedType(type);
    setShowClassificationDropdown(false);
    onChange({ ...filters, type: type });
  };

  const handleLocationSelect = (location, locationName) => {
    setSelectedLocation(locationName || location);
    setShowLocationModal(false);
    onChange({ ...filters, location: location, locationName: locationName });
  };

  const handleApplyPrice = () => {
    console.log('Applying price range:', minPrice, maxPrice);
    setShowPriceDropdown(false);
    onChange({ ...filters, min_price: minPrice, max_price: maxPrice });
  };

  // Debounce search changes to avoid chatty requests
  useEffect(() => {
    const id = setTimeout(() => {
      onChange({ ...filters, search: searchQuery });
    }, 300);
    return () => clearTimeout(id);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [searchQuery]);

  const getSelectedTypeLabel = () => {
    const selected = classifications.find(c => c.value === selectedType);
    return selected ? selected.label : 'All Types';
  };

  const getPriceRangeLabel = () => {
    if (minPrice || maxPrice) {
      return `₱${minPrice || '0'} - ₱${maxPrice || '∞'}`;
    }
    return 'Price Range';
  };

  return (
    <div className="mb-8 relative">
      {/* Single Line: Search Bar + Filters */}
      <div className="flex items-center gap-3 overflow-x-auto relative">
        {/* Main Search Bar */}
        <div className="flex-1 relative min-w-0">
          <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none">
            <svg className="h-5 w-5 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
            </svg>
          </div>
          <input
            type="text"
            placeholder="Search by location, property name, amenities, or any keyword..."
            value={searchQuery}
            onChange={handleSearchChange}
            className="block w-full pl-12 pr-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-black focus:border-black transition-all duration-200 bg-white"
          />
        </div>
        {/* Classification Filter */}
        <div className="relative flex-shrink-0">
          <button 
            onClick={handleClassificationClick}
            className={`flex items-center space-x-2 px-4 py-3 border rounded-lg transition-all duration-200 font-medium whitespace-nowrap ${
              selectedType 
                ? 'border-black bg-black text-white' 
                : 'border-gray-300 hover:border-gray-400 bg-white text-gray-700 hover:bg-gray-50'
            }`}
          >
            <svg className="h-4 w-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16m14 0h2m-2 0h-5m-9 0H3m2 0h5M9 7h1m-1 4h1m4-4h1m-1 4h1m-5 10v-5a1 1 0 011-1h2a1 1 0 011 1v5m-4 0h4" />
            </svg>
            <span>{getSelectedTypeLabel()}</span>
            <svg className={`w-4 h-4 transition-transform duration-200 ${showClassificationDropdown ? 'rotate-180' : ''}`} fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
            </svg>
          </button>

          {/* Classification Dropdown */}
          {showClassificationDropdown && (
            <div className="absolute top-full left-0 mt-2 min-w-[200px] bg-white border border-gray-300 rounded-lg shadow-lg z-[1000]">
              <div className="p-2">
                <div className="text-xs font-semibold text-gray-500 mb-2 px-2 uppercase tracking-wide">Property Type</div>
                <div className="space-y-1">
                  {classifications.map((c) => (
                    <button
                      key={c.value}
                      onClick={() => handleTypeSelect(c.value)}
                      className={`w-full text-left px-3 py-2 text-sm font-medium rounded-md transition-all duration-150 ${
                        selectedType === c.value 
                          ? 'bg-black text-white' 
                          : 'text-gray-700 hover:bg-gray-100'
                      }`}
                    >
                      {c.label}
                    </button>
                  ))}
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Location Filter */}
        <div className="relative flex-shrink-0">
          <button 
            onClick={handleLocationClick}
            className={`flex items-center space-x-2 px-4 py-3 border rounded-lg transition-all duration-200 font-medium whitespace-nowrap ${
              selectedLocation 
                ? 'border-black bg-black text-white' 
                : 'border-gray-300 hover:border-gray-400 bg-white text-gray-700 hover:bg-gray-50'
            }`}
          >
            <svg className="h-4 w-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
            </svg>
            <span>{selectedLocation || 'Location'}</span>
          </button>
        </div>

        {/* Price Range Filter */}
        <div className="relative flex-shrink-0">
          <button 
            onClick={handlePriceClick}
            className={`flex items-center space-x-2 px-4 py-3 border rounded-lg transition-all duration-200 font-medium whitespace-nowrap ${
              (minPrice || maxPrice)
                ? 'border-black bg-black text-white' 
                : 'border-gray-300 hover:border-gray-400 bg-white text-gray-700 hover:bg-gray-50'
            }`}
          >
            <svg className="h-4 w-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.599 1M12 8V7m0 1v8m0 0v1m0-1c-1.11 0-2.08-.402-2.599-1" />
            </svg>
            <span>{getPriceRangeLabel()}</span>
            <svg className={`w-4 h-4 transition-transform duration-200 ${showPriceDropdown ? 'rotate-180' : ''}`} fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
            </svg>
          </button>

          {/* Price Range Dropdown */}
          {showPriceDropdown && (
            <div className="absolute top-full right-0 mt-2 w-80 max-w-[90vw] bg-white border border-gray-300 rounded-lg shadow-lg z-[1000]">
              <div className="p-4">
                <div className="text-xs font-semibold text-gray-500 mb-3 uppercase tracking-wide">Price Range (₱)</div>
                
                {/* Min Price */}
                <div className="mb-3">
                  <label className="block text-sm font-medium text-gray-700 mb-1">Minimum Price</label>
                  <input
                    type="number"
                    value={minPrice}
                    onChange={(e) => setMinPrice(e.target.value)}
                    placeholder="0"
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-black focus:border-black text-sm"
                  />
                </div>

                {/* Max Price */}
                <div className="mb-4">
                  <label className="block text-sm font-medium text-gray-700 mb-1">Maximum Price</label>
                  <input
                    type="number"
                    value={maxPrice}
                    onChange={(e) => setMaxPrice(e.target.value)}
                    placeholder="10000"
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-black focus:border-black text-sm"
                  />
                </div>

                {/* Apply Button */}
                <button
                  onClick={handleApplyPrice}
                  className="w-full bg-black text-white py-2 px-4 rounded-md font-medium hover:bg-gray-800 transition-colors text-sm"
                >
                  Apply Price Range
                </button>
              </div>
            </div>
          )}
        </div>

      </div>

      {/* Click outside to close dropdowns */}
      {(showClassificationDropdown || showPriceDropdown) && (
        <div 
          className="fixed inset-0 z-10" 
          onClick={() => {
            setShowClassificationDropdown(false);
            setShowPriceDropdown(false);
          }}
        />
      )}

      {/* Simple Location Selection Modal */}
      {showLocationModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-2xl w-full max-w-md mx-auto shadow-2xl">
            {/* Modal Header */}
            <div className="flex items-center justify-between p-6 border-b border-gray-200">
              <h3 className="text-xl font-bold text-gray-900">Select Location</h3>
              <button
                onClick={() => setShowLocationModal(false)}
                className="text-gray-400 hover:text-gray-600 transition-colors"
              >
                <svg className="h-6 w-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>

            {/* Modal Content */}
            <div className="p-6">
              {/* Location Options */}
              <div className="space-y-2">
                <h4 className="text-sm font-semibold text-gray-700 mb-3">Select a Location</h4>
                {locations.slice(1).map((location) => (
                  <button
                    key={location.value}
                    onClick={() => handleLocationSelect(location.value, location.label)}
                    className="w-full text-left px-4 py-3 rounded-lg border border-gray-200 hover:border-black hover:bg-gray-50 transition-all duration-200"
                  >
                    <div className="flex items-center space-x-3">
                      <svg className="h-4 w-4 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                      </svg>
                      <span className="font-medium text-gray-900">{location.label}</span>
                    </div>
                  </button>
                ))}
              </div>

              {/* Clear Location */}
              {selectedLocation && (
                <button
                  onClick={() => handleLocationSelect('', '')}
                  className="w-full mt-4 px-4 py-2 text-gray-600 hover:text-gray-800 hover:bg-gray-100 rounded-lg transition-all duration-200 font-medium"
                >
                  Clear Location
                </button>
              )}
            </div>
          </div>
        </div>
      )}
      </div>
  );
};

export default SearchSection;
