import React, { useState, useEffect } from 'react';
import ApiService from '../../services/api';

const Settings = ({ isOpen, onClose }) => {
  const [activeTab, setActiveTab] = useState('account');
  const [loading, setLoading] = useState(false);
  const [profile, setProfile] = useState(null);
  const [showPasswords, setShowPasswords] = useState(false);
  const [passwordForm, setPasswordForm] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });
  const [settings, setSettings] = useState({
    // Account Settings
    emailNotifications: true,
    smsNotifications: false,
    marketingEmails: false,
    language: 'English',
    timezone: 'Asia/Manila',
    
    // Security Settings
    twoFactorAuth: false,
    loginAlerts: true,
    sessionTimeout: '24 hours',
    
    // Business Settings
    companyName: '',
    businessEmail: '',
    businessPhone: '',
    businessAddress: '',
    taxId: '',
    businessPermit: '',
    
    // Property Management Settings
    autoApproveInquiries: false,
    requireDocumentation: true,
    defaultLeaseTerm: '12 months',
    latePaymentGracePeriod: '3 days',
    maintenanceRequestAutoAssign: true,
    
    // Notification Settings
    newInquiryNotifications: true,
    paymentNotifications: true,
    maintenanceNotifications: true,
    tenantMoveInOut: true,
    systemUpdates: false,
    
    // Display Settings
    theme: 'Light',
    fontSize: 'Medium',
    compactMode: false,
    showRevenue: true,
    showOccupancy: true
  });

  // Load settings from localStorage and profile from API
  useEffect(() => {
    if (isOpen) {
      loadInitialSettings();
    }
  }, [isOpen]);

  const loadInitialSettings = async () => {
    try {
      setLoading(true);
      
      // Load settings from localStorage
      const savedSettings = localStorage.getItem('manager_settings');
      if (savedSettings) {
        setSettings(prev => ({ ...prev, ...JSON.parse(savedSettings) }));
      }

      // Load profile from API to get 2FA status
      const profileData = await ApiService.getManagerProfile();
      setProfile(profileData.profile);
      
      // Update 2FA setting from profile
      if (profileData.profile?.personalInfo?.two_factor_enabled !== undefined) {
        setSettings(prev => ({ 
          ...prev, 
          twoFactorAuth: profileData.profile.personalInfo.two_factor_enabled 
        }));
      }
    } catch (error) {
      console.error('Failed to load manager settings:', error);
    } finally {
      setLoading(false);
    }
  };

  // Apply theme and display settings live
  useEffect(() => {
    const html = document.documentElement;
    const body = document.body;
    
    // Apply theme
    if (settings.theme === 'Dark') {
      html.classList.add('dark');
    } else {
      html.classList.remove('dark');
    }
    
    // Apply font size
    html.style.fontSize = settings.fontSize === 'Small' ? '14px' : 
                         settings.fontSize === 'Large' ? '18px' : '16px';
    
    // Apply compact mode
    body.setAttribute('data-compact', settings.compactMode.toString());
  }, [settings.theme, settings.fontSize, settings.compactMode]);

  const handleSettingChange = (setting, value) => {
    setSettings(prev => ({
      ...prev,
      [setting]: value
    }));
  };

  const handleSaveSettings = () => {
    // TODO: Implement settings save logic
    alert('Settings saved successfully!');
  };

  const handleChangePassword = () => {
    // TODO: Implement password change functionality
    alert('Password change functionality would be implemented here');
  };

  const handleDeleteAccount = () => {
    if (window.confirm('Are you sure you want to delete your account? This action cannot be undone.')) {
      // TODO: Implement account deletion functionality
      alert('Account deletion functionality would be implemented here');
    }
  };

  const renderAccountSettings = () => (
    <div className="space-y-6">
      {/* Personal Information */}
      <div className="bg-white rounded-2xl shadow-lg border border-gray-100">
        <div className="p-6 border-b border-gray-100">
          <h3 className="text-xl font-bold text-gray-900">Personal Information</h3>
          <p className="text-gray-600 mt-1">Update your personal details</p>
        </div>
        <div className="p-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-2">Full Name</label>
              <input
                type="text"
                placeholder="Enter your full name"
                className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-black focus:border-transparent"
              />
            </div>
            <div>
